import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import path from "path";
import runtimeErrorOverlay from "@replit/vite-plugin-runtime-error-modal";

export default defineConfig({
  plugins: [
    react(),
    runtimeErrorOverlay(),
    ...(process.env.NODE_ENV !== "production" &&
    process.env.REPL_ID !== undefined
      ? [
          await import("@replit/vite-plugin-cartographer").then((m) =>
            m.cartographer(),
          ),
        ]
      : []),
  ],
  resolve: {
    alias: {
      "@": path.resolve(import.meta.dirname, "client", "src"),
      "@shared": path.resolve(import.meta.dirname, "shared"),
      "@assets": path.resolve(import.meta.dirname, "attached_assets"),
    },
  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist/public"),
    emptyOutDir: true,
  },
  server: {
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

server/db.ts.content:
// Import different database drivers based on environment
import ws from "ws";
import * as schema from "@shared/schema";
import { Pool as NeonPool, neonConfig } from '@neondatabase/serverless';
import { drizzle as drizzleNeon } from 'drizzle-orm/neon-serverless';
import { Pool as PgPool } from 'pg';
import { drizzle as drizzlePg } from 'drizzle-orm/node-postgres';

// Validate DATABASE_URL is provided - fail fast if missing
if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL environment variable is required. Please set it in your environment or .env file."
  );
}

// Environment detection for driver selection
const isReplit = process.env.REPL_ID !== undefined || process.env.REPL_SLUG !== undefined;
const isDocker = process.env.DATABASE_URL?.includes('@database:') || process.env.DOCKER === 'true';
const isNeonDatabase = process.env.DATABASE_URL?.includes('neon.') || process.env.DATABASE_URL?.includes('ep-');
const isProduction = process.env.NODE_ENV === 'production';

// Advanced connection pool configuration
const poolConfig = {
  connectionString: process.env.DATABASE_URL,
  // Connection pool settings optimized based on environment
  max: parseInt(process.env.DB_POOL_MAX || (isReplit ? '3' : '10')), 
  min: parseInt(process.env.DB_POOL_MIN || (isReplit ? '1' : '2')), 
  maxUses: parseInt(process.env.DB_POOL_MAX_USES || '1000'), 

  // Connection timeouts optimized for stability
  connectionTimeoutMillis: parseInt(process.env.DB_CONNECT_TIMEOUT || (isReplit ? '10000' : '30000')), 
  idleTimeoutMillis: parseInt(process.env.DB_IDLE_TIMEOUT || (isReplit ? '60000' : '120000')), 
  maxLifetimeSeconds: parseInt(process.env.DB_MAX_LIFETIME || (isReplit ? '300' : '600')), 

  // Performance settings
  allowExitOnIdle: true,
  application_name: process.env.DB_APP_NAME || 'arcelik-ecommerce',

  // Query timeouts
  statement_timeout: parseInt(process.env.DB_STATEMENT_TIMEOUT || '15000'), 
  query_timeout: parseInt(process.env.DB_QUERY_TIMEOUT || '10000'), 

  // SSL configuration for production
  ssl: isProduction && process.env.DB_SSL_ENABLED === 'true' ? { 
    rejectUnauthorized: false,
    ca: process.env.DB_SSL_CA || undefined,
    cert: process.env.DB_SSL_CERT || undefined,
    key: process.env.DB_SSL_KEY || undefined,
  } : false,

  // Additional performance options
  keepAlive: true,
  keepAliveInitialDelayMillis: 10000,
};

// Choose the appropriate database driver based on environment
let pool: any;
let db: any;

// Logger configuration for development
const loggerConfig = process.env.NODE_ENV === 'development' ? {
  logQuery: (query: string, params: unknown[]) => {
    // Log slow queries in development
    const start = Date.now();
    return () => {
      const duration = Date.now() - start;
      if (duration > 1000) { // Log queries taking more than 1 second
        console.warn(`üêå Slow query (${duration}ms):`, query.substring(0, 100) + '...');
      }
    };
  }
} : false;

// Database connection health monitoring
let connectionErrors = 0;
let lastHealthCheck = Date.now();

// Initialize database connection based on environment
if (isNeonDatabase && !isDocker) {
  // Use Neon serverless driver for Neon databases (Replit)
  // Configure Neon for WebSocket support
  neonConfig.webSocketConstructor = ws;

  console.log('üåê Using Neon serverless database driver (Replit/Neon environment)');
  pool = new NeonPool(poolConfig);
  db = drizzleNeon({ client: pool, schema, logger: loggerConfig });
} else {
  // Use standard PostgreSQL driver for Docker/local PostgreSQL
  if (isDocker) {
    console.log('üê≥ Using PostgreSQL driver (Docker environment)');
  } else {
    console.log('üêò Using PostgreSQL driver (Local/VPS environment)');
  }

  pool = new PgPool(poolConfig);
  db = drizzlePg({ client: pool, schema, logger: loggerConfig });
}

// Set up event handlers after pool initialization
pool.on('connect', (client: any) => {
  console.log('üîó New database connection established');
  connectionErrors = 0; // Reset error counter on successful connection
});

pool.on('error', (err: Error) => {
  console.error('üí• Database pool error:', err.message);
  connectionErrors++;

  // Log critical connection issues
  if (connectionErrors > 5) {
    console.error('üö® Multiple database connection errors detected!');
  }
});

pool.on('remove', (client: any) => {
  console.log('üîå Database connection removed from pool');
});

export { pool, db };

// Logger configuration already applied during database initialization above

// Database health check function
export async function checkDatabaseHealth(): Promise<{
  status: 'healthy' | 'degraded' | 'unhealthy';
  totalConnections: number;
  idleConnections: number;
  waitingCount: number;
  lastError?: string;
}> {
  try {
    const start = Date.now();
    await pool.query('SELECT 1');
    const responseTime = Date.now() - start;

    lastHealthCheck = Date.now();

    return {
      status: responseTime > 5000 ? 'degraded' : 'healthy',
      totalConnections: pool.totalCount,
      idleConnections: pool.idleCount,
      waitingCount: pool.waitingCount,
    };
  } catch (error) {
    console.error('‚ùå Database health check failed:', error);
    return {
      status: 'unhealthy',
      totalConnections: pool.totalCount,
      idleConnections: pool.idleCount,
      waitingCount: pool.waitingCount,
      lastError: error instanceof Error ? error.message : 'Unknown error',
    };
  }
}

// Graceful shutdown handler
export async function closeDatabaseConnections(): Promise<void> {
  console.log('üîÑ Closing database connections...');
  try {
    await pool.end();
    console.log('‚úÖ Database connections closed successfully');
  } catch (error) {
    console.error('‚ùå Error closing database connections:', error);
  }
}

// Performance monitoring helper
export function getDatabaseStats() {
  return {
    totalConnections: pool.totalCount,
    idleConnections: pool.idleCount,
    waitingCount: pool.waitingCount,
    connectionErrors,
    lastHealthCheck: new Date(lastHealthCheck).toISOString(),
    uptime: Date.now() - lastHealthCheck,
  };
}

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";

const app = express();

// Trust proxy for rate limiting in cloud environments (must be set early)
app.set('trust proxy', 1);

app.use(express.json({ limit: '50mb' }));
app.use(express.urlencoded({ extended: false, limit: '50mb' }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "‚Ä¶";
      }

      console.log(`${new Date().toLocaleTimeString("en-US", {
        hour: "numeric",
        minute: "2-digit", 
        second: "2-digit",
        hour12: true,
      })} [express] ${logLine}`);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    // Log the error for debugging but don't crash the process
    console.error(`‚ùå Express Error Handler: ${status} - ${message}`, {
      error: err.message,
      stack: process.env.NODE_ENV === 'development' ? err.stack : undefined,
      url: _req.url,
      method: _req.method,
      ip: _req.ip,
      timestamp: new Date().toISOString()
    });

    res.status(status).json({ message });
    // REMOVED: throw err; - This was causing process crashes after response
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    const { setupVite } = await import("./vite");
    await setupVite(app, server);
  } else {
    const { serveStatic } = await import("./vite");
    await serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const isProd = app.get("env") === "production";
  const port = Number(process.env.PORT || 5000);

  if (isProd && !process.env.PORT) {
    console.warn("‚ö†Ô∏è  PORT not set by hosting provider; using default port 5000");
    console.log("üí° Set PORT environment variable in production for optimal configuration");
  }

  // Bind to 0.0.0.0 for public access (required for Replit deployments)
  // localhost only works in development, 0.0.0.0 required for production
  const host = isProd ? '0.0.0.0' : 'localhost';
  server.listen(port, host, () => {
    console.log(`${new Date().toLocaleTimeString("en-US", {
      hour: "numeric",
      minute: "2-digit",
      second: "2-digit", 
      hour12: true,
    })} [express] ‚úÖ Server serving on ${host}:${port}`);
    if (isProd) {
      console.log(`${new Date().toLocaleTimeString("en-US", {
        hour: "numeric",
        minute: "2-digit",
        second: "2-digit",
        hour12: true,
      })} [express] üåç Production server accessible publicly on port ${port}`);
    }
  });
})();

server/objectStorage.ts.content:
import { Storage, File } from "@google-cloud/storage";
import { Response } from "express";
import { randomUUID } from "crypto";

const REPLIT_SIDECAR_ENDPOINT = "http://127.0.0.1:1106";

// The object storage client is used to interact with the object storage service.
export const objectStorageClient = new Storage({
  credentials: {
    audience: "replit",
    subject_token_type: "access_token",
    token_url: `${REPLIT_SIDECAR_ENDPOINT}/token`,
    type: "external_account",
    credential_source: {
      url: `${REPLIT_SIDECAR_ENDPOINT}/credential`,
      format: {
        type: "json",
        subject_token_field_name: "access_token",
      },
    },
    universe_domain: "googleapis.com",
  },
  projectId: "",
});

export class ObjectNotFoundError extends Error {
  constructor() {
    super("Object not found");
    this.name = "ObjectNotFoundError";
    Object.setPrototypeOf(this, ObjectNotFoundError.prototype);
  }
}

// The object storage service is used to interact with the object storage service.
export class ObjectStorageService {
  constructor() {}

  // Gets the public object search paths.
  getPublicObjectSearchPaths(): Array<string> {
    const pathsStr = process.env.PUBLIC_OBJECT_SEARCH_PATHS || "";
    const paths = Array.from(
      new Set(
        pathsStr
          .split(",")
          .map((path) => path.trim())
          .filter((path) => path.length > 0)
      )
    );
    if (paths.length === 0) {
      throw new Error(
        "PUBLIC_OBJECT_SEARCH_PATHS not set. Create a bucket in 'Object Storage' " +
          "tool and set PUBLIC_OBJECT_SEARCH_PATHS env var (comma-separated paths)."
      );
    }
    return paths;
  }

  // Gets the private object directory.
  getPrivateObjectDir(): string {
    const dir = process.env.PRIVATE_OBJECT_DIR || "";
    if (!dir) {
      throw new Error(
        "PRIVATE_OBJECT_DIR not set. Create a bucket in 'Object Storage' " +
          "tool and set PRIVATE_OBJECT_DIR env var."
      );
    }
    return dir;
  }

  // Search for a public object from the search paths.
  async searchPublicObject(filePath: string): Promise<File | null> {
    for (const searchPath of this.getPublicObjectSearchPaths()) {
      const fullPath = `${searchPath}/${filePath}`;

      // Full path format: /<bucket_name>/<object_name>
      const { bucketName, objectName } = parseObjectPath(fullPath);
      const bucket = objectStorageClient.bucket(bucketName);
      const file = bucket.file(objectName);

      // Check if file exists
      const [exists] = await file.exists();
      if (exists) {
        return file;
      }
    }

    return null;
  }

  // Downloads an object to the response.
  async downloadObject(file: File, res: Response, cacheTtlSec: number = 3600) {
    try {
      // Get file metadata
      const [metadata] = await file.getMetadata();

      // Set appropriate headers
      res.set({
        "Content-Type": metadata.contentType || "application/octet-stream",
        "Content-Length": metadata.size,
        "Cache-Control": `public, max-age=${cacheTtlSec}`,
      });

      // Stream the file to the response
      const stream = file.createReadStream();

      stream.on("error", (err) => {
        console.error("Stream error:", err);
        if (!res.headersSent) {
          res.status(500).json({ error: "Error streaming file" });
        }
      });

      stream.pipe(res);
    } catch (error) {
      console.error("Error downloading file:", error);
      if (!res.headersSent) {
        res.status(500).json({ error: "Error downloading file" });
      }
    }
  }

  // Gets the upload URL for an object entity.
  async getSliderImageUploadURL(): Promise<string> {
    const publicSearchPaths = this.getPublicObjectSearchPaths();
    if (publicSearchPaths.length === 0) {
      throw new Error("No public search paths configured");
    }

    // Use the first public path for slider images
    const publicPath = publicSearchPaths[0];
    const imageId = randomUUID();
    const fullPath = `${publicPath}/slider-images/${imageId}`;

    const { bucketName, objectName } = parseObjectPath(fullPath);

    // Sign URL for PUT method with TTL
    return signObjectURL({
      bucketName,
      objectName,
      method: "PUT",
      ttlSec: 900,
    });
  }

  // Gets the upload URL for product card images
  async getProductCardImageUploadURL(): Promise<string> {
    const publicSearchPaths = this.getPublicObjectSearchPaths();
    if (publicSearchPaths.length === 0) {
      throw new Error("No public search paths configured");
    }

    // Use the first public path for product card images
    const publicPath = publicSearchPaths[0];
    const imageId = randomUUID();
    const fullPath = `${publicPath}/product-images/${imageId}`;

    const { bucketName, objectName } = parseObjectPath(fullPath);

    // Sign URL for PUT method with TTL
    return signObjectURL({
      bucketName,
      objectName,
      method: "PUT",
      ttlSec: 900,
    });
  }

  // Gets the public URL for a slider image
  getSliderImagePublicURL(imageId: string): string {
    const publicSearchPaths = this.getPublicObjectSearchPaths();
    if (publicSearchPaths.length === 0) {
      throw new Error("No public search paths configured");
    }

    return `/public-objects/slider-images/${imageId}`;
  }

  // Gets the upload URL for a product image entity
  async getObjectEntityUploadURL(): Promise<string> {
    const publicSearchPaths = this.getPublicObjectSearchPaths();
    if (publicSearchPaths.length === 0) {
      throw new Error("No public search paths configured");
    }

    // Use the first public path for product images
    const publicPath = publicSearchPaths[0];
    const imageId = randomUUID();
    const fullPath = `${publicPath}/product-images/${imageId}.jpg`;

    const { bucketName, objectName } = parseObjectPath(fullPath);

    // Sign URL for PUT method with TTL
    return signObjectURL({
      bucketName,
      objectName,
      method: "PUT",
      ttlSec: 900,
    });
  }

  // Gets the upload URL for a category icon
  async getCategoryIconUploadURL(): Promise<string> {
    const publicSearchPaths = this.getPublicObjectSearchPaths();
    if (publicSearchPaths.length === 0) {
      throw new Error("No public search paths configured");
    }

    // Use the first public path for category icons
    const publicPath = publicSearchPaths[0];
    const imageId = randomUUID();
    const fullPath = `${publicPath}/category-icons/${imageId}`;

    const { bucketName, objectName } = parseObjectPath(fullPath);

    // Sign URL for PUT method with TTL
    return signObjectURL({
      bucketName,
      objectName,
      method: "PUT",
      ttlSec: 900,
    });
  }

  // Normalizes object entity path from upload URL
  normalizeObjectEntityPath(uploadUrl: string): string {
    try {
      const url = new URL(uploadUrl);
      const pathname = url.pathname;

      // Extract the path after the bucket name
      const pathParts = pathname.split('/');
      if (pathParts.length >= 3) {
        const fileName = pathParts[pathParts.length - 1];
        return `/public-objects/product-images/${fileName}`;
      }

      // Fallback - just return a default path with timestamp
      const timestamp = Date.now();
      return `/public-objects/product-images/image-${timestamp}.jpg`;
    } catch (error) {
      console.error("Error normalizing path:", error);
      // Fallback - just return a default path with timestamp
      const timestamp = Date.now();
      return `/public-objects/product-images/image-${timestamp}.jpg`;
    }
  }

  // Normalizes category icon path from upload URL
  normalizeCategoryIconPath(uploadUrl: string): string {
    try {
      const url = new URL(uploadUrl);
      const pathname = url.pathname;

      // Extract the path after the bucket name
      const pathParts = pathname.split('/');
      if (pathParts.length >= 3) {
        const fileName = pathParts[pathParts.length - 1];
        return `/public-objects/category-icons/${fileName}`;
      }

      // Fallback - just return a default path with timestamp
      const timestamp = Date.now();
      return `/public-objects/category-icons/icon-${timestamp}`;
    } catch (error) {
      console.error("Error normalizing path:", error);
      // Fallback - just return a default path with timestamp
      const timestamp = Date.now();
      return `/public-objects/category-icons/icon-${timestamp}`;
    }
  }
}

function parseObjectPath(path: string): {
  bucketName: string;
  objectName: string;
} {
  if (!path.startsWith("/")) {
    path = `/${path}`;
  }
  const pathParts = path.split("/");
  if (pathParts.length < 3) {
    throw new Error("Invalid path: must contain at least a bucket name");
  }

  const bucketName = pathParts[1];
  const objectName = pathParts.slice(2).join("/");

  return {
    bucketName,
    objectName,
  };
}

async function signObjectURL({
  bucketName,
  objectName,
  method,
  ttlSec,
}: {
  bucketName: string;
  objectName: string;
  method: "GET" | "PUT" | "DELETE" | "HEAD";
  ttlSec: number;
}): Promise<string> {
  const request = {
    bucket_name: bucketName,
    object_name: objectName,
    method,
    expires_at: new Date(Date.now() + ttlSec * 1000).toISOString(),
  };
  const response = await fetch(
    `${REPLIT_SIDECAR_ENDPOINT}/object-storage/signed-object-url`,
    {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(request),
    }
  );
  if (!response.ok) {
    throw new Error(
      `Failed to sign object URL, errorcode: ${response.status}, ` +
        `make sure you're running on Replit`
    );
  }

  const { signed_url: signedURL } = await response.json();
  return signedURL;
}

