    async (req, res) => {
      try {
        if (!req.file) {
          return res.status(400).json({ error: "Resim dosyasƒ± bulunamadƒ±" });
        }

        // Generate unique filename
        const timestamp = Date.now();
        const fileExtension = path.extname(req.file.originalname);
        const filename = `product-${timestamp}${fileExtension}`;

        // Object Storage kullanarak kalƒ±cƒ± depolama
        const { ObjectStorageService } = await import("./objectStorage");
        const objectStorageService = new ObjectStorageService();

        // Public klas√∂re y√ºkle (herkes eri≈üebilir)
        const publicDir = objectStorageService.getPublicObjectSearchPaths()[0];
        const objectPath = `${publicDir}/${filename}`;

        // Parse object path to get bucket and object name
        const pathParts = objectPath.split('/');
        const bucketName = pathParts[1];
        const objectName = pathParts.slice(2).join('/');

        // Upload to Object Storage
        const { objectStorageClient } = await import("./objectStorage");
        const bucket = objectStorageClient.bucket(bucketName);
        const file = bucket.file(objectName);

        // Upload file buffer directly
        await file.save(req.file.buffer, {
          metadata: {
            contentType: req.file.mimetype,
          },
        });

        // Return public URL that can be accessed via our endpoint
        const imageUrl = `/public-objects/${filename}`;

        console.log(`‚úÖ Image uploaded to Object Storage: ${filename}`);
        console.log(`üìÅ Bucket: ${bucketName}, Object: ${objectName}`);
        console.log(`üåê Public URL: ${imageUrl}`);

        res.json({ imageUrl });
      } catch (error) {
        console.error("‚ùå Error uploading image to Object Storage:", error);
        res.status(500).json({ error: "Resim y√ºklenirken hata olu≈ütu" });
