  // Ar√ßelik.com'dan √ºr√ºn bilgilerini √ßek
  app.post("/api/admin/scrape-product", async (req, res) => {
    try {
      const { productName } = req.body;

      if (!productName) {
        return res.status(400).json({ error: "√úr√ºn adƒ± gerekli" });
      }

      console.log(`üîç Scraping product: ${productName}`);

      const { spawn } = await import('child_process').then(m => m.default || m);
      // Ar√ßelik scraper - ƒ∞stenen JSON formatƒ±nda
      console.log('üöÄ Using ARCELIK SCRAPER for:', productName);
      const pythonProcess = spawn('python3', ['server/arcelik_scraper.py', productName]);

      let output = '';
      let error = '';
      let isResponseSent = false;

      pythonProcess.stdout.on('data', (data) => {
        output += data.toString();
      });

      pythonProcess.stderr.on('data', (data) => {
        error += data.toString();
      });

      pythonProcess.on('close', async (code) => {
        if (isResponseSent) return;
        isResponseSent = true;

        if (code !== 0) {
          console.error(`Python script error:`, error);
          return res.status(500).json({ error: "Scraping ba≈üarƒ±sƒ±z: " + (error || "Bilinmeyen hata") });
        }

        try {
          console.log('Raw Python output:', output);
          const cleanOutput = output.trim();

          if (!cleanOutput || cleanOutput.startsWith('<!DOCTYPE') || cleanOutput.startsWith('<html')) {
            return res.status(500).json({ error: "√úr√ºn bulunamadƒ± veya site eri≈üim hatasƒ±" });
          }

          const productData = JSON.parse(cleanOutput);
          console.log(`‚úÖ Product scraped successfully:`, productData.name || productData.error);

          // Resimleri object storage'a y√ºkle
          if (productData.images && productData.images.length > 0) {
            console.log(`üì∏ Uploading ${productData.images.length} images to object storage...`);

            try {
              const uploadedImages = [];
              const { ObjectStorageService } = await import('./objectStorage.js');
              const objectStorage = new ObjectStorageService();

              // Resimler arasƒ± 500ms bekleme ve retry ile
              for (let i = 0; i < Math.min(productData.images.length, 4); i++) {
                const imageUrl = productData.images[i];

                // Resimler arasƒ± bekleme (rate limiting'i √∂nlemek i√ßin)
                if (i > 0) {
                  await new Promise(resolve => setTimeout(resolve, 500));
                }

                let success = false;
                let retryCount = 0;
                const maxRetries = 3;

                while (!success && retryCount < maxRetries) {
                  try {
                    console.log(`üì∏ Downloading image ${i+1}/${productData.images.length} (attempt ${retryCount + 1})...`);

                    // URL'ye g√∂re farklƒ± header stratejileri
                    let headers = {};

                    if (imageUrl.includes('/media/resize/')) {
                      // Resize edilmi≈ü resimler i√ßin minimal header'lar
                      headers = {
                        'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36',
                        'Accept': 'image/webp,image/apng,image/*,*/*;q=0.8',
                        'Referer': 'https://www.arcelik.com.tr/',
                        'Cache-Control': 'no-cache'
                      };
                    } else if (imageUrl.includes('/medias/')) {
                      // Medias klas√∂r√º i√ßin farklƒ± header'lar
                      headers = {
                        'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/120.0.0.0 Safari/537.36',
                        'Accept': '*/*',
                        'Accept-Language': 'tr-TR,tr;q=0.9',
                        'Referer': 'https://www.arcelik.com.tr/',
                        'Connection': 'keep-alive',
                        'Sec-Fetch-Dest': 'image',
                        'Sec-Fetch-Mode': 'no-cors',
                        'Sec-Fetch-Site': 'same-origin'
                      };
                    } else {
                      // Diƒüer URL'ler i√ßin genel header'lar
                      headers = {
                        'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36',
                        'Accept': 'image/*,*/*',
                        'Referer': 'https://www.arcelik.com.tr/'
                      };
                    }

                    const imageResponse = await fetch(imageUrl, {
                      method: 'GET',
                      headers: headers,
                      signal: AbortSignal.timeout(15000) // 15 saniye timeout
                    });

                    if (imageResponse.ok) {
                      const imageBuffer = await imageResponse.arrayBuffer();

                      // Object storage'a y√ºkle
                      const uploadUrl = await objectStorage.getObjectEntityUploadURL();

                      const uploadResponse = await fetch(uploadUrl, {
                        method: 'PUT',
                        body: imageBuffer,
                        headers: {
                          'Content-Type': imageResponse.headers.get('content-type') || 'image/png',
                        }
                      });

                      if (uploadResponse.ok) {
                        const objectPath = objectStorage.normalizeObjectEntityPath(uploadUrl);
                        uploadedImages.push(objectPath);
                        console.log(`‚úÖ Image ${i+1} uploaded successfully: ${objectPath}`);
                        success = true;
                      } else {
                        console.error(`‚ùå Failed to upload image ${i+1}: ${uploadResponse.status}`);
                        retryCount++;
                      }
                    } else {
                      console.log(`‚ùå Failed to download image ${i+1}: ${imageResponse.status} (attempt ${retryCount + 1})`);
                      retryCount++;
                    }
                  } catch (imageError) {
                    console.error(`‚ùå Error processing image ${i+1} (attempt ${retryCount + 1}):`, imageError);
                    retryCount++;
                  }

                  // Retry arasƒ±nda bekleme
                  if (!success && retryCount < maxRetries) {
                    await new Promise(resolve => setTimeout(resolve, 1000));
                  }
                }

                if (!success) {
                  console.error(`‚ùå Failed to process image ${i+1} after ${maxRetries} attempts`);
                }
              }

              // Y√ºklenen resimleri data'ya ekle
              productData.uploadedImages = uploadedImages;
              // Ana images alanƒ±nƒ± da g√ºncelle
              productData.images = uploadedImages;
              console.log(`üì∏ Successfully uploaded ${uploadedImages.length} images`);

            } catch (uploadError) {
              console.error('‚ùå Image upload error:', uploadError);
              productData.uploadedImages = [];
              productData.images = [];
            }
          } else {
            // Eƒüer resim yoksa bo≈ü array
            productData.images = [];
          }

          res.json(productData);
        } catch (parseError) {
          console.error(`JSON parse error:`, parseError, 'Output:', output);
          res.status(500).json({ error: "Veri i≈üleme hatasƒ± - ge√ßersiz format" });
        }
      });

      // 30 saniye timeout
      const timeout = setTimeout(() => {
        if (isResponseSent) return;
        isResponseSent = true;
        pythonProcess.kill();
        res.status(408).json({ error: "ƒ∞≈ülem zaman a≈üƒ±mƒ±na uƒüradƒ±" });
      }, 30000);

    } catch (error) {
      console.error("Error scraping product:", error);
      res.status(500).json({ error: "Scraping hatasƒ±" });
    }
  });

