  }

  async validateCoupon(code: string, orderAmount: number): Promise<Coupon | null> {
    const coupon = await this.getCouponByCode(code);

    if (!coupon || !coupon.isActive) {
      return null;
    }

    const now = new Date();

    // validFrom kontrol√º (kupon hen√ºz ba≈ülamadƒ±ysa)
    if (coupon.validFrom && now < coupon.validFrom) {
      return null;
    }

    // validUntil kontrol√º (kupon s√ºresi bittiyse)
    if (coupon.validUntil && now > coupon.validUntil) {
      return null;
    }

    // Minimum sipari≈ü tutarƒ± kontrol√º
    if (coupon.minOrderAmount && orderAmount < parseFloat(coupon.minOrderAmount)) {
      return null;
    }

    // Kullanƒ±m limiti kontrol√º
    if (coupon.usageLimit && coupon.usageLimit > 0 && (coupon.usedCount || 0) >= coupon.usageLimit) {
      return null;
    }

    return coupon;
  }

