  async getExtendedWarrantyByCategory(categoryName: string): Promise<any | undefined> {
    try {
      const categoryLower = categoryName.toLowerCase();

      // Categories that should NOT have extended warranty - return undefined immediately
      const shouldExclude = 
        categoryLower.includes('mini') ||
        categoryLower.includes('k√º√ß√ºk ev e≈üya') ||
        categoryLower.includes('aspirat√∂r') ||
        categoryLower.includes('elektronik') ||
        categoryLower.includes('televizyon') ||
        categoryLower.includes('tv') ||
        categoryLower.includes('su sebili') ||
        // Only exclude if it contains BOTH 'ankastre' AND 'mikrodalga'
        (categoryLower.includes('ankastre') && categoryLower.includes('mikrodalga')) ||
        // Only exclude standalone 'mikrodalga' that doesn't contain 'fƒ±rƒ±n' 
        (categoryLower.includes('mikrodalga') && !categoryLower.includes('ankastre') && !categoryLower.includes('fƒ±rƒ±n'));

      if (shouldExclude) {
        return undefined; // No warranty for these categories
      }

      const allCategories = await this.getExtendedWarrantyCategories();

      // Direct match first
      let warranty = allCategories.find(cat => cat.categoryName.toLowerCase() === categoryLower);

      // If no direct match, try smart mapping for common categories
      if (!warranty) {
        // For "Beyaz E≈üya", don't automatically map to a specific warranty
        // Instead, let the product-specific mapping logic handle it
        if (categoryLower === "beyaz e≈üya" || categoryLower === "beyaz esya") {
          // Return a generic warranty or the first available one for the logic to work
          warranty = allCategories.find(cat => cat.categoryName.toLowerCase().includes("buzdolab"));
          // If no buzdolabi warranty found, use the first available
          if (!warranty && allCategories.length > 0) {
            warranty = allCategories[0];
          }
        }

        // Map categories with smart matching - BUT SKIP if it's a mini or excluded category
        // Fƒ±rƒ±n kategorileri - ankastre fƒ±rƒ±nlarƒ± da dahil et
        else if ((categoryLower.includes("fƒ±rƒ±n") || categoryLower.includes("firin") || (categoryLower.includes("ankastre") && !categoryLower.includes("mikrodalga"))) && !categoryLower.includes("mini")) {
          warranty = allCategories.find(cat => cat.categoryName.toLowerCase() === "fƒ±rƒ±n");
        }

        // Ocak kategorileri
        else if (categoryLower.includes("ocak") && !categoryLower.includes("mini")) {
          warranty = allCategories.find(cat => cat.categoryName.toLowerCase() === "ocak");
        }

        // Davlumbaz kategorileri
        else if (categoryLower.includes("davlumbaz")) {
          warranty = allCategories.find(cat => cat.categoryName.toLowerCase() === "davlumbaz");
        }

        // Buzdolabƒ± kategorileri
        else if ((categoryLower.includes("√ºstten donduru") || categoryLower.includes("ustten donduru")) && !categoryLower.includes("mini")) {
          warranty = allCategories.find(cat => cat.categoryName.toLowerCase() === "√ºstten donduruculu buzdolabƒ±");
        } else if ((categoryLower.includes("alttan donduru") || categoryLower.includes("alttan donduru")) && !categoryLower.includes("mini")) {
          warranty = allCategories.find(cat => cat.categoryName.toLowerCase() === "alttan donduruculu buzdolabƒ±");
        } else if ((categoryLower.includes("gardƒ±rop") || categoryLower.includes("gardirop")) && !categoryLower.includes("mini")) {
          warranty = allCategories.find(cat => cat.categoryName.toLowerCase() === "gardƒ±rop tipi buzdolabƒ±");
        } else if ((categoryLower.includes("statik") && categoryLower.includes("buzdolab")) && !categoryLower.includes("mini")) {
          warranty = allCategories.find(cat => cat.categoryName.toLowerCase() === "statik buzdolabƒ±");
        } else if ((categoryLower.includes("buzdolabƒ±") || categoryLower.includes("buzdolabi")) && !categoryLower.includes("mini")) {
          // Default buzdolabƒ± to no-frost
          warranty = allCategories.find(cat => cat.categoryName.toLowerCase() === "no-frost buzdolabƒ±");
        }

        // √áama≈üƒ±r ve kurutma kategorileri
        else if ((categoryLower.includes("kurutmalƒ± √ßama≈üƒ±r") || categoryLower.includes("kurutmli camasir")) && !categoryLower.includes("mini")) {
          warranty = allCategories.find(cat => cat.categoryName.toLowerCase() === "kurutmalƒ± √ßama≈üƒ±r makinesi");
        } else if ((categoryLower.includes("kurutma") || categoryLower.includes("kurutucu")) && !categoryLower.includes("mini")) {
          warranty = allCategories.find(cat => cat.categoryName.toLowerCase() === "kurutma makinesi");
        } else if ((categoryLower.includes("√ßama≈üƒ±r") || categoryLower.includes("camasir")) && !categoryLower.includes("mini")) {
          warranty = allCategories.find(cat => cat.categoryName.toLowerCase() === "√ßama≈üƒ±r makinesi");
        }

        // Klima kategorileri - t√ºm varyasyonlarƒ± dahil et
        else if (categoryLower.includes("klima") || 
                 categoryLower.includes("ƒ±sƒ±tma") || 
                 categoryLower.includes("soƒüutma") || 
                 categoryLower.includes("isitma") || 
                 categoryLower.includes("sogutma") || 
                 categoryLower.includes("inverter") ||
                 categoryLower.includes("split")) {
          warranty = allCategories.find(cat => cat.categoryName.toLowerCase() === "ev tipi klima");
        }

        // Bula≈üƒ±k makinesi
        else if ((categoryLower.includes("bula≈üƒ±k") || categoryLower.includes("bulasik")) && !categoryLower.includes("mini")) {
          warranty = allCategories.find(cat => cat.categoryName.toLowerCase() === "bula≈üƒ±k makinesi");
        }
      }

      return warranty;
    } catch (error) {
      console.error("Error getting extended warranty by category:", error);
      return undefined;
    }
  }

