  // Auto SEO Generation methods implementation
  async generateProductSeo(product: Product): Promise<SeoSettings> {
    // Mevcut SEO ayarƒ±nƒ± kontrol et
    const existingSeo = await db
      .select()
      .from(seoSettings)
      .where(
        and(
          eq(seoSettings.pageType, 'product'),
          eq(seoSettings.pageId, product.id)
        )
      )
      .limit(1);

    const categoryText = product.subcategory ? `${product.category} - ${product.subcategory}` : product.category;
    const brandText = product.brand ? ` ${product.brand}` : '';

    const seoData = {
      pageType: 'product' as const,
      pageId: product.id,
      title: `${product.name}${brandText} - ${categoryText} | Ender Outlet`,
      description: `${product.name} √ºr√ºn√ºn√º Ender Outlet'ta uygun fiyatlarla satƒ±n alƒ±n. ${product.description ? product.description.substring(0, 120) + '...' : 'Kaliteli √ºr√ºnler, g√ºvenli alƒ±≈üveri≈ü, hƒ±zlƒ± teslimat.'} Uzatƒ±lmƒ±≈ü garanti se√ßenekleri mevcut.`,
      keywords: `${product.name.toLowerCase()}, ${product.category.toLowerCase()}, ${product.subcategory?.toLowerCase() || ''}, ${product.brand?.toLowerCase() || ''}, beyaz e≈üya, ev aletleri, ender outlet`.replace(/,\s*,/g, ',').replace(/,\s*$/, ''),
      ogTitle: `${product.name} - Ender Outlet`,
      ogDescription: `${product.name} √ºr√ºn√ºn√º g√ºvenle satƒ±n alƒ±n. En uygun fiyat garantisi ile.`,
      ogImage: product.image || '',
      canonicalUrl: `https://enderoutlet.com/products/${product.id}`,
      isActive: true,
      createdAt: new Date(),
      updatedAt: new Date()
    };

    // Eƒüer mevcut SEO varsa g√ºncelle, yoksa olu≈ütur
    if (existingSeo.length > 0) {
      const [updated] = await db
        .update(seoSettings)
