import express, { type Express } from "express";
import { type Server } from "http";

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  // Dynamic imports to avoid bundling vite in production
  const { createServer: createViteServer, createLogger } = await import("vite");
  const viteConfig = await import("../vite.config");
  const { nanoid } = await import("nanoid");

  const viteLogger = createLogger();

  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig.default,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const path = await import("path");
      const fs = await import("fs");

      const clientTemplate = path.default.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.default.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export async function serveStatic(app: Express) {
  const path = await import("path");
  const fs = await import("fs");

  const distPath = path.default.resolve(import.meta.dirname, "public");

  console.log(`üìÅ Looking for static files in: ${distPath}`);

  if (!fs.default.existsSync(distPath)) {
    console.error(`‚ùå Build directory not found: ${distPath}`);
    console.log(`üìÇ Available directories in server:`);
    try {
      const serverDir = path.default.resolve(import.meta.dirname);
      const dirs = fs.default.readdirSync(serverDir);
      dirs.forEach(dir => {
        const fullPath = path.default.join(serverDir, dir);
        const stat = fs.default.statSync(fullPath);
        console.log(`   ${stat.isDirectory() ? 'üìÅ' : 'üìÑ'} ${dir}`);
      });
    } catch (e) {
      console.error("Error listing directories:", e);
    }
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  // Serve static files with proper cache headers for production
  app.use(express.static(distPath, {
    maxAge: process.env.NODE_ENV === 'production' ? '1d' : '0',
    etag: true,
    lastModified: true
  }));

  // Serve uploaded images
  const uploadsPath = path.default.resolve(import.meta.dirname, "..", "public", "uploads");
  if (fs.default.existsSync(uploadsPath)) {
    app.use('/uploads', express.static(uploadsPath));
    console.log(`üì∏ Serving uploads from: ${uploadsPath}`);
  }

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    const indexPath = path.default.resolve(distPath, "index.html");
    console.log(`üìÑ Serving index.html from: ${indexPath}`);
    res.sendFile(indexPath);
  });
}

