  linkText: string;
  linkUrl: string;
  isActive: boolean;
  sortOrder: number;
  sectionOrder: number;
}

export default function Footer() {
  // Genel ayarlarƒ± √ßek
  const { data: generalSettings } = useQuery({
    queryKey: ["/api/admin/settings"],
