    queryKey: ["/api/footer-links"],
  });

  // Footer linklerini b√∂l√ºmlere g√∂re grupla
  const groupedLinks = footerLinks.reduce((acc, link) => {
    if (!acc[link.sectionTitle]) {
      acc[link.sectionTitle] = [];
