import React, { createContext, useContext, useState } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import type { Product } from "@shared/schema";

interface CartItem {
  id: string;
  productId: string;
  quantity: number;
  warranty?: string;
  userId?: string;
  product?: Product;
  warrantyPrice?: number; // Add warranty price from backend
}

interface CartContextType {
  cartItems: CartItem[];
  isLoading: boolean;
  addToCart: (productId: string, quantity?: number, warranty?: string) => void;
  updateQuantity: (id: string, quantity: number) => void;
  removeFromCart: (id: string) => void;
  isCartOpen: boolean;
  setIsCartOpen: (open: boolean) => void;
  cartTotal: number;
  cartItemCount: number;
}

const CartContext = createContext<CartContextType | undefined>(undefined);

export function CartProvider({ children }: { children: React.ReactNode }) {
  const [isCartOpen, setIsCartOpen] = useState(false);
  const queryClient = useQueryClient();
  const { toast } = useToast();

  const { data: cartItems = [], isLoading } = useQuery<CartItem[]>({
    queryKey: ["/api/cart"],
  });

  const addToCartMutation = useMutation({
    mutationFn: async ({ productId, quantity, warranty }: { productId: string; quantity: number; warranty?: string }) => {
      const response = await apiRequest("/api/cart", "POST", { productId, quantity, warranty });
      if (!response.ok) {
        // Store status for error handling
        const errorInfo = { status: response.status };
        const errorData = await response.text();
        const error = new Error(errorData || 'Sepete eklenemedi');
        (error as any).status = response.status;
        throw error;
      }
      return response.json();
    },
    onSuccess: (data, variables) => {
      queryClient.invalidateQueries({ queryKey: ["/api/cart"] });
      toast({
        title: "üõí Sepete Eklendi!",
        description: `${variables.quantity} adet √ºr√ºn sepete ba≈üarƒ±yla eklendi.`,
        className: "bg-green-50 border-green-200 text-green-800",
      });
    },
    onError: (error: any) => {
      console.error('Add to cart error:', error);

      // Check if it's an authentication error (401)
      if (error.status === 401) {
        toast({
          title: "üîí Giri≈ü Yapmanƒ±z Gerekiyor",
          description: (
            <div>
              Sepete √ºr√ºn ekleyebilmek i√ßin √∂nce giri≈ü yapmalƒ±sƒ±nƒ±z.{" "}
              <a href="/login" className="underline font-medium">
                Giri≈ü yap
              </a>
            </div>
          ),
          variant: "destructive",
          duration: 6000
        });
      } else {
        // Parse JSON error if available
        let errorMessage = "√úr√ºn sepete eklenirken bir hata olu≈ütu.";
        try {
          if (error.message && error.message.includes('{"error"')) {
            const parsed = JSON.parse(error.message);
            errorMessage = parsed.error || errorMessage;
          } else if (error.message) {
            errorMessage = error.message;
          }
        } catch (e) {
          // Keep default message if parsing fails
        }

        toast({
          title: "Hata",
          description: errorMessage,
          variant: "destructive"
        });
      }
    }
  });

  const updateQuantityMutation = useMutation({
    mutationFn: async ({ id, quantity }: { id: string; quantity: number }) => {
      const response = await apiRequest(`/api/cart/${id}`, "PUT", { quantity });
      if (!response.ok) {
        const errorData = await response.text();
        throw new Error(errorData || 'G√ºncelleme ba≈üarƒ±sƒ±z');
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/cart"] });
    },
  });

  const removeFromCartMutation = useMutation({
    mutationFn: async (id: string) => {
      const response = await apiRequest(`/api/cart/${id}`, "DELETE", {});
      if (!response.ok) {
        const errorData = await response.text();
        throw new Error(errorData || 'Silme ba≈üarƒ±sƒ±z');
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/cart"] });
      toast({
        title: "üóëÔ∏è Sepetten √áƒ±karƒ±ldƒ±",
        description: "√úr√ºn sepetten ba≈üarƒ±yla √ßƒ±karƒ±ldƒ±.",
        className: "bg-orange-50 border-orange-200 text-orange-800",
      });
    },
  });

  const addToCart = (productId: string, quantity: number = 1, warranty?: string) => {
    addToCartMutation.mutate({ productId, quantity, warranty });
  };

  const updateQuantity = (id: string, quantity: number) => {
    updateQuantityMutation.mutate({ id, quantity });
  };

  const removeFromCart = (id: string) => {
    removeFromCartMutation.mutate(id);
  };

  // No longer needed - warranty price comes from backend

  const cartTotal = cartItems.reduce((total: number, item: CartItem) => {
    // Ensure price is properly parsed as number
    const price = item.product?.price;
    const basePrice = price ? (typeof price === 'number' ? price : parseFloat(price.toString())) * item.quantity : 0;
    const warrantyPrice = item.warrantyPrice || 0; // Use warranty price from backend


    return total + basePrice + warrantyPrice;
  }, 0);

  const cartItemCount = cartItems.reduce((count: number, item: CartItem) => count + item.quantity, 0);

  const value: CartContextType = {
    cartItems,
    isLoading,
    addToCart,
    updateQuantity,
    removeFromCart,
    isCartOpen,
    setIsCartOpen,
    cartTotal,
    cartItemCount,
  };

  return <CartContext.Provider value={value}>{children}</CartContext.Provider>;
}

export function useCart() {
  const context = useContext(CartContext);
  if (context === undefined) {
    throw new Error("useCart must be used within a CartProvider");
  }
  return context;
}

client/src/hooks/use-favorites.tsx.content:
import { createContext, useContext, ReactNode } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/use-auth";

interface Favorite {
  id: string;
  userId: string;
  productId: string;
  createdAt: Date;
  product?: any;
}

interface FavoritesContextType {
  favorites: Favorite[];
  isLoading: boolean;
  toggleFavorite: (productId: string) => void;
  isFavorited: (productId: string) => boolean;
  favoritesCount: number;
}

const FavoritesContext = createContext<FavoritesContextType | undefined>(undefined);

export function FavoritesProvider({ children }: { children: ReactNode }) {
  const { toast } = useToast();
  const { isAuthenticated } = useAuth();

  const { data: favorites = [], isLoading } = useQuery<Favorite[]>({
    queryKey: ["/api/favorites"],
    enabled: isAuthenticated,
  });

  const addToFavoritesMutation = useMutation({
    mutationFn: async (productId: string) => {
      const response = await apiRequest("/api/favorites", "POST", { productId });
      if (!response.ok) {
        const errorData = await response.text();
        throw new Error(errorData || 'Favorilere eklenemedi');
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/favorites"] });
    },
    onError: () => {
      toast({
        title: "Hata",
        description: "√úr√ºn favorilere eklenirken bir hata olu≈ütu.",
        variant: "destructive"
      });
    }
  });

  const removeFromFavoritesMutation = useMutation({
    mutationFn: async (productId: string) => {
      const response = await apiRequest(`/api/favorites/${productId}`, "DELETE");
      if (!response.ok) {
        const errorData = await response.text();
        throw new Error(errorData || 'Favorilerden √ßƒ±karƒ±lamadƒ±');
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/favorites"] });
    },
    onError: () => {
      toast({
        title: "Hata",
        description: "√úr√ºn favorilerden √ßƒ±karƒ±lƒ±rken bir hata olu≈ütu.",
        variant: "destructive"
      });
    }
  });

  const toggleFavorite = (productId: string) => {
    if (!isAuthenticated) {
      toast({
        title: "Giri≈ü yapƒ±n",
        description: "Favorilere eklemek i√ßin giri≈ü yapmanƒ±z gerekiyor.",
        variant: "destructive",
      });
      return;
    }

    const isFav = isFavorited(productId);
    if (isFav) {
      removeFromFavoritesMutation.mutate(productId);
    } else {
      addToFavoritesMutation.mutate(productId);
    }
  };

  const isFavorited = (productId: string) => {
    return favorites.some(fav => fav.productId === productId);
  };

  const favoritesCount = favorites.length;

  const value: FavoritesContextType = {
    favorites,
    isLoading,
    toggleFavorite,
    isFavorited,
    favoritesCount,
  };

  return (
    <FavoritesContext.Provider value={value}>
      {children}
    </FavoritesContext.Provider>
  );
}

export function useFavorites() {
  const context = useContext(FavoritesContext);
  if (context === undefined) {
    throw new Error("useFavorites must be used within a FavoritesProvider");
  }
  return context;
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

