    onError: () => {
      toast({
        title: "Hata",
        description: "E-posta g√∂nderilemedi. L√ºtfen tekrar deneyin.",
        variant: "destructive",
      });
      setEmailVerificationLoading(false);
    }
  });



  const handleEmailVerification = () => {
    setEmailVerificationLoading(true);
    resendEmailVerificationMutation.mutate();
  };


  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center space-x-2">
          <Settings className="h-5 w-5" />
          <span>Hesap G√ºvenliƒüi</span>
        </CardTitle>
        <CardDescription>E-posta doƒürulama durumu</CardDescription>
      </CardHeader>
      <CardContent className="space-y-6">
        {/* Email Verification */}
        <div className="flex items-center justify-between p-4 border rounded-lg">
          <div className="flex items-center space-x-3">
            <div className={`p-2 rounded-full ${(user as any).emailVerified ? 'bg-green-100' : 'bg-yellow-100'}`}>
              <Mail className={`h-4 w-4 ${(user as any).emailVerified ? 'text-green-600' : 'text-yellow-600'}`} />
            </div>
            <div>
              <p className="font-medium">E-posta Doƒürulama</p>
              <p className="text-sm text-gray-600">{user.email}</p>
            </div>
          </div>
          <div className="flex items-center space-x-2">
            {(user as any).emailVerified ? (
              <div className="flex items-center space-x-1 text-green-600">
                <CheckCircle className="h-4 w-4" />
                <span className="text-sm font-medium">Doƒürulandƒ±</span>
              </div>
            ) : (
              <div className="flex items-center space-x-2">
                <div className="flex items-center space-x-1 text-yellow-600">
                  <AlertTriangle className="h-4 w-4" />
                  <span className="text-sm">Doƒürulanmadƒ±</span>
                </div>
                <Button
                  size="sm"
                  variant="outline"
                  onClick={handleEmailVerification}
                  disabled={emailVerificationLoading}
                >
                  {emailVerificationLoading ? "G√∂nderiliyor..." : "Doƒürula"}
                </Button>
              </div>
            )}
          </div>
        </div>

      </CardContent>
    </Card>
  );
}

// Profile Card Component
function ProfileCard({ user }: { user: any }) {
  const [isEditing, setIsEditing] = useState(false);
  const [firstName, setFirstName] = useState(user.firstName || "");
  const [lastName, setLastName] = useState(user.lastName || "");
  const [phone, setPhone] = useState(user.phone || "");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const updateProfileMutation = useMutation({
    mutationFn: async (data: { firstName: string; lastName: string; phone: string }) => {
      const response = await apiRequest("/api/auth/update-profile", "PUT", data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/auth/me"] });
      setIsEditing(false);
      toast({
        title: "Profil g√ºncellendi",
        description: "Profil bilgileriniz ba≈üarƒ±yla g√ºncellendi.",
      });
    },
    onError: () => {
      toast({
        title: "Hata",
        description: "Profil g√ºncellenirken bir hata olu≈ütu.",
        variant: "destructive",
      });
    }
  });

  const handleSave = () => {
    updateProfileMutation.mutate({ firstName, lastName, phone });
  };

  const handleCancel = () => {
    setFirstName(user.firstName || "");
    setLastName(user.lastName || "");
    setPhone(user.phone || "");
    setIsEditing(false);
  };

  return (
    <Card>
