      </CardHeader>
      <CardContent className="space-y-4">
        {isEditing ? (
          <>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="firstName">Ad</Label>
                <Input
                  id="firstName"
                  value={firstName}
                  onChange={(e) => setFirstName(e.target.value)}
                  placeholder="Adƒ±nƒ±zƒ± girin"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="lastName">Soyad</Label>
                <Input
                  id="lastName"
                  value={lastName}
                  onChange={(e) => setLastName(e.target.value)}
                  placeholder="Soyadƒ±nƒ±zƒ± girin"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="phone">Telefon Numarasƒ±</Label>
                <Input
                  id="phone"
                  value={phone}
                  onChange={(e) => setPhone(e.target.value)}
                  placeholder="Telefon numaranƒ±zƒ± girin"
                  type="tel"
                />
              </div>
              <div className="space-y-2">
                <Label>E-posta</Label>
                <p className="text-sm text-gray-600 py-2">{user.email} (deƒüi≈ütirilemez)</p>
              </div>
            </div>
            <div className="flex space-x-2">
              <Button 
                onClick={handleSave} 
                disabled={updateProfileMutation.isPending}
                className="flex items-center"
              >
                <Save className="h-4 w-4 mr-2" />
                {updateProfileMutation.isPending ? "Kaydediliyor..." : "Kaydet"}
              </Button>
              <Button 
                variant="outline" 
                onClick={handleCancel}
                disabled={updateProfileMutation.isPending}
              >
                <X className="h-4 w-4 mr-2" />
                ƒ∞ptal
              </Button>
            </div>
          </>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="text-sm font-medium text-gray-700">Ad</label>
              <p className="mt-1 text-sm text-gray-900">{user.firstName || "Belirtilmemi≈ü"}</p>
            </div>
            <div>
              <label className="text-sm font-medium text-gray-700">Soyad</label>
              <p className="mt-1 text-sm text-gray-900">{user.lastName || "Belirtilmemi≈ü"}</p>
            </div>
            <div>
              <label className="text-sm font-medium text-gray-700">Telefon Numarasƒ±</label>
              <p className="mt-1 text-sm text-gray-900">{user.phone || "Belirtilmemi≈ü"}</p>
            </div>
            <div>
              <label className="text-sm font-medium text-gray-700">E-posta</label>
              <p className="mt-1 text-sm text-gray-900">{user.email}</p>
