          </DialogHeader>
          <CampaignSettingsForm 
            initialData={campaignSettings}
            onSubmit={(data: any) => updateSettingsMutation.mutate(data)}
            onCancel={() => setIsSettingsOpen(false)}
            isLoading={updateSettingsMutation.isPending}
          />
        </DialogContent>
      </Dialog>
    </div>
  );
}

