            <Heart className="h-16 w-16 text-gray-400 mx-auto mb-4" />
            <h1 className="text-2xl font-bold text-gray-900 mb-4">Favorileriniz Bo≈ü</h1>
            <p className="text-gray-600 mb-8">Hen√ºz favori √ºr√ºn√ºn√ºz yok. Beƒüendiƒüiniz √ºr√ºnleri favorilere ekleyin!</p>
            <Link href="/products">
              <Button className="bg-primary hover:bg-primary/90">
                √úr√ºnleri Ke≈üfet
              </Button>
            </Link>
          </div>
        </div>
        <Footer />
      </div>
    );
  }

  const handleAddToCart = (productId: string, productName: string) => {
    addToCart(productId, 1);
