                    {favorite.product?.name || '√úr√ºn'}
                  </h3>
                  <p className="text-sm text-gray-600">
                    {favorite.product?.brand || 'Ar√ßelik'}
                  </p>
                </div>

                <div className="flex items-center justify-between mb-3">
                  <div className="text-lg font-bold text-primary">
                    {favorite.product?.price ? 
                      parseFloat(favorite.product.price).toLocaleString('tr-TR') : '0'} ‚Ç∫
                  </div>
                  {favorite.product?.originalPrice && 
                   parseFloat(favorite.product.originalPrice) > parseFloat(favorite.product.price) && (
                    <div className="text-sm text-gray-500 line-through">
                      {parseFloat(favorite.product.originalPrice).toLocaleString('tr-TR')} ‚Ç∫
