                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      <div className="space-y-2">
                        <div className="flex justify-between">
                          <span className="font-medium">Marka:</span>
                          <span>{product?.brand || "Ar√ßelik"}</span>
                        </div>
                        <div className="flex justify-between">
                          <span className="font-medium">Kategori:</span>
                          <span>{product?.category || "Belirtilmemi≈ü"}</span>
                        </div>
                        {product?.energyClass && (
                          <div className="flex justify-between">
                            <span className="font-medium">Enerji Sƒ±nƒ±fƒ±:</span>
                            <span>{product.energyClass}</span>
                          </div>
                        )}
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </TabsContent>

            <TabsContent value="reviews" className="mt-6">
              <div className="bg-white rounded-lg border p-6">
                <div className="flex items-center justify-between mb-6">
                  <h3 className="text-xl font-semibold">
                    M√º≈üteri Deƒüerlendirmeleri
                  </h3>
                  <div className="text-right">
                    <div className="text-lg font-semibold">
                      {averageRating?.average || 0}/5
                    </div>
                    <div className="text-sm text-gray-500">
                      {averageRating?.count || 0} deƒüerlendirme
                    </div>
                  </div>
                </div>

                {/* Review Form for Authenticated Users */}
                {isAuthenticated && !userReview && (
                  <div className="mb-6 p-4 bg-gray-50 rounded-lg">
                    <Dialog
                      open={isReviewDialogOpen}
                      onOpenChange={setIsReviewDialogOpen}
                    >
                      <DialogTrigger asChild>
                        <Button variant="outline" className="w-full">
                          <MessageSquare className="h-4 w-4 mr-2" />
                          Bu √ºr√ºn√º deƒüerlendir
                        </Button>
                      </DialogTrigger>
                      <DialogContent>
                        <DialogHeader>
                          <DialogTitle>√úr√ºn Deƒüerlendirmesi</DialogTitle>
                        </DialogHeader>
                        <ReviewForm
                          productId={id || ""}
                          onClose={() => setIsReviewDialogOpen(false)}
                        />
                      </DialogContent>
                    </Dialog>
                  </div>
                )}

                {/* User's existing review */}
                {userReview && (
                  <div className="mb-6 p-4 bg-blue-50 rounded-lg border border-blue-200">
                    <div className="flex items-center justify-between mb-2">
                      <div className="font-medium text-blue-800">
                        Sizin deƒüerlendirmeniz
                      </div>
                      <div className="flex items-center">
                        {[...Array(5)].map((_, i) => (
                          <Star
                            key={i}
                            className={`h-4 w-4 ${
                              i < (userReview?.rating || 0)
                                ? "fill-yellow-400 text-yellow-400"
                                : "text-gray-300"
                            }`}
                          />
                        ))}
                      </div>
                    </div>
                    {userReview?.comment && userReview.comment.trim() && (
                      <p className="text-gray-700">{userReview.comment}</p>
                    )}
                    <div className="text-sm text-gray-500 mt-2">
                      {userReview?.createdAt &&
                        new Date(userReview.createdAt).toLocaleDateString(
                          "tr-TR",
                        )}
                    </div>
                  </div>
                )}

                {/* Login prompt for non-authenticated users */}
                {!isAuthenticated && (
                  <div className="mb-6 p-4 bg-gray-50 rounded-lg text-center">
                    <p className="text-gray-600 mb-3">
                      Deƒüerlendirme yapmak i√ßin giri≈ü yapƒ±n
                    </p>
                    <Link href="/login">
                      <Button variant="outline">Giri≈ü Yap</Button>
                    </Link>
                  </div>
                )}

                {/* Reviews List */}
                <div className="space-y-6">
                  {reviewsLoading ? (
                    <div className="text-center py-8">
                      <div className="animate-pulse space-y-4">
                        {[...Array(2)].map((_, i) => (
                          <div key={i} className="border-b pb-6">
                            <div className="flex justify-between mb-2">
                              <div className="h-4 bg-gray-200 rounded w-24"></div>
                              <div className="h-4 bg-gray-200 rounded w-20"></div>
                            </div>
                            <div className="h-4 bg-gray-200 rounded w-3/4 mb-2"></div>
                            <div className="h-3 bg-gray-200 rounded w-20"></div>
                          </div>
                        ))}
                      </div>
                    </div>
                  ) : reviews.length === 0 ? (
                    <div className="text-center py-8 text-gray-500">
                      Hen√ºz deƒüerlendirme yapƒ±lmamƒ±≈ü. ƒ∞lk deƒüerlendirmeyi siz
                      yapƒ±n!
                    </div>
                  ) : (
                    reviews.map((review: any) => (
                      <div
                        key={review.id}
                        className="border-b pb-6 last:border-b-0 last:pb-0"
                      >
                        <div className="flex items-center justify-between mb-2">
                          <div className="font-medium">
                            {review.user
                              ? `${review.user.firstName} ${review.user.lastName.charAt(0)}.`
                              : "Anonim"}
                          </div>
                          <div className="flex items-center">
                            {[...Array(5)].map((_, i) => (
                              <Star
                                key={i}
                                className={`h-4 w-4 ${
                                  i < review.rating
                                    ? "fill-yellow-400 text-yellow-400"
                                    : "text-gray-300"
                                }`}
                              />
                            ))}
                          </div>
                        </div>
                        {review.comment && review.comment.trim() && (
                          <p className="text-gray-700">{review.comment}</p>
                        )}
                        <div className="text-sm text-gray-500 mt-2">
                          {new Date(review.createdAt).toLocaleDateString(
                            "tr-TR",
                          )}
                        </div>
                      </div>
                    ))
                  )}
                </div>
              </div>
            </TabsContent>
          </Tabs>
        </div>
      </div>

      {/* Benzer √úr√ºnler B√∂l√ºm√º */}
      {similarProducts.length > 0 && (
        <div className="bg-white py-12">
          <div className="container mx-auto px-4">
            <h2 className="text-2xl font-bold text-gray-900 mb-8">
              Benzer √úr√ºnler
            </h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-6">
              {similarProducts.map((similarProduct) => (
                <div
                  key={similarProduct.id}
                  className="bg-white border border-gray-200 rounded-lg overflow-hidden hover:shadow-md transition-shadow"
                >
                  <Link href={`/product/${similarProduct.id}`}>
                    <div className="aspect-square bg-gray-100 relative overflow-hidden">
                      <img
                        src={similarProduct.image}
                        alt={similarProduct.name}
                        className="w-full h-full object-cover hover:scale-105 transition-transform duration-300"
                      />
                      {similarProduct.originalPrice &&
                        parseFloat(similarProduct.originalPrice) >
                          parseFloat(similarProduct.price) && (
                          <div className="absolute top-2 left-2">
                            <Badge variant="destructive" className="text-xs">
                              %
                              {Math.round(
                                ((parseFloat(similarProduct.originalPrice) -
                                  parseFloat(similarProduct.price)) /
                                  parseFloat(similarProduct.originalPrice)) *
                                  100,
                              )}
                            </Badge>
                          </div>
                        )}
                    </div>

                    <div className="p-4">
                      <h3 className="font-semibold text-sm text-gray-900 line-clamp-2 mb-2">
                        {similarProduct.name}
                      </h3>

                      <div className="flex items-center justify-between">
                        <div className="flex flex-col">
                          {similarProduct.originalPrice &&
                            parseFloat(similarProduct.originalPrice) >
                              parseFloat(similarProduct.price) && (
                              <span className="text-xs text-gray-500 line-through">
                                {parseFloat(
                                  similarProduct.originalPrice,
                                ).toLocaleString("tr-TR")}{" "}
                                ‚Ç∫
                              </span>
                            )}
                          <span className="text-lg font-bold text-primary">
                            {parseFloat(similarProduct.price).toLocaleString(
                              "tr-TR",
                            )}{" "}
                            ‚Ç∫
                          </span>
                        </div>
                      </div>

                      <Button
                        className="w-full mt-3 bg-primary hover:bg-primary/90 text-white text-sm"
                        onClick={(e) => {
                          e.preventDefault();
                          addToCart(similarProduct.id, 1);
                          toast({
                            title: "√úr√ºn sepete eklendi",
                            description: `${similarProduct.name} sepetinize eklendi.`,
                          });
                        }}
                      >
                        Sepete Ekle
                      </Button>
                    </div>
                  </Link>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}

      <Footer />
    </div>
  );
}

