import { useState, useEffect, useMemo, useRef } from "react";
import { useQuery } from "@tanstack/react-query";
import { useSearch } from "wouter";
import Header from "@/components/header";
import Footer from "@/components/footer";
import ProductCard from "@/components/product-card";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Grid3X3, List } from "lucide-react";
import type { Product, CategoryBanner } from "@shared/schema";

export default function Products() {
  const searchParams = useSearch();
  const urlParams = new URLSearchParams(searchParams);
  const categoryFromUrl = urlParams.get('category') || "all";

  const [selectedCategory, setSelectedCategory] = useState(categoryFromUrl);
  const [sortBy, setSortBy] = useState("name");
  const [viewMode, setViewMode] = useState<"grid" | "list">("grid");
  const [scrollY, setScrollY] = useState(0);
  const bannerRef = useRef<HTMLDivElement>(null);

  // URL'deki category parametresi deƒüi≈ütiƒüinde state'i g√ºncelle
  useEffect(() => {
    const categoryParam = urlParams.get('category');
    if (categoryParam) {
      setSelectedCategory(categoryParam);
    }
  }, [searchParams]);

  // Parallax scroll efekti
  useEffect(() => {
    const handleScroll = () => {
      setScrollY(window.scrollY);
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const { data: products = [], isLoading } = useQuery<Product[]>({
    queryKey: ["/api/products"],
  });

  // Category banner'ƒ±nƒ± fetch et
  const { data: categoryBanner } = useQuery<CategoryBanner>({
    queryKey: [`/api/category-banners/${selectedCategory}`],
    enabled: selectedCategory !== "all" && selectedCategory !== "discounted",
  });

  const categories = [
    { value: "all", label: "T√ºm √úr√ºnler" },
    { value: "discounted", label: "ƒ∞ndirimli √úr√ºnler" },
    // Ana kategoriler
    { value: "Beyaz E≈üya", label: "Beyaz E≈üya" },
    { value: "Ankastre", label: "Ankastre" },
    { value: "Televizyon", label: "Televizyon" },
    { value: "Elektronik", label: "Elektronik" },
    { value: "Isƒ±tma Soƒüutma", label: "Isƒ±tma Soƒüutma" },
    { value: "K√º√ß√ºk Ev Aletleri", label: "K√º√ß√ºk Ev Aletleri" },
    // Alt kategoriler
    { value: "Buzdolabƒ±", label: "Buzdolabƒ±" },
    { value: "√áama≈üƒ±r Makinesi", label: "√áama≈üƒ±r Makinesi" },
    { value: "Bula≈üƒ±k Makinesi", label: "Bula≈üƒ±k Makinesi" },
    { value: "Klima", label: "Klima" },
    { value: "Mikrodalga", label: "Mikrodalga" },
  ];

  const sortOptions = [
    { value: "name", label: "A-Z Sƒ±rala" },
    { value: "price-asc", label: "En Ucuz" },
    { value: "price-desc", label: "En Pahalƒ±" },
    { value: "newest", label: "En Yeni" },
  ];

  const filteredProducts = useMemo(() => {
    let filtered = products.filter((product: Product) => {
      if (selectedCategory === "all") return true;

      // ƒ∞ndirimli √ºr√ºnler filtresi - sadece ger√ßek indirimli √ºr√ºnleri g√∂ster
      if (selectedCategory === "discounted") {
        return product.originalPrice !== null && 
               product.originalPrice !== undefined && 
               product.originalPrice > product.price;
      }

      // √ñnce category'yi kontrol et - tam e≈üle≈üme varsa direkt kabul et
      if (product.category === selectedCategory) {
        return true;
      }

      // Category e≈üle≈ümezse subcategory i√ßinde arama yap
      const categoryMap: { [key: string]: string[] } = {
        // Ana kategoriler
        "Beyaz E≈üya": ["beyaz", "e≈üya", "buzdolabƒ±", "√ßama≈üƒ±r", "bula≈üƒ±k", "dondurucu"],
        "Ankastre": ["ankastre", "fƒ±rƒ±n", "ocak"],
        "Televizyon": ["televizyon", "tv", "smart"],
        "Elektronik": ["elektronik", "ses", "g√∂r√ºnt√º", "oyun"],
        "Isƒ±tma Soƒüutma": ["klima", "ƒ±sƒ±tma", "soƒüutma", "kombi", "termosifon"],
        "K√º√ß√ºk Ev Aletleri": ["k√º√ß√ºk", "blender", "kahve", "√ºt√º", "s√ºp√ºrge"],
        // Alt kategoriler
        "Buzdolabƒ±": ["buzdolabƒ±", "dondurucu"],
        "√áama≈üƒ±r Makinesi": ["√ßama≈üƒ±r"],
        "Bula≈üƒ±k Makinesi": ["bula≈üƒ±k"],
        "Klima": ["klima", "klimati"],
        "Mikrodalga": ["mikrodalga", "micro"]
      };

      const keywords = categoryMap[selectedCategory];
      if (!keywords) return false;

      const subcategory = (product.subcategory || "").toLowerCase();
      return keywords.some(keyword => subcategory.includes(keyword.toLowerCase()));
    });

    // Sƒ±ralama
    if (sortBy === "price-asc") {
      filtered.sort((a: Product, b: Product) => {
        const priceA = parseFloat(String(a.price)) || 0;
        const priceB = parseFloat(String(b.price)) || 0;

        if (priceA <= 0 && priceB > 0) return 1;
        if (priceB <= 0 && priceA > 0) return -1;
        if (priceA <= 0 && priceB <= 0) return 0;

        return priceA - priceB;
      });
    } else if (sortBy === "price-desc") {
      filtered.sort((a: Product, b: Product) => {
        const priceA = parseFloat(String(a.price)) || 0;
        const priceB = parseFloat(String(b.price)) || 0;

        if (priceA <= 0 && priceB > 0) return 1;
        if (priceB <= 0 && priceA > 0) return -1;
        if (priceA <= 0 && priceB <= 0) return 0;

        return priceB - priceA;
      });
    } else if (sortBy === "name") {
      filtered.sort((a: Product, b: Product) => a.name.localeCompare(b.name, 'tr'));
    }

    return filtered;
  }, [products, selectedCategory, sortBy]);

  return (
