  });

  const updateImageMutation = useMutation({
    mutationFn: async ({ id, image }: { id: string; image: string }) => {
      return await apiRequest(`/api/admin/sliders/${id}/image`, "PUT", { image });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/sliders"] });
      toast({
        title: "Ba≈üarƒ±lƒ±",
        description: "Slider g√∂rseli ba≈üarƒ±yla g√ºncellendi.",
      });
    },
    onError: (error) => {
      console.error("Error updating slider image:", error);
      toast({
        title: "Hata",
        description: "Slider g√∂rseli g√ºncellenirken bir hata olu≈ütu.",
        variant: "destructive",
      });
    },
  });

  const createSliderMutation = useMutation({
    mutationFn: async (sliderData: any) => {
      return await apiRequest("/api/admin/sliders", "POST", sliderData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/sliders"] });
      toast({
        title: "Ba≈üarƒ±lƒ±",
        description: "Yeni slider ba≈üarƒ±yla olu≈üturuldu.",
      });
    },
    onError: (error) => {
      console.error("Error creating slider:", error);
      toast({
        title: "Hata",
        description: "Slider olu≈üturulurken bir hata olu≈ütu.",
