              </div>
            </div>
          </DialogContent>
        </Dialog>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-1 xl:grid-cols-2 gap-8">
        {sliders.map((slider) => (
          <Card key={slider.id} className="overflow-hidden">
            <div className="relative group cursor-pointer" onClick={() => setEditingSlider(slider)}>
