              {/* Status Badge */}
              <div className="absolute top-4 right-4">
                <Badge 
                  variant={slider.isActive ? "default" : "secondary"} 
                  className="text-sm px-3 py-1"
                >
                  {slider.isActive ? "Aktif" : "Pasif"}
                </Badge>
