                          </div>

                          <div>
                            <Label>Slider G√∂rseli</Label>
                            <p className="text-sm text-gray-500 mb-2">
                              <strong>Tam Ekran Slider ƒ∞√ßin √ñnerilen Boyutlar:</strong><br/>
                              ‚Ä¢ <strong>Standart:</strong> 1920x1080px (Full HD)<br/>
                              ‚Ä¢ <strong>Y√ºksek Kalite:</strong> 3840x2160px (4K)<br/>
                              ‚Ä¢ Format: WebP veya y√ºksek kaliteli JPEG<br/>
                              ‚Ä¢ Maksimum dosya boyutu: 10MB (optimizasyon i√ßin 2-5MB √∂nerilir)
                            </p>
                            <ObjectUploader
                              maxNumberOfFiles={1}
                              maxFileSize={10485760} // 10MB (4K g√∂rseller i√ßin)
                              onGetUploadParameters={async () => {
                                const response = await fetch("/api/sliders/upload", {
                                  method: "POST"
                                });
                                const data = await response.json();
                                return {
                                  method: "PUT" as const,
                                  url: data.uploadURL
                                };
                              }}
                              onComplete={(result: any) => {
                                if (result.successful.length > 0) {
                                  const uploadURL = result.successful[0].uploadURL;
                                  // Extract filename from URL and create public path
                                  const url = new URL(uploadURL);
                                  const pathParts = url.pathname.split('/');
                                  const fileName = pathParts[pathParts.length - 1];
                                  const objectPath = `/public-objects/slider-images/${fileName}`;
                                  setEditingSlider({ ...editingSlider, image: objectPath });
                                  toast({
                                    title: "Ba≈üarƒ±lƒ±",
                                    description: "G√∂rsel ba≈üarƒ±yla y√ºklendi",
                                  });
                                }
                              }}
                              buttonClassName="w-full"
                            >
                              <Upload className="w-4 h-4 mr-2" />
                              Yeni G√∂rsel Y√ºkle
                            </ObjectUploader>

                            {editingSlider.image && (
                              <div className="mt-4 relative group cursor-pointer">
                                <img 
                                  src={editingSlider.image} 
                                  alt="Slider √ñnizleme"
                                  className="w-full h-48 md:h-64 object-cover rounded-lg border shadow-sm transition-all duration-200 group-hover:brightness-75"
                                />

                                {/* Hover overlay for changing image */}
                                <div className="absolute inset-0 bg-black/0 group-hover:bg-black/40 transition-all duration-200 flex items-center justify-center opacity-0 group-hover:opacity-100 rounded-lg">
                                  <div className="text-white text-center">
                                    <ImageIcon className="w-8 h-8 mx-auto mb-2" />
                                    <p className="text-sm font-semibold">G√∂rseli Deƒüi≈ütir</p>
                                  </div>
                                </div>
                              </div>
                            )}
                          </div>


                          <Button 
                            onClick={handleUpdateSlider}
                            disabled={updateSliderMutation.isPending}
                            className="w-full"
                          >
                            <Save className="w-4 h-4 mr-2" />
                            {updateSliderMutation.isPending ? "Kaydediliyor..." : "Kaydet"}
                          </Button>
                        </div>
                      )}
                    </DialogContent>
                  </Dialog>

                  <Button
                    variant={slider.isActive ? "destructive" : "default"}
                    size="xs"
                    className="text-xs py-1"
                    onClick={() => toggleSliderMutation.mutate({ 
                      id: slider.id, 
                      isActive: !slider.isActive 
                    })}
                    disabled={toggleSliderMutation.isPending}
                  >
                    <Eye className="w-3 h-3 mr-1" />
                    {slider.isActive ? "Gizle" : "G√∂ster"}
                  </Button>

                  <Button
                    variant="outline"
                    size="xs"
                    className="text-xs py-1 text-red-600 hover:text-red-700 hover:bg-red-50"
                    onClick={() => handleDeleteSlider(slider.id)}
                    disabled={deleteSliderMutation.isPending}
                  >
                    <Trash2 className="w-3 h-3" />
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {sliders.length === 0 && (
        <div className="text-center py-12 border-2 border-dashed border-gray-200 rounded-lg">
          <ImageIcon className="w-12 h-12 text-gray-400 mx-auto mb-4" />
          <p className="text-gray-600 font-medium mb-1">Hen√ºz slider yok</p>
          <p className="text-sm text-gray-400">ƒ∞lk slider'ƒ±nƒ±zƒ± olu≈üturun</p>
        </div>
      )}
    </div>
  );
}

client/src/pages/admin/virtual-pos-manag.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { CreditCard, Plus, Edit3, Trash2, Shield } from "lucide-react";

export default function VirtualPosManagement() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [editingConfig, setEditingConfig] = useState<any>(null);
  const [isDialogOpen, setIsDialogOpen] = useState(false);

  // Fetch virtual POS configurations
  const { data: virtualPosConfigs = [], isLoading } = useQuery<any[]>({
    queryKey: ['/api/admin/virtual-pos'],
  });

  // Form state for creating/editing virtual POS config
  const [formData, setFormData] = useState({
    bankName: '',
    posType: '3d',
    isActive: true,
    merchantId: '',
    terminalId: '',
    apiUrl: '',
    successUrl: 'http://localhost:5000/api/payment/virtual-pos/callback',
    failUrl: 'http://localhost:5000/api/payment/virtual-pos/callback',
    secretKey: '',
    storeKey: '',
    hashAlgorithm: 'SHA512',
    currency: 'TRY',
    language: 'tr',
    displayOrder: 0,
    minAmount: '50.00',
    maxAmount: '100000.00'
  });

  // Reset form
  const resetForm = () => {
    setFormData({
      bankName: '',
      posType: '3d',
      isActive: true,
      merchantId: '',
      terminalId: '',
      apiUrl: '',
      successUrl: 'http://localhost:5000/api/payment/virtual-pos/callback',
      failUrl: 'http://localhost:5000/api/payment/virtual-pos/callback',
      secretKey: '',
      storeKey: '',
      hashAlgorithm: 'SHA512',
      currency: 'TRY',
      language: 'tr',
      displayOrder: 0,
      minAmount: '50.00',
      maxAmount: '100000.00'
    });
    setEditingConfig(null);
  };

  // Handle form input changes
  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value, type } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? (e.target as HTMLInputElement).checked : value
    }));
  };

  const handleSelectChange = (name: string, value: string) => {
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  // Create virtual POS config mutation
  const createConfigMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest("POST", "/api/admin/virtual-pos", data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/virtual-pos'] });
      toast({
        title: "Ba≈üarƒ±lƒ±!",
        description: "Sanal POS konfig√ºrasyonu olu≈üturuldu.",
        className: "bg-green-50 border-green-200 text-green-800",
      });
      resetForm();
      setIsDialogOpen(false);
    },
    onError: (error: any) => {
      toast({
        title: "Hata!",
        description: "Konfig√ºrasyon olu≈üturulurken hata olu≈ütu.",
        variant: "destructive",
      });
    },
  });

  // Update virtual POS config mutation
  const updateConfigMutation = useMutation({
    mutationFn: async ({ id, ...data }: any) => {
      const response = await apiRequest("PUT", `/api/admin/virtual-pos/${id}`, data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/virtual-pos'] });
      toast({
        title: "Ba≈üarƒ±lƒ±!",
        description: "Sanal POS konfig√ºrasyonu g√ºncellendi.",
        className: "bg-green-50 border-green-200 text-green-800",
      });
      resetForm();
      setIsDialogOpen(false);
    },
    onError: (error: any) => {
      toast({
        title: "Hata!",
        description: "Konfig√ºrasyon g√ºncellenirken hata olu≈ütu.",
        variant: "destructive",
      });
    },
  });

  // Delete virtual POS config mutation
  const deleteConfigMutation = useMutation({
    mutationFn: async (id: string) => {
      const response = await apiRequest("DELETE", `/api/admin/virtual-pos/${id}`);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/virtual-pos'] });
      toast({
        title: "Ba≈üarƒ±lƒ±!",
        description: "Sanal POS konfig√ºrasyonu silindi.",
        className: "bg-green-50 border-green-200 text-green-800",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Hata!",
        description: "Konfig√ºrasyon silinirken hata olu≈ütu.",
        variant: "destructive",
      });
    },
  });

  // Handle form submission
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (editingConfig) {
      updateConfigMutation.mutate({ id: editingConfig.id, ...formData });
    } else {
      createConfigMutation.mutate(formData);
    }
  };

  // Handle edit
  const handleEdit = (config: any) => {
    setEditingConfig(config);
    setFormData({
      bankName: config.bankName || '',
      posType: config.posType || '3d',
      isActive: config.isActive ?? true,
      merchantId: config.merchantId || '',
      terminalId: config.terminalId || '',
      apiUrl: config.apiUrl || '',
      successUrl: config.successUrl || 'http://localhost:5000/api/payment/virtual-pos/callback',
      failUrl: config.failUrl || 'http://localhost:5000/api/payment/virtual-pos/callback',
      secretKey: config.secretKey || '',
      storeKey: config.storeKey || '',
      hashAlgorithm: config.hashAlgorithm || 'SHA512',
      currency: config.currency || 'TRY',
      language: config.language || 'tr',
      displayOrder: config.displayOrder || 0,
      minAmount: config.minAmount || '50.00',
      maxAmount: config.maxAmount || '100000.00'
    });
    setIsDialogOpen(true);
  };

  // Handle delete
  const handleDelete = (id: string) => {
    if (confirm('Bu sanal POS konfig√ºrasyonunu silmek istediƒüinizden emin misiniz?')) {
      deleteConfigMutation.mutate(id);
    }
  };

  const openCreateDialog = () => {
    resetForm();
    setIsDialogOpen(true);
  };

  if (isLoading) {
    return <div className="text-center py-8">Y√ºkleniyor...</div>;
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">Sanal POS Y√∂netimi</h2>
          <p className="text-gray-600">Banka sanal POS konfig√ºrasyonlarƒ±nƒ± y√∂netin</p>
        </div>
        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
          <DialogTrigger asChild>
            <Button onClick={openCreateDialog} className="bg-blue-600 hover:bg-blue-700">
              <Plus className="w-4 h-4 mr-2" />
              Yeni Sanal POS
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle>
                {editingConfig ? 'Sanal POS D√ºzenle' : 'Yeni Sanal POS Ekle'}
              </DialogTitle>
              <DialogDescription>
                {editingConfig ? 'Mevcut sanal POS konfig√ºrasyonunu d√ºzenleyin.' : 'Yeni bir banka sanal POS konfig√ºrasyonu olu≈üturun.'}
              </DialogDescription>
            </DialogHeader>

            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="bankName">Banka Adƒ±</Label>
                  <Input
                    id="bankName"
                    name="bankName"
                    value={formData.bankName}
                    onChange={handleInputChange}
                    placeholder="Garanti BBVA"
                    required
                  />
                </div>
                <div>
                  <Label htmlFor="posType">POS Tipi</Label>
                  <Select value={formData.posType} onValueChange={(value) => handleSelectChange('posType', value)}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="3d">3D Secure</SelectItem>
                      <SelectItem value="3d_pay">3D Pay</SelectItem>
                      <SelectItem value="non_3d">Non 3D</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="merchantId">Merchant ID</Label>
                  <Input
                    id="merchantId"
                    name="merchantId"
                    value={formData.merchantId}
                    onChange={handleInputChange}
                    placeholder="MERCHANT_123"
                    required
                  />
                </div>
                <div>
                  <Label htmlFor="terminalId">Terminal ID</Label>
                  <Input
                    id="terminalId"
                    name="terminalId"
                    value={formData.terminalId}
                    onChange={handleInputChange}
                    placeholder="TERMINAL_001"
                    required
                  />
                </div>
              </div>

              <div>
                <Label htmlFor="apiUrl">API URL</Label>
                <Input
                  id="apiUrl"
                  name="apiUrl"
                  value={formData.apiUrl}
                  onChange={handleInputChange}
                  placeholder="https://sanalposprov.garanti.com.tr/VPServlet"
                  required
                />
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="secretKey">Secret Key</Label>
                  <Input
                    id="secretKey"
                    name="secretKey"
                    type="password"
                    value={formData.secretKey}
                    onChange={handleInputChange}
                    placeholder="SECRET_KEY_123"
                    required
                  />
                </div>
                <div>
                  <Label htmlFor="storeKey">Store Key</Label>
                  <Input
                    id="storeKey"
                    name="storeKey"
                    type="password"
                    value={formData.storeKey}
                    onChange={handleInputChange}
                    placeholder="STORE_KEY"
                  />
                </div>
              </div>

              <div className="grid grid-cols-3 gap-4">
                <div>
                  <Label htmlFor="minAmount">Min Tutar</Label>
                  <Input
                    id="minAmount"
                    name="minAmount"
                    type="number"
                    step="0.01"
                    value={formData.minAmount}
                    onChange={handleInputChange}
                    required
                  />
                </div>
                <div>
                  <Label htmlFor="maxAmount">Max Tutar</Label>
                  <Input
                    id="maxAmount"
                    name="maxAmount"
                    type="number"
                    step="0.01"
                    value={formData.maxAmount}
                    onChange={handleInputChange}
                  />
                </div>
                <div>
                  <Label htmlFor="displayOrder">Sƒ±ra</Label>
                  <Input
                    id="displayOrder"
                    name="displayOrder"
                    type="number"
                    value={formData.displayOrder}
                    onChange={handleInputChange}
                  />
                </div>
              </div>

              <div className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  id="isActive"
                  name="isActive"
                  checked={formData.isActive}
                  onChange={handleInputChange}
                  className="rounded"
                />
                <Label htmlFor="isActive">Aktif</Label>
              </div>

              <div className="flex justify-end space-x-2 pt-4">
                <Button 
                  type="button" 
                  variant="outline" 
                  onClick={() => setIsDialogOpen(false)}
                >
                  ƒ∞ptal
                </Button>
                <Button 
                  type="submit" 
                  disabled={createConfigMutation.isPending || updateConfigMutation.isPending}
                  className="bg-blue-600 hover:bg-blue-700"
                >
                  {editingConfig ? 'G√ºncelle' : 'Olu≈ütur'}
                </Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      {/* Virtual POS Configurations List */}
      <div className="grid gap-4">
        {virtualPosConfigs.length === 0 ? (
          <Card>
            <CardContent className="text-center py-8">
              <div className="text-gray-500">
                <Shield className="w-12 h-12 mx-auto mb-4 opacity-50" />
                <p>Hen√ºz sanal POS konfig√ºrasyonu eklenmemi≈ü.</p>
                <p className="text-sm mt-2">ƒ∞lk konfig√ºrasyonunuzu eklemek i√ßin yukarƒ±daki butonu kullanƒ±n.</p>
              </div>
            </CardContent>
          </Card>
        ) : (
          virtualPosConfigs.map((config: any) => (
            <Card key={config.id}>
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-4">
                    <div className="flex items-center justify-center w-12 h-12 bg-blue-100 rounded-lg">
                      <CreditCard className="w-6 h-6 text-blue-600" />
                    </div>
                    <div>
                      <h3 className="font-semibold text-lg">{config.bankName}</h3>
                      <div className="flex items-center space-x-4 text-sm text-gray-600">
                        <span>{config.posType === '3d' ? '3D Secure' : config.posType}</span>
                        <span>‚Ä¢</span>
                        <span>Merchant: {config.merchantId}</span>
                        <span>‚Ä¢</span>
                        <span>Terminal: {config.terminalId}</span>
                      </div>
                      <div className="flex items-center space-x-4 text-sm text-gray-500 mt-1">
                        <span>Min: {parseFloat(config.minAmount).toLocaleString('tr-TR')} ‚Ç∫</span>
                        {config.maxAmount && (
                          <>
                            <span>‚Ä¢</span>
                            <span>Max: {parseFloat(config.maxAmount).toLocaleString('tr-TR')} ‚Ç∫</span>
                          </>
                        )}
                      </div>
                    </div>
                  </div>
                  <div className="flex items-center space-x-2">
                    <Badge variant={config.isActive ? "default" : "secondary"}>
                      {config.isActive ? "Aktif" : "Pasif"}
                    </Badge>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => handleEdit(config)}
                    >
                      <Edit3 className="w-4 h-4" />
                    </Button>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => handleDelete(config.id)}
                      className="text-red-600 hover:bg-red-50"
                    >
                      <Trash2 className="w-4 h-4" />
                    </Button>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))
        )}
      </div>
    </div>
  );
}

client/src/pages/forgot-password.tsx.content:
import { useState } from "react";
import { Link } from "wouter";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Mail, ArrowLeft } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import Header from "@/components/header";
import Footer from "@/components/footer";

export default function ForgotPasswordPage() {
  const [email, setEmail] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [isSubmitted, setIsSubmitted] = useState(false);
  const { toast } = useToast();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!email) {
      toast({
        title: "Hata",
        description: "L√ºtfen e-posta adresinizi girin.",
        variant: "destructive",
      });
      return;
    }

    // Basic email validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      toast({
        title: "Hata", 
        description: "L√ºtfen ge√ßerli bir e-posta adresi girin.",
        variant: "destructive",
      });
      return;
    }

    setIsLoading(true);

    try {
      const response = await apiRequest("/api/auth/forgot-password", "POST", { email });

      if (response.ok) {
        setIsSubmitted(true);
        toast({
          title: "Ba≈üarƒ±lƒ±!",
          description: "≈ûifre sƒ±fƒ±rlama talimatlarƒ± e-posta adresinize g√∂nderildi.",
          className: "bg-green-50 border-green-200 text-green-800",
        });
      } else {
        const errorData = await response.text();
        let errorMessage = "Bir hata olu≈ütu.";

        try {
          const parsed = JSON.parse(errorData);
          errorMessage = parsed.error || errorMessage;
        } catch (e) {
          // Keep default message
        }

        toast({
          title: "Hata",
          description: errorMessage,
          variant: "destructive",
        });
      }
    } catch (error) {
      toast({
        title: "Hata",
        description: "Baƒülantƒ± hatasƒ± olu≈ütu. L√ºtfen tekrar deneyin.",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  if (isSubmitted) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="container mx-auto px-4 py-16">
          <div className="max-w-md mx-auto">
            <Card>
              <CardHeader className="text-center">
                <div className="mx-auto w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mb-4">
                  <Mail className="h-8 w-8 text-green-600" />
                </div>
                <CardTitle className="text-2xl">E-posta G√∂nderildi</CardTitle>
                <CardDescription className="text-gray-600">
                  ≈ûifre sƒ±fƒ±rlama talimatlarƒ± <strong>{email}</strong> adresine g√∂nderildi.
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="text-sm text-gray-600 text-center">
                  E-postayƒ± g√∂rm√ºyorsanƒ±z spam klas√∂r√ºn√ºz√º kontrol edin.
                </div>
                <div className="space-y-3">
                  <Link href="/login">
                    <Button className="w-full" variant="outline" data-testid="button-back-to-login">
                      <ArrowLeft className="h-4 w-4 mr-2" />
                      Giri≈ü sayfasƒ±na d√∂n
                    </Button>
                  </Link>
                  <button
                    onClick={() => {
                      setIsSubmitted(false);
                      setEmail("");
                    }}
                    className="w-full text-sm text-primary hover:underline"
                    data-testid="button-send-again"
                  >
                    Tekrar g√∂nder
                  </button>
                </div>
              </CardContent>
            </Card>
          </div>
        </div>
        <Footer />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <div className="container mx-auto px-4 py-16">
        <div className="max-w-md mx-auto">
          <Card>
            <CardHeader className="text-center">
              <CardTitle className="text-2xl">≈ûifremi Unuttum</CardTitle>
              <CardDescription className="text-gray-600">
                E-posta adresinizi girin, size ≈üifre sƒ±fƒ±rlama talimatlarƒ± g√∂nderelim.
              </CardDescription>
            </CardHeader>

            <CardContent>
              <form onSubmit={handleSubmit} className="space-y-4">
                <div className="space-y-2">
                  <Label htmlFor="email">E-posta adresi</Label>
                  <Input
                    id="email"
                    type="email"
                    placeholder="ornek@email.com"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
