  description: z.string().min(20, "A√ßƒ±klama en az 20 karakter olmalƒ±dƒ±r"),
});

type SupportTicketForm = z.infer<typeof supportTicketSchema>;

export default function SupportTicketCreate() {
  const [location, navigate] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { user, isAuthenticated } = useAuth();

  const form = useForm<SupportTicketForm>({
    resolver: zodResolver(supportTicketSchema),
    defaultValues: {
      subject: "",
      category: "",
      priority: "medium",
      description: "",
    },
  });

  const createTicketMutation = useMutation({
    mutationFn: async (data: SupportTicketForm) => {
      const response = await apiRequest("/api/support/tickets", "POST", data);
      return response.json();
    },
    onSuccess: (ticket) => {
      toast({
        title: "Destek talebi olu≈üturuldu",
        description: `Talebiniz #${ticket.id} numarasƒ± ile kaydedildi. En kƒ±sa s√ºrede size d√∂n√º≈ü yapacaƒüƒ±z.`,
      });
      queryClient.invalidateQueries({ queryKey: ['/api/support/tickets'] });
      navigate("/destek-taleplerim");
    },
    onError: (error: any) => {
      console.error("Error creating support ticket:", error);
      let errorMessage = "Destek talebi olu≈üturulurken bir hata olu≈ütu";

      if (error.message) {
        if (error.message.includes("401")) {
          errorMessage = "Oturum s√ºreniz dolmu≈ü. L√ºtfen tekrar giri≈ü yapƒ±n.";
        } else if (error.message.includes("400")) {
          errorMessage = "Form bilgileri ge√ßersiz. L√ºtfen kontrol edin.";
        } else {
          errorMessage = `Hata: ${error.message}`;
        }
      }

      toast({
        variant: "destructive",
        title: "Hata",
        description: errorMessage,
      });
    },
  });

  const onSubmit = (data: SupportTicketForm) => {
    console.log("Form submission:", { data, isAuthenticated, user: user?.id });

    if (!isAuthenticated || !user) {
      console.log("Not authenticated, redirecting to login");
      toast({
        variant: "destructive",
        title: "Giri≈ü Yapƒ±n",
        description: "Destek talebi olu≈üturmak i√ßin giri≈ü yapmanƒ±z gerekir",
      });
      navigate("/login");
      return;
    }

    console.log("Creating ticket mutation...");
    createTicketMutation.mutate(data);
  };

  if (!isAuthenticated || !user) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="container mx-auto px-4 py-12">
          <Card className="max-w-md mx-auto">
            <CardHeader className="text-center">
              <MessageCircleQuestion className="h-12 w-12 text-primary mx-auto mb-4" />
              <CardTitle>Giri≈ü Yapƒ±n</CardTitle>
              <CardDescription>
                Destek talebi olu≈üturmak i√ßin hesabƒ±nƒ±za giri≈ü yapmanƒ±z gerekir
              </CardDescription>
            </CardHeader>
            <CardContent>
              <Button 
                onClick={() => navigate("/login")} 
                className="w-full"
              >
                Giri≈ü Yap
              </Button>
            </CardContent>
          </Card>
        </div>
        <Footer />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <div className="container mx-auto px-4 py-8">
        <div className="max-w-2xl mx-auto">
          <div className="flex items-center gap-4 mb-6">
            <Button
              variant="ghost"
              onClick={() => navigate("/contact")}
              className="flex items-center gap-2"
            >
              <ArrowLeft className="h-4 w-4" />
              Geri
            </Button>
            <div>
              <h1 className="text-2xl font-bold text-gray-900">Destek Talebi Olu≈ütur</h1>
              <p className="text-gray-600">Sorunlarƒ±nƒ±z i√ßin destek talebi olu≈üturun</p>
            </div>
          </div>

          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <MessageCircleQuestion className="h-5 w-5 text-primary" />
                Yeni Destek Talebi
              </CardTitle>
              <CardDescription>
                Sorununuzu detaylƒ± bir ≈üekilde a√ßƒ±klayƒ±n. Destek ekibimiz en kƒ±sa s√ºrede size d√∂n√º≈ü yapacaktƒ±r.
              </CardDescription>
            </CardHeader>

            <CardContent>
              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
                  <FormField
                    control={form.control}
                    name="subject"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Konu</FormLabel>
                        <FormControl>
                          <Input 
                            placeholder="Sorunun kƒ±sa ba≈ülƒ±ƒüƒ±..."
                            {...field}
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <FormField
                      control={form.control}
                      name="category"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Kategori</FormLabel>
                          <Select onValueChange={field.onChange} defaultValue={field.value}>
                            <FormControl>
                              <SelectTrigger>
                                <SelectValue placeholder="Kategori se√ßin" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              <SelectItem value="technical">Teknik Destek</SelectItem>
                              <SelectItem value="sales">Satƒ±≈ü</SelectItem>
                              <SelectItem value="warranty">Garanti</SelectItem>
                              <SelectItem value="other">Diƒüer</SelectItem>
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="priority"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>√ñncelik</FormLabel>
                          <Select onValueChange={field.onChange} defaultValue={field.value}>
                            <FormControl>
                              <SelectTrigger>
                                <SelectValue placeholder="√ñncelik se√ßin" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              <SelectItem value="low">D√º≈ü√ºk</SelectItem>
                              <SelectItem value="medium">Orta</SelectItem>
                              <SelectItem value="high">Y√ºksek</SelectItem>
                              <SelectItem value="urgent">Acil</SelectItem>
                            </SelectContent>
                          </Select>
                          <FormMessage />
