client/src/components/CategoryIconsManag.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Switch } from "@/components/ui/switch";
import { Plus, Edit3, Trash2, Eye, EyeOff, Move, Upload } from "lucide-react";
import type { CategoryIcon, InsertCategoryIcon } from "@shared/schema";
import { ObjectUploader } from "./ObjectUploader";

export default function CategoryIconsManagement() {
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [editingIcon, setEditingIcon] = useState<CategoryIcon | null>(null);
  const [formData, setFormData] = useState<Partial<InsertCategoryIcon>>({
    name: "",
    icon: "",
    linkUrl: "",
    isActive: true,
    sortOrder: 0,
  });

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch category icons
  const { data: categoryIcons = [], isLoading } = useQuery<CategoryIcon[]>({
    queryKey: ["/api/admin/category-icons"],
  });

  // Create mutation
  const createMutation = useMutation({
    mutationFn: async (data: InsertCategoryIcon) => {
      const response = await apiRequest("/api/admin/category-icons", "POST", data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/category-icons"] });
      queryClient.invalidateQueries({ queryKey: ["/api/category-icons"] });
      setIsCreateDialogOpen(false);
      resetForm();
      toast({
        title: "Ba≈üarƒ±lƒ±",
        description: "Kategori ikonu ba≈üarƒ±yla olu≈üturuldu",
      });
    },
    onError: (error) => {
      toast({
        title: "Hata",
        description: "Kategori ikonu olu≈üturulurken hata olu≈ütu",
        variant: "destructive",
      });
    },
  });

  // Update mutation
  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: Partial<InsertCategoryIcon> }) => {
      const response = await apiRequest(`/api/admin/category-icons/${id}`, "PUT", data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/category-icons"] });
      queryClient.invalidateQueries({ queryKey: ["/api/category-icons"] });
      setEditingIcon(null);
      resetForm();
      toast({
        title: "Ba≈üarƒ±lƒ±",
        description: "Kategori ikonu ba≈üarƒ±yla g√ºncellendi",
      });
    },
    onError: (error) => {
      toast({
        title: "Hata",
        description: "Kategori ikonu g√ºncellenirken hata olu≈ütu",
        variant: "destructive",
      });
    },
  });

  // Delete mutation
  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      const response = await apiRequest(`/api/admin/category-icons/${id}`, "DELETE");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/category-icons"] });
      queryClient.invalidateQueries({ queryKey: ["/api/category-icons"] });
      toast({
        title: "Ba≈üarƒ±lƒ±",
        description: "Kategori ikonu ba≈üarƒ±yla silindi",
      });
    },
    onError: (error) => {
      toast({
        title: "Hata",
        description: "Kategori ikonu silinirken hata olu≈ütu",
        variant: "destructive",
      });
    },
  });

  // Toggle active mutation
  const toggleMutation = useMutation({
    mutationFn: async ({ id, isActive }: { id: string; isActive: boolean }) => {
      const response = await apiRequest(`/api/admin/category-icons/${id}/toggle`, "PATCH", { isActive });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/category-icons"] });
      queryClient.invalidateQueries({ queryKey: ["/api/category-icons"] });
      toast({
        title: "Ba≈üarƒ±lƒ±",
        description: "Kategori ikonu durumu g√ºncellendi",
      });
    },
    onError: (error) => {
      toast({
        title: "Hata",
        description: "Kategori ikonu durumu g√ºncellenirken hata olu≈ütu",
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setFormData({
      name: "",
      icon: "",
      linkUrl: "",
      isActive: true,
      sortOrder: 0,
    });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.name || !formData.icon) {
      toast({
        title: "Hata",
        description: "ƒ∞sim ve ikon alanlarƒ± zorunludur",
        variant: "destructive",
      });
      return;
    }

    if (editingIcon) {
      updateMutation.mutate({ id: editingIcon.id, data: formData });
    } else {
      createMutation.mutate(formData as InsertCategoryIcon);
    }
  };

  const handleEdit = (icon: CategoryIcon) => {
    setEditingIcon(icon);
    setFormData({
      name: icon.name,
      icon: icon.icon,
      linkUrl: icon.linkUrl || "",
      isActive: icon.isActive,
      sortOrder: icon.sortOrder,
    });
  };

  const handleDelete = (id: string) => {
    if (confirm("Bu kategori ikonunu silmek istediƒüinizden emin misiniz?")) {
      deleteMutation.mutate(id);
    }
  };

  const handleToggle = (id: string, currentStatus: boolean) => {
