import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { Plus, Edit, Trash2, Eye, Save, ImageIcon } from "lucide-react";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { apiRequest } from "@/lib/queryClient";
import { ObjectUploader } from "@/components/ObjectUploader";

interface ProductCard {
  id: string;
  title: string;
  subtitle: string;
  price: string;
  image: string;
  backgroundColor: string;
  buttonText: string;
  buttonUrl: string;
  imageScale: number;
  imagePositionX: number;
  imagePositionY: number;
  isActive: boolean;
  sortOrder: number;
}

export default function ProductCardsManagement() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [showNewCardDialog, setShowNewCardDialog] = useState(false);
  const [editingCard, setEditingCard] = useState<ProductCard | null>(null);

  const [newCardData, setNewCardData] = useState({
    title: "",
    subtitle: "",
    price: "",
    image: "/public-objects/uploads/placeholder-product.jpg",
    backgroundColor: "#6366f1",
    buttonText: "ƒ∞ncele",
    buttonUrl: "/products",
    imageScale: 1.0,
    imagePositionX: 50,
    imagePositionY: 50,
    isActive: true,
    sortOrder: 1
  });

  const { data: cards = [], isLoading } = useQuery<ProductCard[]>({
    queryKey: ["/api/admin/product-cards"],
  });

  const createCardMutation = useMutation({
    mutationFn: (cardData: any) => apiRequest("/api/admin/product-cards", "POST", cardData),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/product-cards"] });
      toast({
        title: "Ba≈üarƒ±lƒ±",
        description: "√úr√ºn kartƒ± olu≈üturuldu",
      });
    },
    onError: () => {
      toast({
        title: "Hata",
        description: "√úr√ºn kartƒ± olu≈üturulurken hata olu≈ütu",
        variant: "destructive",
      });
    },
  });

  const updateCardMutation = useMutation({
    mutationFn: ({ id, updates }: { id: string; updates: any }) =>
      apiRequest(`/api/admin/product-cards/${id}`, "PUT", updates),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/product-cards"] });
      setEditingCard(null);
      toast({
        title: "Ba≈üarƒ±lƒ±",
        description: "√úr√ºn kartƒ± g√ºncellendi",
      });
    },
    onError: () => {
      toast({
        title: "Hata",
        description: "√úr√ºn kartƒ± g√ºncellenirken hata olu≈ütu",
        variant: "destructive",
      });
    },
  });

  const deleteCardMutation = useMutation({
    mutationFn: (id: string) => apiRequest(`/api/admin/product-cards/${id}`, "DELETE"),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/product-cards"] });
      toast({
        title: "Ba≈üarƒ±lƒ±",
        description: "√úr√ºn kartƒ± silindi",
      });
    },
    onError: () => {
      toast({
        title: "Hata",
        description: "√úr√ºn kartƒ± silinirken hata olu≈ütu",
        variant: "destructive",
      });
    },
  });

  const handleCreateCard = () => {
    createCardMutation.mutate(newCardData);
    setShowNewCardDialog(false);
    setNewCardData({
      title: "",
      subtitle: "",
      price: "",
      image: "/public-objects/uploads/placeholder-product.jpg",
      backgroundColor: "#6366f1",
      buttonText: "ƒ∞ncele",
      buttonUrl: "/products",
      imageScale: 1.0,
      imagePositionX: 50,
      imagePositionY: 50,
      isActive: true,
      sortOrder: 1
    });
  };

  const handleUpdateCard = () => {
    if (editingCard) {
      const updates = {
        ...editingCard,
        imageScale: editingCard.imageScale?.toString(),
        imagePositionX: editingCard.imagePositionX?.toString(),
        imagePositionY: editingCard.imagePositionY?.toString(),
      };

      updateCardMutation.mutate({
        id: editingCard.id,
        updates,
      });
    }
  };

  const handleDeleteCard = (id: string) => {
    if (confirm("Bu √ºr√ºn kartƒ±nƒ± silmek istediƒüinize emin misiniz?")) {
      deleteCardMutation.mutate(id);
    }
  };

  if (isLoading) {
    return <div className="text-center py-8">√úr√ºn kartlarƒ± y√ºkleniyor...</div>;
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold">√úr√ºn Kartlarƒ± Y√∂netimi</h2>
          <p className="text-gray-600">Ana sayfada g√∂sterilecek √ºr√ºn kartlarƒ±nƒ± y√∂netin</p>
        </div>

        <Dialog open={showNewCardDialog} onOpenChange={setShowNewCardDialog}>
          <DialogTrigger asChild>
            <Button>
              <Plus className="w-4 h-4 mr-2" />
              Yeni Kart Ekle
            </Button>
          </DialogTrigger>

          <DialogContent className="max-w-lg max-h-[90vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle>Yeni √úr√ºn Kartƒ±</DialogTitle>
            </DialogHeader>

            <div className="space-y-4">
              <div>
                <Label htmlFor="new-title">Ba≈ülƒ±k</Label>
                <Input
                  id="new-title"
                  value={newCardData.title}
                  onChange={(e) => setNewCardData({ ...newCardData, title: e.target.value })}
                  placeholder="√∂rn: SM 6670"
                />
              </div>

              <div>
                <Label htmlFor="new-subtitle">Alt Ba≈ülƒ±k</Label>
                <Textarea
                  id="new-subtitle"
                  value={newCardData.subtitle}
                  onChange={(e) => setNewCardData({ ...newCardData, subtitle: e.target.value })}
                  placeholder="√∂rn: Slush ve Buzlu ƒ∞√ßecek Makineleri"
                  rows={2}
                />
              </div>

              <div>
                <Label htmlFor="new-price">Fiyat</Label>
                <Input
                  id="new-price"
                  value={newCardData.price}
                  onChange={(e) => setNewCardData({ ...newCardData, price: e.target.value })}
                  placeholder="√∂rn: 12.999 TL"
                />
              </div>

              <div>
                <Label htmlFor="new-backgroundColor">Arka Plan Rengi</Label>
                <Input
                  id="new-backgroundColor"
                  type="color"
                  value={newCardData.backgroundColor}
                  onChange={(e) => setNewCardData({ ...newCardData, backgroundColor: e.target.value })}
                  className="h-10"
                />
              </div>

              <div>
                <Label htmlFor="new-buttonText">Buton Metni</Label>
                <Input
                  id="new-buttonText"
                  value={newCardData.buttonText}
                  onChange={(e) => setNewCardData({ ...newCardData, buttonText: e.target.value })}
                  placeholder="√∂rn: ƒ∞ncele"
                />
              </div>

              <div>
                <Label htmlFor="new-buttonUrl">Buton Y√∂nlendirme</Label>
                <Input
                  id="new-buttonUrl"
                  value={newCardData.buttonUrl}
                  onChange={(e) => setNewCardData({ ...newCardData, buttonUrl: e.target.value })}
                  placeholder="√∂rn: /products?category=icecek-makinesi"
                />
              </div>

              <div>
                <Label>√úr√ºn G√∂rseli</Label>
                <p className="text-sm text-gray-500 mb-2">
                  √ñnerilen boyut: 400x400px, maksimum 3MB (JPG, PNG)
                </p>

                {!newCardData.image || newCardData.image === "/public-objects/uploads/placeholder-product.jpg" ? (
                  <div className="border-2 border-dashed border-gray-200 rounded-lg p-4 text-center hover:border-primary/30 transition-colors cursor-pointer group">
                    <ObjectUploader
                      maxNumberOfFiles={1}
                      maxFileSize={3145728} // 3MB
                      onGetUploadParameters={async () => {
                        const response = await fetch("/api/product-cards/upload", {
                          method: "POST"
                        });
                        const data = await response.json();
                        return {
                          method: "PUT" as const,
                          url: data.uploadURL
                        };
                      }}
                      onComplete={(result: any) => {
                        if (result.successful.length > 0) {
                          const uploadURL = result.successful[0].uploadURL;
                          const url = new URL(uploadURL);
                          const pathParts = url.pathname.split('/');
                          const fileName = pathParts[pathParts.length - 1];
                          const objectPath = `/public-objects/product-images/${fileName}`;
                          setNewCardData({ ...newCardData, image: objectPath });
                          toast({
                            title: "Ba≈üarƒ±lƒ±",
                            description: "√úr√ºn g√∂rseli y√ºklendi",
                          });
                        }
                      }}
                      buttonClassName="w-full h-20 bg-transparent border-none hover:bg-gray-50 text-gray-500 group-hover:text-primary"
                    >
                      <div className="flex flex-col items-center">
                        <ImageIcon className="w-8 h-8 mb-2 text-gray-300 group-hover:text-primary" />
                        <p className="text-sm font-medium">√úr√ºn G√∂rseli Ekle</p>
                      </div>
                    </ObjectUploader>
                  </div>
                ) : (
                  <div className="relative group">
                    <img 
                      src={newCardData.image} 
                      alt="√úr√ºn g√∂rseli"
                      className="w-full h-32 object-cover rounded border"
                    />
                    <div className="absolute inset-0 bg-black/0 group-hover:bg-black/40 transition-all duration-200 flex items-center justify-center opacity-0 group-hover:opacity-100 rounded">
                      <Button
                        type="button"
                        variant="destructive"
                        size="sm"
                        onClick={() => setNewCardData({ ...newCardData, image: "/public-objects/uploads/placeholder-product.jpg" })}
                      >
                        <Trash2 className="w-4 h-4 mr-1" />
                        Kaldƒ±r
                      </Button>
                    </div>
                  </div>
                )}
              </div>

              {/* Image Customization */}
              {newCardData.image && newCardData.image !== "/public-objects/uploads/placeholder-product.jpg" && (
                <div className="space-y-4">
                  <div>
                    <Label htmlFor="image-scale">G√∂rsel Boyutu: {newCardData.imageScale}x</Label>
                    <input
                      id="image-scale"
                      type="range"
                      min="0.5"
                      max="3.0"
                      step="0.1"
                      value={newCardData.imageScale}
                      onChange={(e) => setNewCardData({ ...newCardData, imageScale: parseFloat(e.target.value) })}
                      className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer"
                    />
                  </div>

                  <div>
                    <Label htmlFor="image-position-x">Yatay Pozisyon: {newCardData.imagePositionX}%</Label>
                    <input
                      id="image-position-x"
                      type="range"
                      min="0"
                      max="100"
                      step="5"
                      value={newCardData.imagePositionX}
                      onChange={(e) => setNewCardData({ ...newCardData, imagePositionX: parseFloat(e.target.value) })}
                      className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer"
                    />
                  </div>

                  <div>
                    <Label htmlFor="image-position-y">Dikey Pozisyon: {newCardData.imagePositionY}%</Label>
                    <input
                      id="image-position-y"
                      type="range"
                      min="0"
                      max="100"
                      step="5"
                      value={newCardData.imagePositionY}
                      onChange={(e) => setNewCardData({ ...newCardData, imagePositionY: parseFloat(e.target.value) })}
                      className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer"
                    />
                  </div>
                </div>
              )}

              {/* Realistic Preview */}
              {newCardData.title && newCardData.subtitle && newCardData.price && (
                <div>
                  <Label>Ger√ßek Boyutlu √ñnizleme</Label>
                  <p className="text-sm text-gray-500 mb-2">Kartƒ±n ana sayfada nasƒ±l g√∂r√ºneceƒüi</p>
                  <div className="max-w-xs mx-auto">
                    <div 
                      className="relative rounded-2xl overflow-hidden shadow-lg text-white min-h-[500px] flex flex-col"
                      style={{ backgroundColor: newCardData.backgroundColor }}
                    >
                      {/* Content Section - Top */}
                      <div className="text-center space-y-2 p-4 pb-2">
                        <h3 className="text-lg font-bold leading-tight">{newCardData.title}</h3>
                        <p className="text-xs opacity-90 leading-relaxed">{newCardData.subtitle}</p>
                        <p className="text-base font-semibold mb-3">{newCardData.price}</p>

                        <Button variant="secondary" size="sm" className="bg-white text-gray-900">
                          {newCardData.buttonText}
                        </Button>
                      </div>

                      {/* Large Product Image - Bottom */}
                      {newCardData.image && newCardData.image !== "/public-objects/uploads/placeholder-product.jpg" && (
                        <div className="flex-1 flex items-center justify-center min-h-[300px] p-4 overflow-hidden">
                          <img 
                            src={newCardData.image}
                            alt={newCardData.title}
                            className="object-contain drop-shadow-xl"
                            style={{ 
                              transform: `scale(${newCardData.imageScale})`,
                              objectPosition: `${newCardData.imagePositionX}% ${newCardData.imagePositionY}%`,
                              width: '100%',
                              height: '100%'
                            }}
                          />
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              )}

              <div className="flex gap-2">
                <Button 
                  onClick={handleCreateCard}
                  disabled={!newCardData.title || !newCardData.subtitle || !newCardData.price}
                  size="sm"
                  className="flex-1"
                >
                  <Save className="w-3 h-3 mr-1" />
                  Olu≈ütur
                </Button>
                <Button 
                  variant="outline" 
                  onClick={() => setShowNewCardDialog(false)}
                  size="sm"
                  className="flex-1"
                >
                  ƒ∞ptal
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      </div>

      {/* Cards Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {cards.map((card) => (
          <Card key={card.id} className="overflow-hidden">
            <div 
              className="relative h-32 p-4 text-white"
              style={{ backgroundColor: card.backgroundColor }}
            >
              <div className="text-center space-y-1">
                <h3 className="font-bold">{card.title}</h3>
                <p className="text-xs opacity-90">{card.subtitle}</p>
                <p className="text-sm font-semibold">{card.price}</p>
              </div>

              {card.image && card.image !== "/public-objects/uploads/placeholder-product.jpg" && (
                <div className="absolute bottom-2 right-2">
                  <img 
                    src={card.image}
                    alt="√úr√ºn"
                    className="w-12 h-12 object-cover rounded border"
                  />
                </div>
              )}
            </div>

            <CardContent className="p-4">
              <div className="space-y-3">
                <div className="flex items-center justify-between text-sm">
                  <span className="text-gray-500">Durum:</span>
                  <Badge variant={card.isActive ? "default" : "outline"}>
                    {card.isActive ? "Aktif" : "Pasif"}
                  </Badge>
                </div>

                <div className="text-sm">
                  <span className="text-gray-500">Buton: </span>
                  <span className="font-mono text-xs">{card.buttonText} ‚Üí {card.buttonUrl}</span>
                </div>

                <div className="flex gap-1">
                  <Dialog>
                    <DialogTrigger asChild>
                      <Button 
                        variant="outline" 
                        size="sm" 
                        className="flex-1 text-xs py-1"
                        onClick={() => setEditingCard(card)}
                      >
                        <Edit className="w-3 h-3 mr-1" />
                        D√ºzenle
                      </Button>
                    </DialogTrigger>

                    <DialogContent className="max-w-lg max-h-[90vh] overflow-y-auto">
                      <DialogHeader>
                        <DialogTitle>√úr√ºn Kartƒ±nƒ± D√ºzenle</DialogTitle>
                      </DialogHeader>

                      {editingCard && (
                        <div className="space-y-4">
                          <div>
                            <Label htmlFor="edit-title">Ba≈ülƒ±k</Label>
                            <Input
                              id="edit-title"
                              value={editingCard.title}
                              onChange={(e) => setEditingCard({
                                ...editingCard,
                                title: e.target.value
                              })}
                            />
                          </div>

                          <div>
                            <Label htmlFor="edit-subtitle">Alt Ba≈ülƒ±k</Label>
                            <Textarea
                              id="edit-subtitle"
                              value={editingCard.subtitle}
                              onChange={(e) => setEditingCard({
                                ...editingCard,
                                subtitle: e.target.value
                              })}
                              rows={2}
                            />
                          </div>

                          <div>
                            <Label htmlFor="edit-price">Fiyat</Label>
                            <Input
                              id="edit-price"
                              value={editingCard.price}
                              onChange={(e) => setEditingCard({
                                ...editingCard,
                                price: e.target.value
                              })}
                            />
                          </div>

                          <div>
                            <Label htmlFor="edit-backgroundColor">Arka Plan Rengi</Label>
                            <Input
                              id="edit-backgroundColor"
                              type="color"
                              value={editingCard.backgroundColor}
                              onChange={(e) => setEditingCard({
                                ...editingCard,
                                backgroundColor: e.target.value
                              })}
                              className="h-10"
                            />
                          </div>

                          <div>
                            <Label htmlFor="edit-buttonText">Buton Metni</Label>
                            <Input
                              id="edit-buttonText"
                              value={editingCard.buttonText}
                              onChange={(e) => setEditingCard({
                                ...editingCard,
                                buttonText: e.target.value
                              })}
                            />
                          </div>

                          <div>
                            <Label htmlFor="edit-buttonUrl">Buton Y√∂nlendirme</Label>
                            <Input
                              id="edit-buttonUrl"
                              value={editingCard.buttonUrl}
                              onChange={(e) => setEditingCard({
                                ...editingCard,
                                buttonUrl: e.target.value
                              })}
                            />
                          </div>

                          {/* Image Customization for Edit */}
                          {editingCard.image && editingCard.image !== "/public-objects/uploads/placeholder-product.jpg" && (
                            <div className="space-y-4">
                              <div>
                                <Label htmlFor="edit-image-scale">G√∂rsel Boyutu: {editingCard.imageScale || 1.0}x</Label>
                                <input
                                  id="edit-image-scale"
                                  type="range"
                                  min="0.5"
                                  max="3.0"
                                  step="0.1"
                                  value={editingCard.imageScale || 1.0}
                                  onChange={(e) => setEditingCard({
                                    ...editingCard,
                                    imageScale: parseFloat(e.target.value)
                                  })}
                                  className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer"
                                />
                              </div>

                              <div>
                                <Label htmlFor="edit-image-position-x">Yatay Pozisyon: {editingCard.imagePositionX || 50}%</Label>
                                <input
                                  id="edit-image-position-x"
                                  type="range"
                                  min="0"
                                  max="100"
                                  step="5"
                                  value={editingCard.imagePositionX || 50}
                                  onChange={(e) => setEditingCard({
                                    ...editingCard,
                                    imagePositionX: parseFloat(e.target.value)
                                  })}
                                  className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer"
                                />
                              </div>

                              <div>
                                <Label htmlFor="edit-image-position-y">Dikey Pozisyon: {editingCard.imagePositionY || 50}%</Label>
                                <input
                                  id="edit-image-position-y"
                                  type="range"
                                  min="0"
                                  max="100"
                                  step="5"
                                  value={editingCard.imagePositionY || 50}
                                  onChange={(e) => setEditingCard({
                                    ...editingCard,
                                    imagePositionY: parseFloat(e.target.value)
                                  })}
                                  className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer"
                                />
                              </div>

                              {/* Edit Preview */}
                              <div>
                                <Label>√ñnizleme</Label>
                                <div className="max-w-xs mx-auto">
                                  <div 
                                    className="relative rounded-2xl overflow-hidden shadow-lg text-white min-h-[400px] flex flex-col"
                                    style={{ backgroundColor: editingCard.backgroundColor }}
                                  >
                                    <div className="text-center space-y-1 p-3 pb-2">
                                      <h3 className="text-base font-bold leading-tight">{editingCard.title}</h3>
                                      <p className="text-xs opacity-90">{editingCard.subtitle}</p>
                                      <p className="text-sm font-semibold">{editingCard.price}</p>
                                      <Button variant="secondary" size="sm" className="bg-white text-gray-900 mt-2">
                                        {editingCard.buttonText}
                                      </Button>
                                    </div>
                                    <div className="flex-1 flex items-center justify-center min-h-[240px] p-3 overflow-hidden">
                                      <img 
                                        src={editingCard.image}
                                        alt={editingCard.title}
                                        className="object-contain drop-shadow-xl"
                                        style={{ 
                                          transform: `scale(${editingCard.imageScale || 1.0})`,
                                          objectPosition: `${editingCard.imagePositionX || 50}% ${editingCard.imagePositionY || 50}%`,
                                          width: '100%',
                                          height: '100%'
                                        }}
                                      />
                                    </div>
                                  </div>
                                </div>
                              </div>
                            </div>
                          )}

                          <div className="flex items-center space-x-2">
                            <input
                              type="checkbox"
                              id="edit-isActive"
                              checked={editingCard.isActive}
                              onChange={(e) => setEditingCard({
                                ...editingCard,
                                isActive: e.target.checked
                              })}
                              className="rounded border-gray-300"
                            />
                            <Label htmlFor="edit-isActive">Aktif</Label>
                          </div>

                          <Button 
                            onClick={handleUpdateCard}
                            disabled={updateCardMutation.isPending}
                            className="w-full"
                          >
                            <Save className="w-4 h-4 mr-2" />
                            {updateCardMutation.isPending ? "Kaydediliyor..." : "Kaydet"}
                          </Button>
                        </div>
                      )}
                    </DialogContent>
                  </Dialog>

                  <Button
                    variant="outline"
                    size="sm"
                    className="text-xs py-1 text-red-600 hover:text-red-700 hover:bg-red-50"
                    onClick={() => handleDeleteCard(card.id)}
                    disabled={deleteCardMutation.isPending}
                  >
                    <Trash2 className="w-3 h-3" />
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {cards.length === 0 && (
        <div className="text-center py-12 border-2 border-dashed border-gray-200 rounded-lg">
          <ImageIcon className="w-12 h-12 text-gray-400 mx-auto mb-4" />
          <p className="text-gray-600 font-medium mb-1">Hen√ºz √ºr√ºn kartƒ± yok</p>
          <p className="text-sm text-gray-400">ƒ∞lk √ºr√ºn kartƒ±nƒ±zƒ± olu≈üturun</p>
        </div>
      )}
    </div>
  );
}

client/src/components/warranty-managemen.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Checkbox } from "@/components/ui/checkbox";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { Command, CommandEmpty, CommandGroup, CommandInput, CommandItem } from "@/components/ui/command";
import { ChevronDown, Check } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Shield, Edit2, Save, Plus, X, Settings } from "lucide-react";

export function WarrantyManagement() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [editingWarranty, setEditingWarranty] = useState<any>(null);
  const [managingMappings, setManagingMappings] = useState<any>(null);
  const [newMapping, setNewMapping] = useState({ 
    productCategory: "", 
    productSubcategories: [] as string[] 
  });
  const [categoryDropdownOpen, setCategoryDropdownOpen] = useState(false);

  const { data: warranties = [], isLoading } = useQuery<any[]>({
    queryKey: ["/api/admin/extended-warranty/categories"],
  });

  // Get warranty category mappings for the selected warranty
  const { data: mappings = [], isLoading: mappingsLoading } = useQuery<any[]>({
    queryKey: ["/api/admin/extended-warranty/categories", managingMappings?.categoryName, "mappings"],
    enabled: !!managingMappings?.categoryName,
  });

  // Get available product categories
  const { data: productCategories = [], isLoading: categoriesLoading } = useQuery<any[]>({
    queryKey: ["/api/admin/product-categories"],
  });

  const updateWarrantyMutation = useMutation({
    mutationFn: async ({ categoryName, updates }: { categoryName: string; updates: any }) => {
      return await apiRequest(`/api/admin/extended-warranty/categories/${encodeURIComponent(categoryName)}`, "PUT", updates);
    },
    onSuccess: () => {
      toast({ title: "Ba≈üarƒ±lƒ±!", description: "Ek garanti fiyatlarƒ± g√ºncellendi" });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/extended-warranty/categories"] });
      setEditingWarranty(null);
    },
    onError: () => {
      toast({ title: "Hata", description: "Ek garanti fiyatlarƒ± g√ºncellenemedi", variant: "destructive" });
    },
  });

  const handleSave = (warranty: any) => {
    updateWarrantyMutation.mutate({
      categoryName: warranty.categoryName,
      updates: {
        twoYearPrice: parseFloat(warranty.twoYearPrice),
        fourYearPrice: parseFloat(warranty.fourYearPrice)
      }
    });
  };

  const addMappingMutation = useMutation({
    mutationFn: async ({ categoryName, productCategory, productSubcategory }: { categoryName: string; productCategory: string; productSubcategory?: string }) => {
      return await apiRequest(`/api/admin/extended-warranty/categories/${encodeURIComponent(categoryName)}/mappings`, "POST", {
        productCategory,
        productSubcategories: productSubcategory ? [productSubcategory] : ["ALL_SUBCATEGORIES"]
      });
    },
    onSuccess: () => {
      toast({ title: "Ba≈üarƒ±lƒ±!", description: "Kategori e≈üle≈ütirmesi eklendi" });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/extended-warranty/categories", managingMappings?.categoryName, "mappings"] });
      setNewMapping({ productCategory: "", productSubcategories: [] });
    },
    onError: () => {
      toast({ title: "Hata", description: "Kategori e≈üle≈ütirmesi eklenemedi", variant: "destructive" });
    },
  });

  const deleteMappingMutation = useMutation({
    mutationFn: async (mappingId: string) => {
      return await apiRequest(`/api/admin/extended-warranty/mappings/${mappingId}`, "DELETE");
    },
    onSuccess: () => {
      toast({ title: "Ba≈üarƒ±lƒ±!", description: "Kategori e≈üle≈ütirmesi silindi" });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/extended-warranty/categories", managingMappings?.categoryName, "mappings"] });
    },
    onError: () => {
      toast({ title: "Hata", description: "Kategori e≈üle≈ütirmesi silinemedi", variant: "destructive" });
    },
  });

  const handleAddMapping = () => {
    if (!newMapping.productCategory) {
      toast({ title: "Hata", description: "L√ºtfen bir kategori se√ßin", variant: "destructive" });
      return;
    }

    // Eƒüer hi√ß alt kategori se√ßilmemi≈üse, t√ºm alt kategoriler i√ßin ekle
    const subcategoriesToAdd = newMapping.productSubcategories.length === 0 
      ? [undefined] // T√ºm alt kategoriler i√ßin
      : newMapping.productSubcategories;

    // Her alt kategori i√ßin ayrƒ± mapping ekle
    subcategoriesToAdd.forEach(subcategory => {
      addMappingMutation.mutate({
        categoryName: managingMappings.categoryName,
        productCategory: newMapping.productCategory,
        productSubcategory: subcategory === "ALL_SUBCATEGORIES" ? undefined : subcategory
      });
    });
  };

  if (isLoading) {
    return <div>Y√ºkleniyor...</div>;
  }

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center">
          <Shield className="h-5 w-5 mr-2" />
          Ek Garanti Fiyat Y√∂netimi
        </CardTitle>
        <p className="text-sm text-gray-600">
          Her kategori i√ßin ek garanti fiyatlarƒ±nƒ± d√ºzenleyebilirsiniz
        </p>
      </CardHeader>
      <CardContent>
        <div className="space-y-4">
          {warranties.map((warranty) => (
            <div key={warranty.categoryName} className="border rounded-lg p-4">
              <div className="flex items-center justify-between mb-4">
                <div>
                  <h3 className="font-medium text-lg">{warranty.categoryName}</h3>
                  <p className="text-sm text-gray-600">Mevcut fiyatlar: 2 Yƒ±l: {warranty.twoYearPrice}‚Ç∫, 4 Yƒ±l: {warranty.fourYearPrice}‚Ç∫</p>
                </div>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => setEditingWarranty({
                    categoryName: warranty.categoryName,
                    twoYearPrice: warranty.twoYearPrice.toString(),
                    fourYearPrice: warranty.fourYearPrice.toString()
                  })}
                  data-testid={`button-edit-warranty-${warranty.categoryName}`}
                >
                  <Edit2 className="h-4 w-4 mr-2" />
                  D√ºzenle
                </Button>
                <Button
                  variant="secondary"
                  size="sm"
                  onClick={() => {
                    setManagingMappings({
                      categoryName: warranty.categoryName
                    });
                  }}
                  data-testid={`button-manage-mappings-${warranty.categoryName}`}
                >
                  <Settings className="h-4 w-4 mr-2" />
                  Kategori E≈üle≈ütirme
                </Button>
              </div>

              {editingWarranty?.categoryName === warranty.categoryName && (
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mt-4">
                  <div>
                    <Label>2 Yƒ±l Ek Garanti Fiyatƒ± (‚Ç∫)</Label>
                    <Input
                      type="number"
                      step="0.01"
                      value={editingWarranty.twoYearPrice}
                      onChange={(e) => setEditingWarranty({
                        ...editingWarranty,
                        twoYearPrice: e.target.value
                      })}
                      data-testid={`input-warranty-2year-${warranty.categoryName}`}
                    />
                  </div>
                  <div>
                    <Label>4 Yƒ±l Ek Garanti Fiyatƒ± (‚Ç∫)</Label>
                    <Input
                      type="number"
                      step="0.01"
                      value={editingWarranty.fourYearPrice}
                      onChange={(e) => setEditingWarranty({
                        ...editingWarranty,
                        fourYearPrice: e.target.value
                      })}
                      data-testid={`input-warranty-4year-${warranty.categoryName}`}
                    />
                  </div>
                  <div className="flex items-end space-x-2">
                    <Button
                      onClick={() => handleSave(editingWarranty)}
                      disabled={updateWarrantyMutation.isPending}
                      size="sm"
                      data-testid={`button-save-warranty-${warranty.categoryName}`}
                    >
                      <Save className="h-4 w-4 mr-2" />
                      Kaydet
                    </Button>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => setEditingWarranty(null)}
                      data-testid={`button-cancel-warranty-${warranty.categoryName}`}
                    >
                      ƒ∞ptal
                    </Button>
                  </div>
                </div>
              )}
            </div>
          ))}
        </div>

        <div className="mt-6 p-4 bg-blue-50 rounded-lg">
          <h4 className="font-medium text-blue-900 mb-2">üìã Kategori E≈üle≈ütirme Bilgisi</h4>
          <div className="text-sm text-blue-800 space-y-1">
            <p>‚Ä¢ <strong>Ankastre kategorisi:</strong> Sadece ankastre fƒ±rƒ±nlar i√ßin ek garanti. Ankastre mikrodalga hari√ß.</p>
            <p>‚Ä¢ <strong>Buzdolabƒ± kategorileri:</strong> √ústten/Alttan Donduruculu ‚Üí No-frost fiyatlarƒ± kullanƒ±lƒ±r.</p>
            <p>‚Ä¢ <strong>Klima kategorileri:</strong> Split, inverter, ƒ±sƒ±tma soƒüutma ‚Üí Ev Tipi Klima fiyatlarƒ± kullanƒ±lƒ±r.</p>
            <p>‚Ä¢ <strong>Mini kategoriler:</strong> Mini buzdolabƒ±, mini fƒ±rƒ±n vb. ‚Üí Ek garanti YOK.</p>
          </div>
        </div>

        {/* Kategori E≈üle≈ütirme Dialog */}
        {managingMappings && (
          <Dialog open={!!managingMappings} onOpenChange={() => setManagingMappings(null)}>
            <DialogContent className="max-w-6xl max-h-[95vh] overflow-y-auto bg-white border-2 shadow-2xl" style={{ zIndex: 9999 }}>
              <DialogHeader className="border-b pb-4">
                <DialogTitle className="text-xl font-bold text-gray-900">
                  {managingMappings.categoryName} - Kategori E≈üle≈ütirmesi
                </DialogTitle>
                <p className="text-sm text-gray-600">
                  Bu ek garanti hangi √ºr√ºn kategorilerini kapsayacak?
                </p>
              </DialogHeader>

              <div className="space-y-4">
                {/* Mevcut E≈üle≈ütirmeler */}
                <div>
                  <h4 className="font-medium mb-2">Mevcut E≈üle≈ütirmeler</h4>
                  <div className="space-y-2 max-h-60 overflow-y-auto">
                    {mappingsLoading ? (
                      <div>Y√ºkleniyor...</div>
                    ) : mappings.length === 0 ? (
                      <div className="text-sm text-gray-500 italic">Hen√ºz e≈üle≈ütirme eklenmemi≈ü</div>
                    ) : (
                      mappings.map((mapping: any) => (
                        <div key={mapping.id} className="flex items-center justify-between p-2 border rounded">
                          <div className="flex items-center space-x-2">
                            <Badge variant="outline">{mapping.productCategory}</Badge>
                            {mapping.productSubcategory && (
                              <Badge variant="secondary">{mapping.productSubcategory}</Badge>
                            )}
                          </div>
                          <Button
                            variant="destructive"
                            size="sm"
                            onClick={() => deleteMappingMutation.mutate(mapping.id)}
                            disabled={deleteMappingMutation.isPending}
                          >
                            <X className="h-4 w-4" />
                          </Button>
                        </div>
                      ))
                    )}
                  </div>
                </div>

                {/* Yeni E≈üle≈ütirme Ekle */}
                <div className="border-t pt-4">
                  <h4 className="font-medium mb-3">Yeni E≈üle≈ütirme Ekle</h4>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <Label>Ana Kategori *</Label>
                      <Popover open={categoryDropdownOpen} onOpenChange={setCategoryDropdownOpen}>
                        <PopoverTrigger asChild>
                          <Button
                            variant="outline"
                            role="combobox"
                            aria-expanded={categoryDropdownOpen}
                            className="w-full justify-between"
                          >
                            {newMapping.productCategory 
                              ? productCategories.find((cat: any) => cat.category === newMapping.productCategory)?.category
                              : "Kategori se√ßin"}
                            <ChevronDown className="ml-2 h-4 w-4 shrink-0 opacity-50" />
                          </Button>
                        </PopoverTrigger>
                        <PopoverContent className="w-full p-0" align="start" style={{ zIndex: 10000 }}>
                          <Command>
                            <CommandInput placeholder="Kategori ara..." />
                            <CommandEmpty>Kategori bulunamadƒ±.</CommandEmpty>
                            <CommandGroup className="max-h-[200px] overflow-auto">
                              {categoriesLoading ? (
                                <CommandItem disabled>Y√ºkleniyor...</CommandItem>
                              ) : productCategories.length === 0 ? (
                                <CommandItem disabled>Kategori bulunamadƒ±</CommandItem>
                              ) : (
                                productCategories.map((cat: any) => (
                                  <CommandItem
                                    key={cat.category}
                                    value={cat.category}
                                    onSelect={(currentValue) => {
                                      setNewMapping({
                                        ...newMapping,
                                        productCategory: currentValue,
                                        productSubcategories: []
                                      });
                                      setCategoryDropdownOpen(false);
                                    }}
                                  >
                                    <Check
                                      className={`mr-2 h-4 w-4 ${
                                        newMapping.productCategory === cat.category ? "opacity-100" : "opacity-0"
                                      }`}
                                    />
                                    {cat.category} ({cat.subcategories?.length || 0} alt kategori)
                                  </CommandItem>
                                ))
                              )}
                            </CommandGroup>
                          </Command>
                        </PopoverContent>
                      </Popover>
                      <p className="text-xs text-gray-500 mt-1">
                        {productCategories.length} kategori mevcut
                      </p>
                    </div>

                    <div>
                      <Label>Alt Kategoriler (√áoklu Se√ßim)</Label>
                      <div className="border rounded-md p-3 max-h-40 overflow-y-auto space-y-2">
                        {!newMapping.productCategory ? (
                          <p className="text-sm text-gray-500 italic">√ñnce ana kategori se√ßin</p>
                        ) : (
                          <>
                            <div className="flex items-center space-x-2">
                              <Checkbox
                                id="all-subcategories"
                                checked={newMapping.productSubcategories.includes("ALL_SUBCATEGORIES")}
                                onCheckedChange={(checked) => {
                                  if (checked) {
                                    setNewMapping({
                                      ...newMapping,
                                      productSubcategories: ["ALL_SUBCATEGORIES"]
                                    });
                                  } else {
                                    setNewMapping({
                                      ...newMapping,
                                      productSubcategories: newMapping.productSubcategories.filter(s => s !== "ALL_SUBCATEGORIES")
                                    });
                                  }
                                }}
                              />
                              <label htmlFor="all-subcategories" className="text-sm font-medium">
                                T√ºm alt kategoriler
                              </label>
                            </div>
                            {productCategories
                              .find((cat: any) => cat.category === newMapping.productCategory)
                              ?.subcategories?.map((subcat: string) => (
                                <div key={subcat} className="flex items-center space-x-2">
                                  <Checkbox
                                    id={`subcat-${subcat}`}
                                    checked={newMapping.productSubcategories.includes(subcat)}
                                    disabled={newMapping.productSubcategories.includes("ALL_SUBCATEGORIES")}
                                    onCheckedChange={(checked) => {
                                      if (checked) {
                                        setNewMapping({
                                          ...newMapping,
                                          productSubcategories: [...newMapping.productSubcategories.filter(s => s !== "ALL_SUBCATEGORIES"), subcat]
                                        });
                                      } else {
                                        setNewMapping({
                                          ...newMapping,
                                          productSubcategories: newMapping.productSubcategories.filter(s => s !== subcat)
                                        });
                                      }
                                    }}
                                  />
                                  <label htmlFor={`subcat-${subcat}`} className="text-sm">
                                    {subcat}
                                  </label>
                                </div>
                              ))}
                          </>
                        )}
                      </div>
                    </div>

                    <div className="flex items-end">
                      <Button
                        onClick={handleAddMapping}
                        disabled={addMappingMutation.isPending || !newMapping.productCategory}
                        className="w-full"
                      >
                        <Plus className="h-4 w-4 mr-2" />
                        {newMapping.productSubcategories.length === 0 
                          ? "T√ºm Alt Kategoriler ƒ∞√ßin Ekle" 
                          : newMapping.productSubcategories.includes("ALL_SUBCATEGORIES")
                            ? "T√ºm Alt Kategoriler ƒ∞√ßin Ekle"
                            : `Se√ßili ${newMapping.productSubcategories.length} Alt Kategoriyi Ekle`}
                      </Button>
                    </div>
                  </div>
                </div>
              </div>
            </DialogContent>
          </Dialog>
        )}
      </CardContent>
    </Card>
  );
}

client/src/pages/blog-detail.tsx.content:
import { useEffect } from "react";
import { Link, useRoute, useLocation } from "wouter";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import Header from "@/components/header";
import Footer from "@/components/footer";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { 
  Calendar, 
  User, 
  Eye, 
  ArrowLeft, 
  Clock, 
  Share2,
  BookOpen,
  Tag,
  ChevronRight 
} from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface BlogPost {
  id: string;
  title: string;
  slug: string;
  excerpt?: string;
  content: string;
  featuredImage?: string;
  status: 'draft' | 'published';
  publishedAt?: string;
  createdAt: string;
  updatedAt: string;
  viewCount?: number;
  categoryId?: string;
  authorId: string;
  metaTitle?: string;
  metaDescription?: string;
  metaKeywords?: string;
  category?: {
    id: string;
    name: string;
    slug: string;
    description?: string;
  };
  author?: {
    name: string;
    email: string;
  };
  tags?: Array<{
    id: string;
    name: string;
    slug: string;
  }>;
}

export default function BlogDetail() {
  const [match, params] = useRoute("/blog/:slug");
  const slug = params?.slug;
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();


  // Blog yazƒ±sƒ±nƒ± getir
  const { data: blogPost, isLoading, error } = useQuery<BlogPost>({
    queryKey: ['/api/blog', slug],
    enabled: !!slug,
  });

  // ƒ∞lgili yazƒ±larƒ± getir
  const { data: relatedPosts = [] } = useQuery<BlogPost[]>({
    queryKey: ['/api/blog-posts', 'related', blogPost?.categoryId],
    enabled: !!blogPost?.categoryId,
  });

  // G√∂r√ºnt√ºlenme sayƒ±sƒ±nƒ± artƒ±r
  const incrementViewMutation = useMutation({
    mutationFn: (postId: string) => apiRequest(`/api/blog-posts/${postId}/view`, 'POST', {}),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/blog', slug] });
    },
  });

  // Sayfa y√ºklendiƒüinde g√∂r√ºnt√ºlenme sayƒ±sƒ±nƒ± artƒ±r
  useEffect(() => {
    if (blogPost?.id) {
      incrementViewMutation.mutate(blogPost.id);
    }
  }, [blogPost?.id]);

  // SEO meta tags g√ºncelle
  useEffect(() => {
    if (blogPost) {
      document.title = blogPost.metaTitle || `${blogPost.title} | Ar√ßelik Blog`;

      // Meta description
      const metaDescription = document.querySelector('meta[name="description"]');
      if (metaDescription) {
        metaDescription.setAttribute('content', blogPost.metaDescription || blogPost.excerpt || '');
      } else {
        const meta = document.createElement('meta');
        meta.name = 'description';
        meta.content = blogPost.metaDescription || blogPost.excerpt || '';
        document.head.appendChild(meta);
      }

      // Meta keywords
      if (blogPost.metaKeywords) {
        const metaKeywords = document.querySelector('meta[name="keywords"]');
        if (metaKeywords) {
          metaKeywords.setAttribute('content', blogPost.metaKeywords);
        } else {
          const meta = document.createElement('meta');
          meta.name = 'keywords';
          meta.content = blogPost.metaKeywords;
          document.head.appendChild(meta);
        }
      }

      // Open Graph tags
      const ogTitle = document.querySelector('meta[property="og:title"]');
      if (ogTitle) {
        ogTitle.setAttribute('content', blogPost.title);
      } else {
        const meta = document.createElement('meta');
        meta.setAttribute('property', 'og:title');
        meta.content = blogPost.title;
        document.head.appendChild(meta);
      }

      const ogDescription = document.querySelector('meta[property="og:description"]');
      if (ogDescription) {
        ogDescription.setAttribute('content', blogPost.excerpt || '');
      } else {
        const meta = document.createElement('meta');
        meta.setAttribute('property', 'og:description');
        meta.content = blogPost.excerpt || '';
        document.head.appendChild(meta);
      }

      if (blogPost.featuredImage) {
        const ogImage = document.querySelector('meta[property="og:image"]');
        if (ogImage) {
          ogImage.setAttribute('content', blogPost.featuredImage);
        } else {
          const meta = document.createElement('meta');
          meta.setAttribute('property', 'og:image');
          meta.content = blogPost.featuredImage;
          document.head.appendChild(meta);
        }
      }
    }

    // Cleanup function
    return () => {
      document.title = 'Ar√ßelik - Kaliteli Beyaz E≈üya ve Elektronik';
    };
  }, [blogPost]);

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('tr-TR', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const handleShare = async () => {
    const url = window.location.href;
    const title = blogPost?.title || '';

    if (navigator.share) {
      try {
        await navigator.share({
          title,
          url,
        });
      } catch (error) {
        // Kullanƒ±cƒ± payla≈üƒ±mƒ± iptal etmi≈ü olabilir
      }
    } else {
      // Fallback: URL'yi clipboard'a kopyala
      try {
        await navigator.clipboard.writeText(url);
        toast({
          title: "Baƒülantƒ± kopyalandƒ±",
          description: "Blog yazƒ±sƒ±nƒ±n baƒülantƒ±sƒ± panoya kopyalandƒ±",
        });
      } catch (error) {
        toast({
          title: "Hata",
          description: "Baƒülantƒ± kopyalanamadƒ±",
          variant: "destructive",
        });
      }
    }
  };

  const getReadingTime = (content: string) => {
    const wordsPerMinute = 200;
    const words = content.replace(/<[^>]*>/g, '').split(' ').length;
    const minutes = Math.ceil(words / wordsPerMinute);
    return `${minutes} dakika okuma`;
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-white">
        <Header />
        <div className="container mx-auto px-4 py-8">
          <div className="flex items-center justify-center py-16">
            <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
          </div>
        </div>
        <Footer />
      </div>
    );
  }

  if (error || !blogPost) {
    return (
      <div className="min-h-screen bg-white">
        <Header />
        <div className="container mx-auto px-4 py-8">
          <div className="text-center py-16">
            <BookOpen className="w-16 h-16 text-gray-300 mx-auto mb-4" />
            <h1 className="text-2xl font-bold text-gray-900 mb-2">
              Blog yazƒ±sƒ± bulunamadƒ±
            </h1>
            <p className="text-gray-600 mb-6">
              Aradƒ±ƒüƒ±nƒ±z blog yazƒ±sƒ± mevcut deƒüil veya kaldƒ±rƒ±lmƒ±≈ü olabilir.
            </p>
            <Button onClick={() => setLocation('/blog')} data-testid="back-to-blog-button">
              <ArrowLeft className="w-4 h-4 mr-2" />
              Blog Sayfasƒ±na D√∂n
            </Button>
          </div>
        </div>
        <Footer />
      </div>
    );
  }

  // ƒ∞lgili yazƒ±larƒ± filtrele (mevcut yazƒ±yƒ± hari√ß tut ve sadece yayƒ±nda olanlarƒ± al)
  const filteredRelatedPosts = relatedPosts
    .filter((post: BlogPost) => post.id !== blogPost.id && post.status === 'published')
    .slice(0, 3);

  return (
    <div className="min-h-screen bg-white">
      <Header />

      {/* Hero Section with Featured Image */}
      {blogPost.featuredImage && (
        <div className="relative h-[400px] overflow-hidden">
          <img
            src={blogPost.featuredImage}
            alt={blogPost.title}
            className="w-full h-full object-cover"
          />
          {/* Dark overlay for better text readability */}
          <div className="absolute inset-0 bg-black bg-opacity-40" />

          {/* Breadcrumb overlaid on hero image */}
          <nav className="absolute top-6 left-0 right-0 z-10" data-testid="breadcrumb">
            <div className="container mx-auto px-4">
              <div className="flex items-center space-x-2 text-sm text-white">
                <Link href="/blog" className="hover:text-gray-200 transition-colors">
                  Blog
                </Link>
                <ChevronRight className="w-4 h-4" />
                {blogPost.category && (
                  <>
                    <span>{blogPost.category.name}</span>
                    <ChevronRight className="w-4 h-4" />
                  </>
                )}
                <span className="text-gray-200 truncate">{blogPost.title}</span>
              </div>
            </div>
          </nav>
        </div>
      )}

      <main className="container mx-auto px-4 py-8">
        {/* Back Button */}
        <Button 
          variant="ghost" 
          className="mb-6" 
          onClick={() => setLocation('/blog')}
          data-testid="back-to-blog"
        >
          <ArrowLeft className="w-4 h-4 mr-2" />
          Blog Sayfasƒ±na D√∂n
        </Button>

        <article className="max-w-4xl mx-auto">
          {/* Article Header */}
          <header className="mb-12">
            {/* Category Badge */}
            {blogPost.category && (
              <div className="mb-6">
                <Badge className="bg-red-500 text-white border-0 text-sm font-medium px-4 py-2">
                  {blogPost.category.name}
                </Badge>
              </div>
            )}

            {/* Title */}
            <h1 className="text-4xl lg:text-5xl font-bold text-gray-900 mb-6 leading-tight" data-testid="blog-title">
              {blogPost.title}
            </h1>

            {/* Excerpt */}
            {blogPost.excerpt && (
              <p className="text-xl text-gray-600 mb-8 leading-relaxed max-w-3xl" data-testid="blog-excerpt">
                {blogPost.excerpt}
              </p>
            )}

            {/* Article Meta */}
            <div className="flex flex-wrap items-center justify-between border-y border-gray-200 py-4 mb-8">
              <div className="flex flex-wrap items-center gap-6 text-sm text-gray-500">
                <div className="flex items-center gap-2">
                  <Calendar className="w-4 h-4" />
                  <span>{formatDate(blogPost.publishedAt || blogPost.createdAt)}</span>
                </div>

                <div className="flex items-center gap-2">
                  <Clock className="w-4 h-4" />
                  <span>{getReadingTime(blogPost.content)}</span>
                </div>

                {blogPost.viewCount && (
                  <div className="flex items-center gap-2">
                    <Eye className="w-4 h-4" />
                    <span data-testid="view-count">{blogPost.viewCount} g√∂r√ºnt√ºlenme</span>
                  </div>
                )}
              </div>

              <Button 
                variant="ghost" 
                size="sm" 
                onClick={handleShare}
                className="text-gray-500 hover:text-primary flex items-center gap-2"
                data-testid="share-button"
              >
                <Share2 className="w-4 h-4" />
                Payla≈ü
              </Button>
            </div>
          </header>

          {/* Article Content - Enhanced Typography */}
          <div 
            className="prose prose-lg prose-gray max-w-none mb-12 
                       prose-headings:font-bold prose-headings:text-gray-900 
                       prose-h2:text-2xl prose-h2:mt-12 prose-h2:mb-6
                       prose-h3:text-xl prose-h3:mt-8 prose-h3:mb-4
                       prose-p:text-gray-700 prose-p:leading-relaxed prose-p:mb-6
                       prose-ul:my-6 prose-li:my-2 prose-li:text-gray-700
                       prose-strong:text-gray-900 prose-strong:font-semibold
                       prose-img:rounded-lg prose-img:shadow-lg prose-img:my-8"
            dangerouslySetInnerHTML={{ __html: blogPost.content }}
            data-testid="blog-content"
          />

          {/* Article Tags */}
          {blogPost.tags && blogPost.tags.length > 0 && (
            <div className="bg-gray-50 rounded-lg p-6 mb-12">
              <div className="flex items-center gap-2 mb-4">
                <Tag className="w-5 h-5 text-gray-600" />
                <span className="text-lg font-semibold text-gray-900">Etiketler</span>
              </div>
              <div className="flex flex-wrap gap-3">
                {blogPost.tags.map((tag) => (
                  <Badge 
                    key={tag.id} 
                    variant="outline" 
                    className="text-sm px-3 py-1 border-gray-300 hover:bg-primary hover:text-white transition-colors cursor-pointer" 
                    data-testid={`tag-${tag.slug}`}
                  >
                    {tag.name}
                  </Badge>
                ))}
              </div>
            </div>
          )}

          {/* Author Info (if available) */}
          {blogPost.author && (
            <div className="bg-gradient-to-r from-gray-50 to-gray-100 rounded-xl p-8 mb-12 border border-gray-200">
              <div className="flex items-center gap-6">
                <div className="w-16 h-16 bg-red-500 rounded-full flex items-center justify-center shadow-lg">
                  <User className="w-8 h-8 text-white" />
                </div>
                <div>
                  <h3 className="text-xl font-bold text-gray-900 mb-1" data-testid="author-name">
                    {blogPost.author.name}
                  </h3>
                  <p className="text-gray-600 font-medium">Ar√ßelik ƒ∞√ßerik Edit√∂r√º</p>
                  <p className="text-sm text-gray-500 mt-2">
                    Ev ya≈üamƒ±nƒ± kolayla≈ütƒ±ran teknolojiler hakkƒ±nda uzman i√ßerikler hazƒ±rlƒ±yor.
                  </p>
                </div>
              </div>
            </div>
          )}

          {/* Related Posts */}
          {filteredRelatedPosts.length > 0 && (
            <section className="border-t border-gray-200 pt-12">
              <h2 className="text-3xl font-bold text-gray-900 mb-8 text-center">
                ƒ∞lgili Blog Yazƒ±larƒ±
              </h2>

              <div className="grid gap-8 md:grid-cols-2 lg:grid-cols-3">
                {filteredRelatedPosts.map((post: BlogPost) => (
                  <Card 
                    key={post.id}
                    className="group hover:shadow-xl transition-all duration-300 cursor-pointer border-0 shadow-md overflow-hidden"
                    onClick={() => setLocation(`/blog/${post.slug}`)}
                    data-testid={`related-post-${post.slug}`}
                  >
                    {post.featuredImage && (
                      <div className="relative overflow-hidden h-48">
                        <img
                          src={post.featuredImage}
                          alt={post.title}
                          className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-500"
                        />
                        <div className="absolute inset-0 bg-gradient-to-t from-black/50 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
                      </div>
                    )}

                    <CardHeader className="pb-3">
                      {post.category && (
                        <Badge className="bg-red-500 text-white border-0 text-xs font-medium px-2 py-1 w-fit mb-2">
                          {post.category.name}
                        </Badge>
                      )}

                      <CardTitle className="text-lg font-bold group-hover:text-red-500 transition-colors duration-300 line-clamp-2 leading-tight">
                        {post.title}
                      </CardTitle>

                      <div className="flex items-center gap-2 text-xs text-gray-500 mt-2">
                        <Calendar className="w-3 h-3" />
                        <span>{formatDate(post.publishedAt || post.createdAt)}</span>
                      </div>
                    </CardHeader>

                    {post.excerpt && (
                      <CardContent>
                        <p className="text-sm text-gray-600 line-clamp-3 leading-relaxed">
                          {post.excerpt}
                        </p>
                      </CardContent>
                    )}
                  </Card>
                ))}
              </div>

              {/* See All Posts Button */}
              <div className="text-center mt-10">
                <Button 
                  onClick={() => setLocation('/blog')}
                  className="bg-red-500 hover:bg-red-600 text-white px-8 py-3 rounded-full font-semibold"
                >
                  T√ºm Blog Yazƒ±larƒ±nƒ± G√∂r
                </Button>
              </div>
            </section>
          )}
        </article>
      </main>

      <Footer />
    </div>
  );
}

