}

// Blog Slider Form Component
function BlogSliderForm({ 
  initialData, 
  onSubmit, 
  isLoading, 
  blogPosts 
}: {
  initialData?: BlogSlider;
  onSubmit: (data: Omit<BlogSlider, 'id'>) => void;
  isLoading: boolean;
  blogPosts: any[];
}) {
  const [formData, setFormData] = useState({
    blogPostId: initialData?.blogPostId || 'none',
    title: initialData?.title || '',
    subtitle: initialData?.subtitle || '',
    description: initialData?.description || '',
    image: initialData?.image || '',
    buttonText: initialData?.buttonText || 'Devamƒ±',
    buttonLink: initialData?.buttonLink || '',
    tagText: initialData?.tagText || '√úr√ºn ƒ∞nceleme',
    tagColor: initialData?.tagColor || 'red',
    overlayOpacity: initialData?.overlayOpacity || 40,
    isActive: initialData?.isActive ?? true,
    order: initialData?.order || 0,
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!formData.title || !formData.image) {
      return;
    }

    // Convert "none" back to empty string for blogPostId
    const submitData = {
      ...formData,
      blogPostId: formData.blogPostId === 'none' ? '' : formData.blogPostId
    };

    onSubmit(submitData);
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <Tabs defaultValue="basic" className="w-full">
        <TabsList>
          <TabsTrigger value="basic">Temel Bilgiler</TabsTrigger>
          <TabsTrigger value="design">Tasarƒ±m</TabsTrigger>
          <TabsTrigger value="settings">Ayarlar</TabsTrigger>
        </TabsList>

        <TabsContent value="basic" className="space-y-4">
          <div className="grid grid-cols-1 gap-4">
            <div>
              <Label htmlFor="title">Ba≈ülƒ±k *</Label>
              <Input
                id="title"
                value={formData.title}
                onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                placeholder="Slider ba≈ülƒ±ƒüƒ±"
                data-testid="input-title"
                required
              />
            </div>

            <div>
              <Label htmlFor="subtitle">Alt Ba≈ülƒ±k</Label>
              <Input
                id="subtitle"
                value={formData.subtitle}
                onChange={(e) => setFormData({ ...formData, subtitle: e.target.value })}
                placeholder="Slider alt ba≈ülƒ±ƒüƒ±"
                data-testid="input-subtitle"
              />
            </div>

            <div>
              <Label htmlFor="description">A√ßƒ±klama</Label>
              <Textarea
                id="description"
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                placeholder="Slider a√ßƒ±klamasƒ±"
                data-testid="input-description"
              />
            </div>
          </div>
        </TabsContent>

        <TabsContent value="design" className="space-y-4">
          <div className="space-y-4">
            <div>
              <Label>Slider G√∂rseli *</Label>
              <UniversalImageUploader
                label="Blog Slider G√∂rseli"
                currentImage={formData.image}
                onImageUpdate={(url) => setFormData({ ...formData, image: url })}
                uploadEndpoint="/api/admin/slider-upload-image"
                imageSpecs={IMAGE_SPECS.BLOG_SLIDER}
                showUrlInput={true}
              />
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label htmlFor="tagText">Etiket Metni</Label>
                <Input
                  id="tagText"
                  value={formData.tagText}
                  onChange={(e) => setFormData({ ...formData, tagText: e.target.value })}
                  placeholder="√úr√ºn ƒ∞nceleme"
                  data-testid="input-tag-text"
                />
              </div>

              <div>
                <Label htmlFor="tagColor">Etiket Rengi</Label>
                <Select 
                  value={formData.tagColor} 
                  onValueChange={(value) => setFormData({ ...formData, tagColor: value })}
                >
                  <SelectTrigger data-testid="select-tag-color">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent className="z-[100] bg-white shadow-lg">
                    {tagColors.map((color) => (
                      <SelectItem key={color.value} value={color.value}>
                        <div className="flex items-center gap-2">
                          <div className={`w-3 h-3 rounded-full ${color.color}`} />
                          {color.label}
                        </div>
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            </div>

            <div>
              <Label htmlFor="overlayOpacity">Karartƒ± Yoƒüunluƒüu (%)</Label>
              <Input
                id="overlayOpacity"
                type="number"
                min="0"
                max="100"
                value={formData.overlayOpacity}
                onChange={(e) => setFormData({ ...formData, overlayOpacity: Number(e.target.value) })}
                data-testid="input-overlay-opacity"
              />
            </div>
          </div>
        </TabsContent>

        <TabsContent value="settings" className="space-y-4">
          <div className="grid grid-cols-1 gap-4">
            <div>
              <Label htmlFor="buttonText">Buton Metni</Label>
              <Input
                id="buttonText"
                value={formData.buttonText}
                onChange={(e) => setFormData({ ...formData, buttonText: e.target.value })}
                placeholder="Devamƒ±"
                data-testid="input-button-text"
              />
            </div>

            <div>
              <Label htmlFor="buttonLink">Buton Linki</Label>
              <Input
                id="buttonLink"
                value={formData.buttonLink}
                onChange={(e) => setFormData({ ...formData, buttonLink: e.target.value })}
                placeholder="/blog/makale-slug"
                data-testid="input-button-link"
              />
            </div>

            <div>
              <Label htmlFor="blogPost">ƒ∞lgili Blog Yazƒ±sƒ± (Opsiyonel)</Label>
              <Select 
                value={formData.blogPostId} 
                onValueChange={(value) => setFormData({ ...formData, blogPostId: value })}
              >
                <SelectTrigger data-testid="select-blog-post">
                  <SelectValue placeholder="Blog yazƒ±sƒ± se√ß" />
                </SelectTrigger>
                <SelectContent className="z-[100] bg-white shadow-lg">
                  <SelectItem value="none">Se√ßilmedi</SelectItem>
                  {blogPosts.map((post) => (
                    <SelectItem key={post.id} value={post.id}>
                      {post.title}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label htmlFor="order">Sƒ±ralama</Label>
                <Input
                  id="order"
                  type="number"
                  min="0"
                  value={formData.order}
                  onChange={(e) => setFormData({ ...formData, order: Number(e.target.value) })}
                  data-testid="input-order"
                />
              </div>

              <div className="flex items-center space-x-2 pt-6">
                <input
                  type="checkbox"
                  id="isActive"
                  checked={formData.isActive}
                  onChange={(e) => setFormData({ ...formData, isActive: e.target.checked })}
                  data-testid="input-is-active"
                />
                <Label htmlFor="isActive">Aktif</Label>
              </div>
            </div>
          </div>
        </TabsContent>
      </Tabs>

      <div className="flex justify-end gap-3">
        <Button type="submit" disabled={isLoading || !formData.title || !formData.image}>
          {isLoading ? (
            <div className="animate-spin w-4 h-4 border-2 border-white border-t-transparent rounded-full mr-2" />
          ) : (
            <Save className="w-4 h-4 mr-2" />
          )}
          {initialData ? 'G√ºncelle' : 'Kaydet'}
        </Button>
      </div>
    </form>
  );
}

