import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import {
  ArrowLeft,
  Package,
  Clock,
  CheckCircle,
  XCircle,
  ArrowRight,
} from "lucide-react";
import { Link } from "wouter";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

interface OrderItem {
  id: string;
  quantity: number;
  price: number;
  warranty: string;
}

interface Product {
  id: string;
  name: string;
  image: string;
  price: number;
}

interface Order {
  id: string;
  orderNumber: string;
  totalAmount: number;
  createdAt: string;
  paymentMethod: string;
  installments: number;
}

interface ReturnRequest {
  id: string;
  returnType: string;
  returnReason: string;
  status: string;
  adminNotes?: string;
  requestDate: string;
  responseDate?: string;
  order: Order;
  orderItem: OrderItem;
  product: Product;
}

export default function Returns() {
  const [selectedOrderItemId, setSelectedOrderItemId] = useState<string>("");
  const [returnType, setReturnType] = useState<string>("return");
  const [returnReason, setReturnReason] = useState<string>("");
  const [showReturnDialog, setShowReturnDialog] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Kullanƒ±cƒ±nƒ±n sipari≈ülerini getir
  const { data: orders = [], isLoading: ordersLoading } = useQuery({
    queryKey: ["/api/orders"],
  });

  // Kullanƒ±cƒ±nƒ±n iade taleplerini getir
  const { data: returns = [], isLoading: returnsLoading } = useQuery({
    queryKey: ["/api/returns"],
  });

  // ƒ∞ade edilmi≈ü √ºr√ºn listesi
  const { data: returnedOrderItems = [], isLoading: returnedItemsLoading } = useQuery({
    queryKey: ["/api/returns/order-items"],
  });

  // Yeni iade talebi olu≈ütur
  const createReturnMutation = useMutation({
    mutationFn: async (returnData: any) => {
      const response = await fetch("/api/returns", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(returnData),
      });
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Failed to create return");
      }
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "ƒ∞ade Talebi Olu≈üturuldu",
        description:
          "ƒ∞ade talebiniz ba≈üarƒ±yla olu≈üturuldu. En kƒ±sa s√ºrede incelenecek.",
      });
      setShowReturnDialog(false);
      setReturnReason("");
      setSelectedOrderItemId("");
      queryClient.invalidateQueries({ queryKey: ["/api/returns"] });
      queryClient.invalidateQueries({ queryKey: ["/api/returns/order-items"] });
    },
    onError: (error: any) => {
      toast({
        title: "Hata",
        description: error.message || "ƒ∞ade talebi olu≈üturulurken bir hata olu≈ütu.",
        variant: "destructive",
      });
    },
  });

  const handleCreateReturn = () => {
    if (!selectedOrderItemId || !returnReason.trim()) {
      toast({
        title: "Eksik Bilgi",
        description: "L√ºtfen t√ºm alanlarƒ± doldurun.",
        variant: "destructive",
      });
      return;
    }

    const selectedOrder = (orders as any[]).find((order: any) =>
      order.items.some((item: any) => item.id === selectedOrderItemId),
    );

    if (!selectedOrder) {
      toast({
        title: "Hata",
        description: "Se√ßilen √ºr√ºn bulunamadƒ±.",
        variant: "destructive",
      });
      return;
    }

    const returnData = {
      orderId: selectedOrder.id,
      orderItemId: selectedOrderItemId,
      returnType,
      returnReason,
    };

    createReturnMutation.mutate(returnData);
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case "pending":
        return (
          <Badge variant="secondary" className="bg-yellow-100 text-yellow-800">
            <Clock className="w-3 h-3 mr-1" />
            Beklemede
          </Badge>
        );
      case "approved":
        return (
          <Badge variant="secondary" className="bg-green-100 text-green-800">
            <CheckCircle className="w-3 h-3 mr-1" />
            Onaylandƒ±
          </Badge>
        );
      case "rejected":
        return (
          <Badge variant="secondary" className="bg-red-100 text-red-800">
