import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { 
  Save, 
  Plus, 
  Trash2, 
  Edit3,
  CreditCard,
  Banknote,
  Building2,
  Copy,
  Check
} from "lucide-react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import type { GeneralSettings, BankAccount } from "@shared/schema";

export default function PaymentMethodsManagement() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [editingAccount, setEditingAccount] = useState<BankAccount | null>(null);
  const [isAddingNew, setIsAddingNew] = useState(false);
  const [copiedIban, setCopiedIban] = useState<string | null>(null);

  const [newAccount, setNewAccount] = useState({
    bankName: "",
    accountHolder: "",
    iban: "",
    branchCode: "",
    branchName: "",
    accountNumber: "",
    swiftCode: "",
    description: "",
    isActive: true,
    isDefault: false
  });

  // Local form state for general settings
  const [formSettings, setFormSettings] = useState({
    bankName: "",
    accountHolder: "",
    iban: "",
    bankTransferInstructions: ""
  });
  const [isFormInitialized, setIsFormInitialized] = useState(false);

  // Fetch general settings to get current bank transfer settings
  const { data: settings, isLoading: settingsLoading } = useQuery<GeneralSettings>({
    queryKey: ["/api/admin/settings"],
  });

  // Fetch bank accounts
  const { data: bankAccounts = [], isLoading: accountsLoading } = useQuery<BankAccount[]>({
    queryKey: ["/api/admin/bank-accounts"],
  });

  // Update form settings when server data loads (only on initial load)
  useEffect(() => {
    if (settings && !isFormInitialized) {
      setFormSettings({
        bankName: settings.bankName || "",
        accountHolder: settings.accountHolder || "",
        iban: settings.iban || "",
        bankTransferInstructions: settings.bankTransferInstructions || ""
      });
      setIsFormInitialized(true);
    }
  }, [settings, isFormInitialized]);

  // Update general settings mutation
  const updateSettingsMutation = useMutation({
    mutationFn: async (data: any) => {
      await apiRequest("/api/admin/settings", "PUT", data);
    },
    onSuccess: () => {
      toast({
        title: "Ba≈üarƒ±lƒ±!",
        description: "Havale/EFT ayarlarƒ± g√ºncellendi.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/settings"] });
    },
    onError: (error) => {
      toast({
        title: "Hata!",
        description: "Ayarlar g√ºncellenirken bir hata olu≈ütu.",
        variant: "destructive",
      });
    },
  });

  // Create bank account mutation
  const createAccountMutation = useMutation({
    mutationFn: async (data: any) => {
      await apiRequest("/api/admin/bank-accounts", "POST", data);
    },
    onSuccess: () => {
      toast({
        title: "Ba≈üarƒ±lƒ±!",
        description: "Banka hesabƒ± eklendi.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/bank-accounts"] });
      setIsAddingNew(false);
      setNewAccount({
        bankName: "",
        accountHolder: "",
        iban: "",
        branchCode: "",
        branchName: "",
        accountNumber: "",
        swiftCode: "",
        description: "",
        isActive: true,
        isDefault: false
      });
    },
    onError: (error) => {
      toast({
        title: "Hata!",
        description: "Banka hesabƒ± eklenirken bir hata olu≈ütu.",
        variant: "destructive",
      });
    },
  });

  // Update bank account mutation
  const updateAccountMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: any }) => {
      await apiRequest(`/api/admin/bank-accounts/${id}`, "PUT", data);
    },
    onSuccess: () => {
      toast({
        title: "Ba≈üarƒ±lƒ±!",
        description: "Banka hesabƒ± g√ºncellendi.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/bank-accounts"] });
      setEditingAccount(null);
    },
    onError: (error) => {
      toast({
        title: "Hata!",
        description: "Banka hesabƒ± g√ºncellenirken bir hata olu≈ütu.",
        variant: "destructive",
      });
    },
  });

  // Delete bank account mutation
  const deleteAccountMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest(`/api/admin/bank-accounts/${id}`, "DELETE");
    },
    onSuccess: () => {
      toast({
        title: "Ba≈üarƒ±lƒ±!",
        description: "Banka hesabƒ± silindi.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/bank-accounts"] });
    },
    onError: (error) => {
      toast({
        title: "Hata!",
        description: "Banka hesabƒ± silinirken bir hata olu≈ütu.",
        variant: "destructive",
      });
    },
  });

  const handleUpdateSettings = (e: React.FormEvent) => {
    e.preventDefault();
    if (settings) {
      const updatedSettings = {
        ...settings,
        ...formSettings
      };
      updateSettingsMutation.mutate(updatedSettings);
    }
  };

  const handleCreateAccount = (e: React.FormEvent) => {
    e.preventDefault();
    createAccountMutation.mutate(newAccount);
  };

  const handleUpdateAccount = (e: React.FormEvent) => {
    e.preventDefault();
    if (editingAccount) {
      updateAccountMutation.mutate({
        id: editingAccount.id,
        data: editingAccount
      });
    }
  };

  const copyIban = (iban: string) => {
    navigator.clipboard.writeText(iban);
    setCopiedIban(iban);
    toast({
      title: "Kopyalandƒ±!",
      description: "IBAN numarasƒ± panoya kopyalandƒ±.",
    });

    setTimeout(() => {
      setCopiedIban(null);
    }, 2000);
  };

  const formatIban = (iban: string) => {
    return iban.replace(/(.{4})/g, '$1 ').trim();
  };

  if (settingsLoading || accountsLoading) {
    return <div className="animate-pulse">Y√ºkleniyor...</div>;
  }

  return (
    <div className="space-y-6">
      <div>
        <h2 className="text-2xl font-bold text-gray-900">√ñdeme Y√∂ntemleri Y√∂netimi</h2>
        <p className="text-gray-600 mt-2">Havale/EFT ve diƒüer √∂deme y√∂ntemlerini y√∂netin</p>
      </div>

      {/* Current Settings Card */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Banknote className="w-5 h-5" />
            Havale/EFT Genel Ayarlarƒ±
          </CardTitle>
          <CardDescription>
            Havale/EFT √∂deme y√∂ntemi i√ßin genel ayarlarƒ± d√ºzenleyin
          </CardDescription>
        </CardHeader>
        <CardContent>
          {settings && (
            <form onSubmit={handleUpdateSettings} className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label>Varsayƒ±lan Banka Adƒ±</Label>
                  <Input
                    value={formSettings.bankName}
                    onChange={(e) => {
                      setFormSettings({
                        ...formSettings,
                        bankName: e.target.value
                      });
                    }}
                    placeholder="Garanti BBVA"
                  />
                </div>

                <div className="space-y-2">
                  <Label>Hesap Sahibi</Label>
                  <Input
                    value={formSettings.accountHolder}
                    onChange={(e) => {
                      setFormSettings({
                        ...formSettings,
                        accountHolder: e.target.value
                      });
                    }}
                    placeholder="AR√áELIK A.≈û."
                  />
                </div>

                <div className="space-y-2 md:col-span-2">
                  <Label>IBAN</Label>
                  <div className="flex gap-2">
                    <Input
                      value={formSettings.iban}
                      onChange={(e) => {
                        setFormSettings({
                          ...formSettings,
                          iban: e.target.value
                        });
                      }}
                      placeholder="TR00 0000 0000 0000 0000 0000 00"
                      className="flex-1"
                    />
                    {formSettings.iban && (
                      <Button
                        type="button"
                        variant="outline"
                        onClick={() => copyIban(formSettings.iban)}
                        className="px-3"
                      >
                        {copiedIban === formSettings.iban ? (
                          <Check className="w-4 h-4 text-green-600" />
                        ) : (
                          <Copy className="w-4 h-4" />
                        )}
                      </Button>
                    )}
                  </div>
                </div>

                <div className="space-y-2 md:col-span-2">
                  <Label>√ñdeme Talimatlarƒ±</Label>
                  <Textarea
                    value={formSettings.bankTransferInstructions}
                    onChange={(e) => {
                      setFormSettings({
                        ...formSettings,
                        bankTransferInstructions: e.target.value
                      });
                    }}
                    placeholder="Havale/EFT yaparken a√ßƒ±klama kƒ±smƒ±na sipari≈ü numaranƒ±zƒ± yazƒ±nƒ±z..."
                    rows={3}
                  />
                </div>
              </div>

              <Button 
                type="submit" 
                disabled={updateSettingsMutation.isPending}
                className="w-full"
              >
                {updateSettingsMutation.isPending ? "Kaydediliyor..." : "Ayarlarƒ± Kaydet"}
              </Button>
            </form>
          )}
        </CardContent>
      </Card>

      {/* Bank Accounts Management */}
      <Card>
        <CardHeader>
          <div className="flex justify-between items-center">
            <div>
              <CardTitle className="flex items-center gap-2">
                <Building2 className="w-5 h-5" />
                Banka Hesaplarƒ±
