import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import Header from "@/components/header";
import Footer from "@/components/footer";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { CreditCard, Building2, Info } from "lucide-react";
import BankLogo from "@/components/bank-logo";

export default function CommissionRatesPage() {
  const [selectedBankId, setSelectedBankId] = useState<string>('all');

  // Komisyon oranlarƒ±nƒ± getir
  const { data: commissionRates = [], isLoading } = useQuery<any[]>({
    queryKey: ['/api/commission-rates'],
  });

  // Unique bankalarƒ± bul
  const uniqueBanks = commissionRates.reduce((acc: any[], rate: any) => {
    if (!acc.find(bank => bank.id === rate.virtualPosConfigId)) {
      acc.push({
        id: rate.virtualPosConfigId,
        name: rate.bankName
      });
    }
    return acc;
  }, []);

  // Filtrelenmi≈ü komisyon oranlarƒ±
  const filteredRates = selectedBankId === 'all' 
    ? commissionRates 
    : commissionRates.filter((rate: any) => rate.virtualPosConfigId === selectedBankId);

  // Bankalarƒ± grupla
  const groupedByBank = filteredRates.reduce((acc: any, rate: any) => {
    if (!acc[rate.bankName]) {
      acc[rate.bankName] = [];
    }
    acc[rate.bankName].push(rate);
    return acc;
  }, {});

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <main className="container mx-auto px-4 py-8">
        {/* Hero Section */}
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-4">
            Komisyon Oranlarƒ±
          </h1>
          <p className="text-lg text-gray-600 mb-6">
            T√ºm bankalar i√ßin g√ºncel taksit komisyon oranlarƒ±mƒ±zƒ± g√∂r√ºnt√ºleyin
          </p>

          {/* Bilgilendirme */}
          <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-8 max-w-4xl mx-auto">
            <div className="flex items-start gap-3">
              <Info className="w-5 h-5 text-blue-600 mt-0.5 flex-shrink-0" />
              <div className="text-left">
                <h3 className="font-semibold text-blue-900 mb-2">Komisyon Oranlarƒ± Hakkƒ±nda</h3>
                <p className="text-blue-800 text-sm leading-relaxed">
                  A≈üaƒüƒ±daki komisyon oranlarƒ± taksitli alƒ±≈üveri≈ülerinizde uygulanmaktadƒ±r. 
                  Tek √ßekim alƒ±≈üveri≈ülerde komisyon √ºcreti alƒ±nmamaktadƒ±r. 
                  Komisyon oranlarƒ± banka ve taksit sayƒ±sƒ±na g√∂re deƒüi≈üiklik g√∂sterebilir.
                </p>
              </div>
            </div>
          </div>
        </div>

        {/* Filtreler */}
        <div className="flex justify-center mb-8">
          <div className="flex items-center gap-4 bg-white rounded-lg p-4 shadow-sm">
            <label className="text-sm font-medium text-gray-700">Banka Filtresi:</label>
            <select 
              className="px-4 py-2 border border-gray-300 rounded-md text-sm focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              value={selectedBankId}
              onChange={(e) => setSelectedBankId(e.target.value)}
            >
              <option value="all">T√ºm Bankalar ({commissionRates.length} taksit se√ßeneƒüi)</option>
              {uniqueBanks.map((bank: any) => {
                const bankRatesCount = commissionRates.filter((r: any) => r.virtualPosConfigId === bank.id).length;
                return (
                  <option key={bank.id} value={bank.id}>
                    {bank.name} ({bankRatesCount} taksit se√ßeneƒüi)
                  </option>
                );
              })}
            </select>
          </div>
        </div>

        {/* Loading State */}
        {isLoading && (
          <div className="flex items-center justify-center py-12">
            <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
          </div>
        )}

        {/* Komisyon Oranlarƒ± */}
        {!isLoading && (
          <div className="space-y-8">
            {Object.entries(groupedByBank).map(([bankName, rates]: [string, any]) => (
              <Card key={bankName} className="overflow-hidden">
                <CardHeader className="bg-gradient-to-r from-gray-50 to-gray-100">
                  <CardTitle className="text-xl flex items-center gap-3">
                    <BankLogo bankName={bankName} className="w-20 h-16" showName={false} />
                    <Badge variant="outline" className="ml-auto">
                      {rates.length} taksit se√ßeneƒüi
                    </Badge>
                  </CardTitle>
                </CardHeader>
                <CardContent className="p-0">
                  <div className="overflow-x-auto">
                    <table className="w-full">
                      <thead className="bg-gray-50 border-b">
                        <tr>
                          <th className="text-left py-3 px-6 font-semibold text-gray-700">Kart T√ºr√º</th>
                          <th className="text-left py-3 px-6 font-semibold text-gray-700">Taksit Sayƒ±sƒ±</th>
                          <th className="text-left py-3 px-6 font-semibold text-gray-700">Komisyon Oranƒ±</th>
                          <th className="text-left py-3 px-6 font-semibold text-gray-700">Minimum Tutar</th>
                        </tr>
                      </thead>
                      <tbody>
                        {rates
                          .sort((a: any, b: any) => a.installmentCount - b.installmentCount)
                          .map((rate: any) => (
                          <tr key={rate.id} className="border-b hover:bg-gray-50 transition-colors">
                            <td className="py-4 px-6">
                              <div className="flex items-center gap-2">
                                <CreditCard className="w-4 h-4 text-gray-500" />
                                <span className="font-medium">
                                  {rate.cardType === 'all' ? 'T√ºm Kartlar' : rate.cardType.toUpperCase()}
                                </span>
                              </div>
                            </td>
                            <td className="py-4 px-6">
                              <Badge variant={rate.installmentCount === 1 ? "secondary" : "default"}>
                                {rate.installmentCount === 1 ? 'Tek √áekim' : `${rate.installmentCount} Ay`}
                              </Badge>
                            </td>
                            <td className="py-4 px-6">
                              <div className="text-lg font-bold text-blue-600">
                                %{parseFloat(rate.commissionRate).toFixed(2)}
                              </div>
                            </td>
                            <td className="py-4 px-6">
                              <span className="text-gray-600">
                                {parseFloat(rate.minAmount) > 0 ? `${parseFloat(rate.minAmount).toFixed(2)} ‚Ç∫` : 'Limit yok'}
                              </span>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}

        {/* Bo≈ü State */}
        {!isLoading && commissionRates.length === 0 && (
          <div className="text-center py-12">
            <CreditCard className="w-16 h-16 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-semibold text-gray-900 mb-2">Hen√ºz Komisyon Oranƒ± Tanƒ±mlanmamƒ±≈ü</h3>
