import getApp from '../dist/vercel-app.js';

let cachedApp = null;

export default async function handler(req, res) {
  if (!cachedApp) {
    cachedApp = await getApp();
  }

  return cachedApp(req, res);
}

server/vercel-app.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";

const app = express();

// Trust proxy for rate limiting in cloud environments (must be set early)
app.set('trust proxy', 1);

app.use(express.json({ limit: '50mb' }));
app.use(express.urlencoded({ extended: false, limit: '50mb' }));

let appInitialized = false;
let initializedApp: express.Application | null = null;

async function getApp() {
  if (!appInitialized) {
    await registerRoutes(app);

    app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
      const status = err.status || err.statusCode || 500;
      const message = err.message || "Internal Server Error";
      res.status(status).json({ message });
    });

    initializedApp = app;
    appInitialized = true;
  }

  return initializedApp;
}

export default getApp;

