import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { CreditCard, Info } from "lucide-react";
import BankLogo from "@/components/bank-logo";

interface CommissionRate {
  id: string;
  virtualPosConfigId: string;
  bankName: string;
  cardType: string;
  installmentCount: number;
  commissionRate: string;
  minAmount: string;
}

interface InstallmentOptionsProps {
  productPrice: number;
  className?: string;
}

export default function InstallmentOptions({ productPrice, className = "" }: InstallmentOptionsProps) {
  // Komisyon oranlarƒ±nƒ± getir
  const { data: commissionRates = [], isLoading, error } = useQuery<CommissionRate[]>({
    queryKey: ['/api/commission-rates'],
  });

  if (isLoading) {
    return (
      <div className={`space-y-4 ${className}`}>
        <h3 className="text-lg font-semibold text-gray-900 mb-4">Taksit Se√ßenekleri</h3>
        <div className="animate-pulse space-y-3">
          {[...Array(3)].map((_, i) => (
            <div key={i} className="bg-gray-200 h-32 rounded-lg"></div>
          ))}
        </div>
      </div>
    );
  }

  if (error || !commissionRates.length) {
    return (
      <div className={`${className}`}>
        <h3 className="text-lg font-semibold text-gray-900 mb-4">Taksit Se√ßenekleri</h3>
        <div className="bg-gray-50 border rounded-lg p-6 text-center">
          <CreditCard className="w-12 h-12 text-gray-400 mx-auto mb-3" />
          <p className="text-gray-600">Taksit se√ßenekleri ≈üu anda mevcut deƒüil.</p>
        </div>
      </div>
    );
  }

  // √úr√ºn fiyatƒ±na uygun komisyon oranlarƒ±nƒ± filtrele
  const eligibleRates = commissionRates.filter((rate: CommissionRate) => {
    const minAmount = parseFloat(rate.minAmount);
    return isNaN(minAmount) || minAmount <= 0 || productPrice >= minAmount;
  });

  // Bankalarƒ± grupla
  const groupedByBank = eligibleRates.reduce((acc: any, rate: CommissionRate) => {
    if (!acc[rate.bankName]) {
      acc[rate.bankName] = [];
    }
    acc[rate.bankName].push(rate);
    return acc;
  }, {});

  // Taksit hesaplama fonksiyonu
  const calculateInstallment = (rate: CommissionRate) => {
    const installmentCount = rate.installmentCount;
    const commissionRate = parseFloat(rate.commissionRate);

    // Tek √ßekim i√ßin komisyon sƒ±fƒ±r
    const effectiveCommissionRate = installmentCount === 1 ? 0 : commissionRate;

    const totalAmount = productPrice * (1 + effectiveCommissionRate / 100);
    const monthlyAmount = totalAmount / installmentCount;

    return {
      totalAmount: totalAmount,
      monthlyAmount: monthlyAmount,
      commissionRate: effectiveCommissionRate
    };
  };

  // Eƒüer hi√ß uygun se√ßenek yoksa
  if (Object.keys(groupedByBank).length === 0) {
    return (
      <div className={`${className}`}>
        <h3 className="text-lg font-semibold text-gray-900 mb-4">Taksit Se√ßenekleri</h3>
        <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
          <div className="flex items-start gap-3">
            <Info className="w-5 h-5 text-yellow-600 mt-0.5 flex-shrink-0" />
            <div>
              <p className="text-yellow-800 text-sm">
                Bu √ºr√ºn i√ßin minimum tutarƒ± kar≈üƒ±lamadƒ±ƒüƒ±ndan taksit se√ßeneƒüi bulunmamaktadƒ±r.
              </p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className={`${className}`}>
      <h3 className="text-lg font-semibold text-gray-900 mb-4">Taksit Se√ßenekleri</h3>

      {/* Bilgilendirme */}
      <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-6">
        <div className="flex items-start gap-3">
          <Info className="w-5 h-5 text-blue-600 mt-0.5 flex-shrink-0" />
          <div className="text-left">
            <p className="text-blue-800 text-sm leading-relaxed">
              A≈üaƒüƒ±daki taksit se√ßenekleri bu √ºr√ºn i√ßin ge√ßerlidir. 
              Tek √ßekim alƒ±≈üveri≈ülerde komisyon √ºcreti alƒ±nmaz.
            </p>
          </div>
        </div>
      </div>

      {/* Banka bazƒ±nda taksit se√ßenekleri */}
      <div className="space-y-6">
        {Object.entries(groupedByBank).map(([bankName, rates]: [string, any]) => {
          // Taksit sayƒ±sƒ±na g√∂re sƒ±rala
          const sortedRates = rates.sort((a: CommissionRate, b: CommissionRate) => 
            a.installmentCount - b.installmentCount
          );

          return (
            <Card key={bankName} className="overflow-hidden" data-testid={`bank-installments-${bankName.toLowerCase().replace(/\s+/g, '-')}`}>
              <CardHeader className="bg-gradient-to-r from-gray-50 to-gray-100 py-3">
                <CardTitle className="text-lg flex items-center gap-3">
                  <BankLogo bankName={bankName} className="w-16 h-12" showName={false} />
                  <span className="font-medium">{bankName}</span>
                  <Badge variant="outline" className="ml-auto">
                    {sortedRates.length} se√ßenek
                  </Badge>
                </CardTitle>
              </CardHeader>
              <CardContent className="p-0">
                <div className="overflow-x-auto">
                  <table className="w-full">
                    <thead className="bg-gray-50 border-b">
                      <tr>
                        <th className="text-left py-3 px-4 font-semibold text-gray-700 text-sm">Taksit</th>
                        <th className="text-left py-3 px-4 font-semibold text-gray-700 text-sm">Komisyon</th>
                        <th className="text-left py-3 px-4 font-semibold text-gray-700 text-sm">Aylƒ±k Tutar</th>
                        <th className="text-left py-3 px-4 font-semibold text-gray-700 text-sm">Toplam Tutar</th>
                      </tr>
                    </thead>
                    <tbody>
                      {sortedRates.map((rate: CommissionRate) => {
                        const calculation = calculateInstallment(rate);
                        const bankId = bankName.toLowerCase().replace(/\s+/g, '-');

                        return (
                          <tr 
                            key={rate.id} 
                            className="border-b hover:bg-gray-50 transition-colors"
                            data-testid={`row-installment-${bankId}-${rate.installmentCount}`}
                          >
                            <td className="py-3 px-4">
                              <Badge variant={rate.installmentCount === 1 ? "secondary" : "default"}>
                                {rate.installmentCount === 1 ? 'Tek √áekim' : `${rate.installmentCount} Ay`}
                              </Badge>
                            </td>
                            <td className="py-3 px-4">
                              <span className={`font-semibold ${calculation.commissionRate === 0 ? 'text-green-600' : 'text-orange-600'}`}>
                                %{calculation.commissionRate.toFixed(2)}
                              </span>
                            </td>
                            <td className="py-3 px-4" data-testid={`text-monthly-${bankId}-${rate.installmentCount}`}>
                              <span className="font-bold text-primary">
                                {calculation.monthlyAmount.toLocaleString("tr-TR", { 
                                  minimumFractionDigits: 2, 
                                  maximumFractionDigits: 2 
                                })} ‚Ç∫
                              </span>
                            </td>
                            <td className="py-3 px-4">
                              <span className="text-gray-700 font-medium">
                                {calculation.totalAmount.toLocaleString("tr-TR", { 
                                  minimumFractionDigits: 2, 
                                  maximumFractionDigits: 2 
                                })} ‚Ç∫
                              </span>
                            </td>
                          </tr>
                        );
                      })}
                    </tbody>
                  </table>
                </div>
              </CardContent>
            </Card>
          );
        })}
      </div>
    </div>
  );
}

client/src/pages/admin/categories.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Plus, Edit, Trash2, Upload, Image } from "lucide-react";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
// Remove unused import

interface Category {
  id: string;
  name: string;
  icon: string;
  image?: string;
  subcategories: string[];
}

export default function CategoryManagement() {
  const [isCreateOpen, setIsCreateOpen] = useState(false);
  const [editingCategory, setEditingCategory] = useState<Category | null>(null);
  const [formData, setFormData] = useState({
    name: "",
    icon: "",
    image: "",
    subcategories: [] as string[],
  });
  const [subcategoryInput, setSubcategoryInput] = useState("");
  const [imageFile, setImageFile] = useState<File | null>(null);
  const [imagePreview, setImagePreview] = useState<string>("");

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch categories
  const { data: categories = [], isLoading } = useQuery<Category[]>({
    queryKey: ["/api/admin/categories"],
  });

  // Create category mutation
  const createCategoryMutation = useMutation({
    mutationFn: async (categoryData: any) => {
      const response = await fetch("/api/admin/categories", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(categoryData),
      });
      if (!response.ok) throw new Error("Failed to create category");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/categories"] });
      toast({ title: "Kategori ba≈üarƒ±yla olu≈üturuldu!" });
      resetForm();
      setIsCreateOpen(false);
    },
    onError: () => {
      toast({ title: "Kategori olu≈üturulurken hata olu≈ütu", variant: "destructive" });
    },
  });

  // Update category mutation
  const updateCategoryMutation = useMutation({
    mutationFn: async ({ id, ...data }: any) => {
      const response = await fetch(`/api/admin/categories/${id}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });
      if (!response.ok) throw new Error("Failed to update category");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/categories"] });
      toast({ title: "Kategori ba≈üarƒ±yla g√ºncellendi!" });
      resetForm();
      setEditingCategory(null);
    },
    onError: () => {
      toast({ title: "Kategori g√ºncellenirken hata olu≈ütu", variant: "destructive" });
    },
  });

  // Delete category mutation
  const deleteCategoryMutation = useMutation({
    mutationFn: async (id: string) => {
      const response = await fetch(`/api/admin/categories/${id}`, {
        method: "DELETE",
      });
      if (!response.ok) throw new Error("Failed to delete category");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/categories"] });
      toast({ title: "Kategori ba≈üarƒ±yla silindi!" });
    },
    onError: () => {
      toast({ title: "Kategori silinirken hata olu≈ütu", variant: "destructive" });
    },
  });

  const resetForm = () => {
    setFormData({ name: "", icon: "", image: "", subcategories: [] });
    setSubcategoryInput("");
    setImageFile(null);
    setImagePreview("");
  };

  const handleImageChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      setImageFile(file);
      const reader = new FileReader();
      reader.onload = (e) => {
        const result = e.target?.result as string;
        setImagePreview(result);
        setFormData(prev => ({ ...prev, image: result }));
      };
      reader.readAsDataURL(file);
    }
  };

  const addSubcategory = () => {
    if (subcategoryInput.trim() && !formData.subcategories.includes(subcategoryInput.trim())) {
      setFormData(prev => ({
        ...prev,
        subcategories: [...prev.subcategories, subcategoryInput.trim()]
      }));
      setSubcategoryInput("");
    }
  };

  const removeSubcategory = (index: number) => {
    setFormData(prev => ({
      ...prev,
      subcategories: prev.subcategories.filter((_, i) => i !== index)
    }));
  };

  const handleCreate = () => {
    if (!formData.name.trim()) {
      toast({ title: "Kategori adƒ± gerekli", variant: "destructive" });
      return;
    }
    createCategoryMutation.mutate(formData);
  };

  const handleUpdate = () => {
    if (!editingCategory || !formData.name.trim()) {
      toast({ title: "Kategori adƒ± gerekli", variant: "destructive" });
      return;
    }
    updateCategoryMutation.mutate({ id: editingCategory.id, ...formData });
  };

  const startEdit = (category: Category) => {
    setEditingCategory(category);
    setFormData({
      name: category.name,
      icon: category.icon,
      image: category.image || "",
      subcategories: category.subcategories || [],
    });
    setImagePreview(category.image || "");
  };

  if (isLoading) {
    return <div className="p-6">Kategoriler y√ºkleniyor...</div>;
  }

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">Kategori Y√∂netimi</h1>

        <Dialog open={isCreateOpen} onOpenChange={setIsCreateOpen}>
          <DialogTrigger asChild>
            <Button data-testid="button-create-category">
              <Plus className="h-4 w-4 mr-2" />
              Yeni Kategori
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Yeni Kategori Olu≈ütur</DialogTitle>
              <DialogDescription>
                Kategori bilgilerini girin ve fotoƒüraf y√ºkleyin
              </DialogDescription>
            </DialogHeader>

            <div className="space-y-4">
              <div>
                <Label htmlFor="name">Kategori Adƒ±</Label>
                <Input
                  id="name"
                  value={formData.name}
                  onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value }))}
                  placeholder="√ñrn: Beyaz E≈üya"
                  data-testid="input-category-name"
                />
              </div>

              <div>
                <Label htmlFor="icon">ƒ∞kon (Emoji)</Label>
                <Input
                  id="icon"
                  value={formData.icon}
                  onChange={(e) => setFormData(prev => ({ ...prev, icon: e.target.value }))}
                  placeholder="üè†"
                  data-testid="input-category-icon"
                />
              </div>

              <div>
                <Label htmlFor="image">Kategori Fotoƒürafƒ±</Label>
                <div className="space-y-2">
                  <Input
                    id="image"
                    type="file"
                    accept="image/*"
                    onChange={handleImageChange}
                    data-testid="input-category-image"
                  />
                  {imagePreview && (
                    <div className="flex items-center gap-2">
                      <img 
                        src={imagePreview} 
                        alt="√ñnizleme" 
                        className="h-16 w-16 object-contain border rounded"
                      />
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => {
                          setImagePreview("");
                          setFormData(prev => ({ ...prev, image: "" }));
                          setImageFile(null);
                        }}
                      >
                        Kaldƒ±r
                      </Button>
                    </div>
                  )}
                </div>
              </div>

              <div>
                <Label>Alt Kategoriler</Label>
                <div className="flex gap-2 mb-2">
                  <Input
                    value={subcategoryInput}
                    onChange={(e) => setSubcategoryInput(e.target.value)}
                    placeholder="Alt kategori adƒ±"
                    onKeyPress={(e) => e.key === 'Enter' && addSubcategory()}
                    data-testid="input-subcategory"
                  />
                  <Button type="button" onClick={addSubcategory} data-testid="button-add-subcategory">
                    Ekle
                  </Button>
                </div>
                <div className="flex flex-wrap gap-2">
                  {formData.subcategories.map((sub, index) => (
                    <Badge 
                      key={index} 
                      variant="secondary" 
                      className="cursor-pointer"
                      onClick={() => removeSubcategory(index)}
                    >
                      {sub} √ó
                    </Badge>
                  ))}
                </div>
              </div>

              <div className="flex justify-end gap-2">
                <Button variant="outline" onClick={() => setIsCreateOpen(false)}>
                  ƒ∞ptal
                </Button>
                <Button 
                  onClick={handleCreate}
                  disabled={createCategoryMutation.isPending}
                  data-testid="button-save-category"
                >
                  {createCategoryMutation.isPending ? "Kaydediliyor..." : "Kaydet"}
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {categories.map((category) => (
          <Card key={category.id} className="relative">
            <CardHeader className="pb-3">
              <div className="flex items-center justify-between">
                <CardTitle className="flex items-center gap-2">
                  {category.image ? (
                    <img 
                      src={category.image} 
                      alt={category.name} 
                      className="h-8 w-8 object-contain"
                    />
                  ) : (
                    <span className="text-2xl">{category.icon}</span>
                  )}
                  {category.name}
                </CardTitle>
                <div className="flex gap-1">
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => startEdit(category)}
                    data-testid={`button-edit-category-${category.id}`}
                  >
                    <Edit className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => deleteCategoryMutation.mutate(category.id)}
                    data-testid={`button-delete-category-${category.id}`}
                  >
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </div>
              </div>
            </CardHeader>
            <CardContent>
              <div className="text-sm text-gray-500 mb-2">
                {category.subcategories?.length || 0} alt kategori
              </div>
              <div className="flex flex-wrap gap-1">
                {category.subcategories?.slice(0, 3).map((sub, index) => (
                  <Badge key={index} variant="outline" className="text-xs">
                    {sub}
                  </Badge>
                ))}
                {(category.subcategories?.length || 0) > 3 && (
                  <Badge variant="outline" className="text-xs">
                    +{(category.subcategories?.length || 0) - 3}
                  </Badge>
                )}
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Edit Dialog */}
      <Dialog open={!!editingCategory} onOpenChange={() => setEditingCategory(null)}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Kategori D√ºzenle</DialogTitle>
            <DialogDescription>
              Kategori bilgilerini g√ºncelleyin
            </DialogDescription>
          </DialogHeader>

          <div className="space-y-4">
            <div>
              <Label htmlFor="edit-name">Kategori Adƒ±</Label>
              <Input
                id="edit-name"
                value={formData.name}
                onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value }))}
                placeholder="√ñrn: Beyaz E≈üya"
                data-testid="input-edit-category-name"
              />
            </div>

            <div>
              <Label htmlFor="edit-icon">ƒ∞kon (Emoji)</Label>
              <Input
                id="edit-icon"
                value={formData.icon}
                onChange={(e) => setFormData(prev => ({ ...prev, icon: e.target.value }))}
                placeholder="üè†"
                data-testid="input-edit-category-icon"
              />
            </div>

            <div>
              <Label htmlFor="edit-image">Kategori Fotoƒürafƒ±</Label>
              <div className="space-y-2">
                <Input
                  id="edit-image"
                  type="file"
                  accept="image/*"
                  onChange={handleImageChange}
                  data-testid="input-edit-category-image"
                />
                {imagePreview && (
                  <div className="flex items-center gap-2">
                    <img 
                      src={imagePreview} 
                      alt="√ñnizleme" 
                      className="h-16 w-16 object-contain border rounded"
                    />
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => {
                        setImagePreview("");
                        setFormData(prev => ({ ...prev, image: "" }));
                        setImageFile(null);
                      }}
                    >
                      Kaldƒ±r
                    </Button>
                  </div>
                )}
              </div>
            </div>

            <div>
              <Label>Alt Kategoriler</Label>
              <div className="flex gap-2 mb-2">
                <Input
                  value={subcategoryInput}
                  onChange={(e) => setSubcategoryInput(e.target.value)}
                  placeholder="Alt kategori adƒ±"
                  onKeyPress={(e) => e.key === 'Enter' && addSubcategory()}
                  data-testid="input-edit-subcategory"
                />
                <Button type="button" onClick={addSubcategory} data-testid="button-edit-add-subcategory">
                  Ekle
                </Button>
              </div>
              <div className="flex flex-wrap gap-2">
                {formData.subcategories.map((sub, index) => (
                  <Badge 
                    key={index} 
                    variant="secondary" 
                    className="cursor-pointer"
                    onClick={() => removeSubcategory(index)}
                  >
                    {sub} √ó
                  </Badge>
                ))}
              </div>
            </div>

            <div className="flex justify-end gap-2">
              <Button variant="outline" onClick={() => setEditingCategory(null)}>
                ƒ∞ptal
              </Button>
              <Button 
                onClick={handleUpdate}
                disabled={updateCategoryMutation.isPending}
                data-testid="button-update-category"
              >
                {updateCategoryMutation.isPending ? "G√ºncelleniyor..." : "G√ºncelle"}
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/admin/blog-management.t.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Checkbox } from "@/components/ui/checkbox";
import { useToast } from "@/hooks/use-toast";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Edit, Plus, Trash2, Eye, Calendar, User, TrendingUp, FileText, Tag } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { ObjectUploader } from "@/components/ObjectUploader";

interface BlogPost {
  id: string;
  title: string;
  slug: string;
  content: string;
  excerpt: string;
  featuredImage: string;
  status: "draft" | "published" | "archived";
  categoryId: string;
  authorId: string;
  tags: string[];
  viewCount: number;
  publishedAt?: string;
  createdAt: string;
  updatedAt: string;
  category?: {
    id: string;
    name: string;
  };
  author?: {
    id: string;
    firstName: string;
    lastName: string;
  };
}

interface BlogCategory {
  id: string;
  name: string;
  slug: string;
  description: string;
  color: string;
  isActive: boolean;
}

const statusOptions = [
  { value: "draft", label: "Taslak", color: "bg-gray-100 text-gray-800" },
  { value: "published", label: "Yayƒ±nlandƒ±", color: "bg-green-100 text-green-800" },
  { value: "archived", label: "Ar≈üivlendi", color: "bg-red-100 text-red-800" },
];

export default function BlogManagement() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [editingPost, setEditingPost] = useState<BlogPost | null>(null);
  const [showNewPostDialog, setShowNewPostDialog] = useState(false);
  const [showCategoriesDialog, setShowCategoriesDialog] = useState(false);
  const [searchTerm, setSearchTerm] = useState<string>("");
  const [statusFilter, setStatusFilter] = useState<string>("all");
  const [categoryFilter, setCategoryFilter] = useState<string>("all");

  const [newPostData, setNewPostData] = useState({
    title: "",
    slug: "",
    content: "",
    excerpt: "",
    featuredImage: "/public-objects/uploads/placeholder-blog.jpg",
    status: "draft" as "draft" | "published" | "archived",
    categoryId: "",
    tags: [] as string[],
    tagInput: ""
  });

  // Fetch blog posts
  const { data: blogPosts = [], isLoading } = useQuery<BlogPost[]>({
    queryKey: ["/api/admin/blog-posts"],
  });

  // Fetch blog categories
  const { data: categories = [] } = useQuery<BlogCategory[]>({
    queryKey: ["/api/admin/blog-categories"],
  });

  // Create blog post
  const createPostMutation = useMutation({
    mutationFn: async (postData: any) => {
      return await apiRequest("/api/admin/blog-posts", "POST", postData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/blog-posts"] });
      setShowNewPostDialog(false);
      resetForm();
      toast({ title: "Blog yazƒ±sƒ± ba≈üarƒ±yla olu≈üturuldu" });
    },
    onError: () => {
      toast({ title: "Blog yazƒ±sƒ± olu≈üturulurken hata olu≈ütu", variant: "destructive" });
    },
  });

  // Update blog post
  const updatePostMutation = useMutation({
    mutationFn: async ({ id, updates }: { id: string; updates: any }) => {
      return await apiRequest(`/api/admin/blog-posts/${id}`, "PUT", updates);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/blog-posts"] });
      setEditingPost(null);
      toast({ title: "Blog yazƒ±sƒ± g√ºncellendi" });
    },
    onError: () => {
      toast({ title: "Blog yazƒ±sƒ± g√ºncellenemedi", variant: "destructive" });
    },
  });

  // Delete blog post
  const deletePostMutation = useMutation({
    mutationFn: async (postId: string) => {
      return await apiRequest(`/api/admin/blog-posts/${postId}`, "DELETE");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/blog-posts"] });
      toast({ title: "Blog yazƒ±sƒ± silindi" });
    },
    onError: () => {
      toast({ title: "Blog yazƒ±sƒ± silinirken hata olu≈ütu", variant: "destructive" });
    },
  });

  // Filter blog posts
  const filteredPosts = blogPosts.filter((post) => {
    const matchesStatus = statusFilter === "all" || post.status === statusFilter;
    const matchesCategory = categoryFilter === "all" || post.categoryId === categoryFilter;
    const matchesSearch = 
      post.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      post.content.toLowerCase().includes(searchTerm.toLowerCase()) ||
      post.tags.some(tag => tag.toLowerCase().includes(searchTerm.toLowerCase()));
    return matchesStatus && matchesCategory && matchesSearch;
  });

  const resetForm = () => {
    setNewPostData({
      title: "",
      slug: "",
      content: "",
      excerpt: "",
      featuredImage: "/public-objects/uploads/placeholder-blog.jpg",
      status: "draft",
      categoryId: "",
      tags: [],
      tagInput: ""
    });
  };

  const handleInputChange = (field: string, value: any) => {
    setNewPostData(prev => ({
      ...prev,
      [field]: value
    }));

    // Auto-generate slug from title
    if (field === "title") {
      const slug = value
        .toLowerCase()
        .replace(/[^a-z0-9\s]/g, '')
        .replace(/\s+/g, '-')
        .trim();
      setNewPostData(prev => ({
        ...prev,
        slug: slug
      }));
    }
  };

  const addTag = () => {
    if (newPostData.tagInput.trim() && !newPostData.tags.includes(newPostData.tagInput.trim())) {
      setNewPostData(prev => ({
        ...prev,
        tags: [...prev.tags, prev.tagInput.trim()],
        tagInput: ""
      }));
    }
  };

  const removeTag = (tagToRemove: string) => {
    setNewPostData(prev => ({
      ...prev,
      tags: prev.tags.filter(tag => tag !== tagToRemove)
    }));
  };

  const getStatusBadge = (status: string) => {
    const statusOption = statusOptions.find(option => option.value === status);
    if (!statusOption) return <Badge variant="secondary">{status}</Badge>;

    return (
      <Badge className={statusOption.color}>
        {statusOption.label}
      </Badge>
    );
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('tr-TR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric'
    });
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold">Blog Y√∂netimi</h2>
          <p className="text-gray-600">Blog yazƒ±larƒ±nƒ± ve kategorilerini y√∂netin</p>
        </div>
        <div className="flex gap-2">
          <Button variant="outline" onClick={() => setShowCategoriesDialog(true)}>
            <Tag className="w-4 h-4 mr-2" />
            Kategoriler
          </Button>
          <Dialog open={showNewPostDialog} onOpenChange={setShowNewPostDialog}>
            <DialogTrigger asChild>
              <Button>
                <Plus className="w-4 h-4 mr-2" />
                Yeni Yazƒ±
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-5xl max-h-[90vh] overflow-y-auto">
              <DialogHeader>
                <DialogTitle>Yeni Blog Yazƒ±sƒ±</DialogTitle>
              </DialogHeader>
              <div className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="title">Ba≈ülƒ±k *</Label>
                    <Input
                      id="title"
                      value={newPostData.title}
                      onChange={(e) => handleInputChange("title", e.target.value)}
                      placeholder="Blog yazƒ±sƒ± ba≈ülƒ±ƒüƒ±"
                    />
                  </div>
                  <div>
                    <Label htmlFor="slug">URL Yolu *</Label>
                    <Input
                      id="slug"
                      value={newPostData.slug}
                      onChange={(e) => handleInputChange("slug", e.target.value)}
                      placeholder="blog-yazisi-url-yolu"
                    />
                  </div>
                </div>

                <div>
                  <Label htmlFor="excerpt">√ñzet</Label>
                  <Textarea
                    id="excerpt"
                    value={newPostData.excerpt}
                    onChange={(e) => handleInputChange("excerpt", e.target.value)}
                    placeholder="Blog yazƒ±sƒ±nƒ±n kƒ±sa √∂zeti..."
                    rows={3}
                  />
                </div>

                <div>
                  <Label htmlFor="content">ƒ∞√ßerik *</Label>
                  <Textarea
                    id="content"
                    value={newPostData.content}
                    onChange={(e) => handleInputChange("content", e.target.value)}
                    placeholder="Blog yazƒ±sƒ±nƒ±n tam i√ßeriƒüi..."
                    rows={10}
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="categoryId">Kategori *</Label>
                    <Select value={newPostData.categoryId} onValueChange={(value) => handleInputChange("categoryId", value)}>
                      <SelectTrigger>
                        <SelectValue placeholder="Kategori se√ßin" />
                      </SelectTrigger>
                      <SelectContent>
                        {categories.map((category) => (
                          <SelectItem key={category.id} value={category.id}>
                            {category.name}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>
                  <div>
                    <Label htmlFor="status">Durum</Label>
                    <Select value={newPostData.status} onValueChange={(value) => handleInputChange("status", value)}>
                      <SelectTrigger>
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        {statusOptions.map((option) => (
                          <SelectItem key={option.value} value={option.value}>
                            {option.label}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>
                </div>

                <div>
                  <Label>√ñne √áƒ±kan G√∂rsel</Label>
                  <ObjectUploader
                    currentImageUrl={newPostData.featuredImage}
                    onImageUploaded={(url) => handleInputChange("featuredImage", url)}
                    uploadPath="blog"
                    imageType="blog-post"
                  />
                </div>

                <div>
                  <Label>Etiketler</Label>
                  <div className="flex gap-2 mb-2">
                    <Input
                      value={newPostData.tagInput}
                      onChange={(e) => handleInputChange("tagInput", e.target.value)}
                      placeholder="Etiket ekle..."
                      onKeyPress={(e) => {
                        if (e.key === 'Enter') {
                          e.preventDefault();
                          addTag();
                        }
                      }}
                    />
                    <Button type="button" variant="outline" onClick={addTag}>
                      Ekle
                    </Button>
                  </div>
                  <div className="flex flex-wrap gap-2">
                    {newPostData.tags.map((tag) => (
                      <Badge key={tag} variant="secondary" className="cursor-pointer" onClick={() => removeTag(tag)}>
                        {tag} √ó
                      </Badge>
                    ))}
                  </div>
                </div>

                <div className="flex justify-end gap-2">
                  <Button variant="outline" onClick={() => setShowNewPostDialog(false)}>
                    ƒ∞ptal
                  </Button>
                  <Button 
                    onClick={() => createPostMutation.mutate(newPostData)}
                    disabled={!newPostData.title || !newPostData.slug || !newPostData.content || !newPostData.categoryId}
                  >
                    Olu≈ütur
                  </Button>
                </div>
              </div>
            </DialogContent>
          </Dialog>
        </div>
      </div>

      {/* Filters */}
      <Card>
        <CardContent className="p-4">
          <div className="flex gap-4 items-center">
            <div className="flex-1">
              <Input
                placeholder="Ba≈ülƒ±k, i√ßerik veya etiket ile ara..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
            <Select value={statusFilter} onValueChange={setStatusFilter}>
              <SelectTrigger className="w-40">
                <SelectValue placeholder="Durum" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">T√ºm Durumlar</SelectItem>
                {statusOptions.map((option) => (
                  <SelectItem key={option.value} value={option.value}>
                    {option.label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
            <Select value={categoryFilter} onValueChange={setCategoryFilter}>
              <SelectTrigger className="w-48">
                <SelectValue placeholder="Kategori" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">T√ºm Kategoriler</SelectItem>
                {categories.map((category) => (
                  <SelectItem key={category.id} value={category.id}>
                    {category.name}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>
        </CardContent>
      </Card>

      {/* Blog Posts Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {filteredPosts.map((post) => (
          <Card key={post.id} className="overflow-hidden">
            <div className="aspect-video relative">
              <img
                src={post.featuredImage || "/placeholder-blog.jpg"}
                alt={post.title}
                className="w-full h-full object-cover"
                onError={(e) => {
                  (e.target as HTMLImageElement).src = "/placeholder-blog.jpg";
                }}
              />
              <div className="absolute top-2 right-2">
                {getStatusBadge(post.status)}
              </div>
            </div>
            <CardContent className="p-6">
              <div className="space-y-3">
                <div>
                  <h3 className="font-semibold text-lg line-clamp-2">{post.title}</h3>
                  <p className="text-sm text-gray-600 line-clamp-2 mt-1">{post.excerpt}</p>
                </div>

                <div className="flex items-center justify-between text-sm text-gray-500">
                  <div className="flex items-center gap-4">
                    <div className="flex items-center gap-1">
                      <User className="w-3 h-3" />
                      <span>{post.author?.firstName} {post.author?.lastName}</span>
                    </div>
                    <div className="flex items-center gap-1">
                      <TrendingUp className="w-3 h-3" />
                      <span>{post.viewCount} g√∂r√ºnt√ºleme</span>
                    </div>
                  </div>
                </div>

                <div className="flex items-center gap-2 text-xs">
                  <Badge variant="outline">{post.category?.name}</Badge>
                  <div className="flex items-center gap-1 text-gray-500">
                    <Calendar className="w-3 h-3" />
                    <span>{formatDate(post.createdAt)}</span>
                  </div>
                </div>

                {post.tags.length > 0 && (
                  <div className="flex flex-wrap gap-1">
                    {post.tags.slice(0, 3).map((tag) => (
                      <Badge key={tag} variant="secondary" className="text-xs">
                        #{tag}
                      </Badge>
                    ))}
                    {post.tags.length > 3 && (
                      <span className="text-xs text-gray-500">+{post.tags.length - 3} daha</span>
                    )}
                  </div>
                )}

                <div className="flex justify-end gap-2">
                  <Button
                    size="sm"
                    variant="outline"
                    onClick={() => setEditingPost(post)}
                  >
                    <Edit className="w-3 h-3 mr-1" />
                    D√ºzenle
                  </Button>
                  <Button
                    size="sm"
                    variant="destructive"
                    onClick={() => {
                      if (confirm("Bu blog yazƒ±sƒ±nƒ± silmek istediƒüinizden emin misiniz?")) {
                        deletePostMutation.mutate(post.id);
                      }
                    }}
                  >
                    <Trash2 className="w-3 h-3 mr-1" />
                    Sil
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {filteredPosts.length === 0 && (
        <Card>
          <CardContent className="p-8 text-center">
            <FileText className="w-12 h-12 mx-auto mb-4 text-gray-400" />
            <h3 className="text-lg font-semibold mb-2">Blog yazƒ±sƒ± bulunamadƒ±</h3>
            <p className="text-gray-600 mb-4">
              {searchTerm || statusFilter !== "all" || categoryFilter !== "all"
                ? "Arama kriterlerinize uygun blog yazƒ±sƒ± bulunamadƒ±."
                : "Hen√ºz hi√ß blog yazƒ±sƒ± olu≈üturulmamƒ±≈ü."}
            </p>
            {(!searchTerm && statusFilter === "all" && categoryFilter === "all") && (
              <Button onClick={() => setShowNewPostDialog(true)}>
                <Plus className="w-4 h-4 mr-2" />
                ƒ∞lk Blog Yazƒ±sƒ±nƒ± Olu≈ütur
              </Button>
            )}
          </CardContent>
        </Card>
      )}

      {/* Edit Post Dialog */}
      {editingPost && (
        <Dialog open={!!editingPost} onOpenChange={() => setEditingPost(null)}>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Blog Yazƒ±sƒ± D√ºzenle</DialogTitle>
            </DialogHeader>
            <div className="space-y-4">
              <div>
                <Label htmlFor="editTitle">Ba≈ülƒ±k</Label>
                <Input
                  id="editTitle"
                  value={editingPost.title}
                  onChange={(e) => setEditingPost(prev => prev ? { ...prev, title: e.target.value } : null)}
                />
              </div>

              <div>
                <Label htmlFor="editStatus">Durum</Label>
                <Select 
                  value={editingPost.status} 
                  onValueChange={(value) => setEditingPost(prev => prev ? { ...prev, status: value as any } : null)}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {statusOptions.map((option) => (
                      <SelectItem key={option.value} value={option.value}>
                        {option.label}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div className="flex justify-end gap-2">
                <Button variant="outline" onClick={() => setEditingPost(null)}>
                  ƒ∞ptal
                </Button>
                <Button 
                  onClick={() => {
                    updatePostMutation.mutate({
                      id: editingPost.id,
                      updates: {
                        title: editingPost.title,
                        status: editingPost.status
                      }
                    });
                  }}
                >
                  G√ºncelle
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      )}
    </div>
  );
}

client/src/pages/admin/campaign-manageme.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Checkbox } from "@/components/ui/checkbox";
import { useToast } from "@/hooks/use-toast";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Edit, Plus, Trash2, Eye, Calendar, Target, TrendingUp } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { ObjectUploader } from "@/components/ObjectUploader";

interface Campaign {
  id: string;
  title: string;
  description: string;
  image: string;
  startDate: string;
  endDate: string;
  isActive: boolean;
  campaignType: "discount" | "free_shipping" | "bundle" | "special";
  targetAudience: string;
  clickCount: number;
  createdAt: string;
  updatedAt: string;
}

const campaignTypes = [
  { value: "discount", label: "ƒ∞ndirim Kampanyasƒ±" },
  { value: "free_shipping", label: "√úcretsiz Kargo" },
  { value: "bundle", label: "Paket Kampanya" },
  { value: "special", label: "√ñzel Kampanya" },
];

export default function CampaignManagement() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [editingCampaign, setEditingCampaign] = useState<Campaign | null>(null);
  const [showNewCampaignDialog, setShowNewCampaignDialog] = useState(false);
  const [searchTerm, setSearchTerm] = useState<string>("");
  const [statusFilter, setStatusFilter] = useState<string>("all");

  const [newCampaignData, setNewCampaignData] = useState({
    title: "",
    description: "",
    image: "/public-objects/uploads/placeholder-campaign.jpg",
    startDate: "",
    endDate: "",
    isActive: true,
    campaignType: "discount" as "discount" | "free_shipping" | "bundle" | "special",
    targetAudience: "all"
  });

  // Fetch campaigns
  const { data: campaigns = [], isLoading } = useQuery<Campaign[]>({
    queryKey: ["/api/admin/campaigns"],
  });

  // Create campaign
  const createCampaignMutation = useMutation({
    mutationFn: async (campaignData: any) => {
      return await apiRequest("/api/admin/campaigns", "POST", campaignData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/campaigns"] });
      setShowNewCampaignDialog(false);
      resetForm();
      toast({ title: "Kampanya ba≈üarƒ±yla olu≈üturuldu" });
    },
    onError: () => {
      toast({ title: "Kampanya olu≈üturulurken hata olu≈ütu", variant: "destructive" });
    },
  });

  // Update campaign
  const updateCampaignMutation = useMutation({
    mutationFn: async ({ id, updates }: { id: string; updates: any }) => {
      return await apiRequest(`/api/admin/campaigns/${id}`, "PUT", updates);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/campaigns"] });
      setEditingCampaign(null);
      toast({ title: "Kampanya g√ºncellendi" });
    },
    onError: () => {
      toast({ title: "Kampanya g√ºncellenemedi", variant: "destructive" });
    },
  });

  // Delete campaign
  const deleteCampaignMutation = useMutation({
    mutationFn: async (campaignId: string) => {
      return await apiRequest(`/api/admin/campaigns/${campaignId}`, "DELETE");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/campaigns"] });
      toast({ title: "Kampanya silindi" });
    },
    onError: () => {
      toast({ title: "Kampanya silinirken hata olu≈ütu", variant: "destructive" });
    },
  });

  // Filter campaigns
  const filteredCampaigns = campaigns.filter((campaign) => {
    const now = new Date();
    const endDate = new Date(campaign.endDate);
    const startDate = new Date(campaign.startDate);

    const matchesStatus = statusFilter === "all" || 
      (statusFilter === "active" && campaign.isActive && now >= startDate && now <= endDate) ||
      (statusFilter === "inactive" && !campaign.isActive) ||
      (statusFilter === "expired" && now > endDate) ||
      (statusFilter === "scheduled" && now < startDate);

    const matchesSearch = 
      campaign.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      campaign.description.toLowerCase().includes(searchTerm.toLowerCase());

    return matchesStatus && matchesSearch;
  });

  const resetForm = () => {
    setNewCampaignData({
      title: "",
      description: "",
      image: "/public-objects/uploads/placeholder-campaign.jpg",
      startDate: "",
      endDate: "",
      isActive: true,
      campaignType: "discount",
      targetAudience: "all"
    });
  };

  const handleInputChange = (field: string, value: any) => {
    setNewCampaignData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const getStatusBadge = (campaign: Campaign) => {
    const now = new Date();
    const startDate = new Date(campaign.startDate);
    const endDate = new Date(campaign.endDate);

    if (!campaign.isActive) {
      return <Badge variant="secondary">Pasif</Badge>;
    }
    if (now > endDate) {
      return <Badge variant="destructive">S√ºresi Dolmu≈ü</Badge>;
    }
    if (now < startDate) {
      return <Badge variant="outline">Zamanlanmƒ±≈ü</Badge>;
    }
    return <Badge variant="default">Aktif</Badge>;
  };

  const getCampaignTypeLabel = (type: string) => {
    return campaignTypes.find(t => t.value === type)?.label || type;
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('tr-TR');
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold">Kampanya Y√∂netimi</h2>
          <p className="text-gray-600">Pazarlama kampanyalarƒ±nƒ± olu≈üturun ve y√∂netin</p>
        </div>
        <Dialog open={showNewCampaignDialog} onOpenChange={setShowNewCampaignDialog}>
          <DialogTrigger asChild>
            <Button>
              <Plus className="w-4 h-4 mr-2" />
              Yeni Kampanya
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-3xl max-h-[90vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle>Yeni Kampanya</DialogTitle>
            </DialogHeader>
            <div className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div className="col-span-2">
                  <Label htmlFor="title">Kampanya Ba≈ülƒ±ƒüƒ± *</Label>
                  <Input
                    id="title"
                    value={newCampaignData.title}
                    onChange={(e) => handleInputChange("title", e.target.value)}
                    placeholder="Yazlƒ±k ƒ∞ndirim Kampanyasƒ±"
                  />
                </div>
              </div>

              <div>
                <Label htmlFor="description">A√ßƒ±klama *</Label>
                <Textarea
                  id="description"
                  value={newCampaignData.description}
                  onChange={(e) => handleInputChange("description", e.target.value)}
                  placeholder="Kampanya hakkƒ±nda detaylƒ± a√ßƒ±klama..."
                  rows={4}
                />
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="campaignType">Kampanya T√ºr√º *</Label>
                  <Select value={newCampaignData.campaignType} onValueChange={(value) => handleInputChange("campaignType", value)}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      {campaignTypes.map((type) => (
                        <SelectItem key={type.value} value={type.value}>
                          {type.label}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
                <div>
                  <Label htmlFor="targetAudience">Hedef Kitle</Label>
                  <Select value={newCampaignData.targetAudience} onValueChange={(value) => handleInputChange("targetAudience", value)}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">T√ºm M√º≈üteriler</SelectItem>
                      <SelectItem value="new_customers">Yeni M√º≈üteriler</SelectItem>
                      <SelectItem value="loyal_customers">Sadƒ±k M√º≈üteriler</SelectItem>
                      <SelectItem value="vip_customers">VIP M√º≈üteriler</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="startDate">Ba≈ülangƒ±√ß Tarihi *</Label>
                  <Input
                    id="startDate"
                    type="datetime-local"
                    value={newCampaignData.startDate}
                    onChange={(e) => handleInputChange("startDate", e.target.value)}
                  />
                </div>
                <div>
                  <Label htmlFor="endDate">Biti≈ü Tarihi *</Label>
                  <Input
                    id="endDate"
                    type="datetime-local"
                    value={newCampaignData.endDate}
                    onChange={(e) => handleInputChange("endDate", e.target.value)}
                  />
                </div>
              </div>

              <div>
                <Label>Kampanya G√∂rseli</Label>
                <ObjectUploader
                  currentImageUrl={newCampaignData.image}
                  onImageUploaded={(url) => handleInputChange("image", url)}
                  uploadPath="campaigns"
                  imageType="campaign"
                />
              </div>

              <div className="flex items-center space-x-2">
                <Checkbox
                  id="isActive"
                  checked={newCampaignData.isActive}
                  onCheckedChange={(checked) => handleInputChange("isActive", checked)}
                />
                <Label htmlFor="isActive">Aktif</Label>
              </div>

              <div className="flex justify-end gap-2">
                <Button variant="outline" onClick={() => setShowNewCampaignDialog(false)}>
                  ƒ∞ptal
                </Button>
                <Button 
                  onClick={() => createCampaignMutation.mutate(newCampaignData)}
                  disabled={!newCampaignData.title || !newCampaignData.description || !newCampaignData.startDate || !newCampaignData.endDate}
                >
                  Olu≈ütur
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      </div>

      {/* Filters */}
      <Card>
        <CardContent className="p-4">
          <div className="flex gap-4 items-center">
            <div className="flex-1">
              <Input
                placeholder="Kampanya ba≈ülƒ±ƒüƒ± veya a√ßƒ±klama ile ara..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
            <Select value={statusFilter} onValueChange={setStatusFilter}>
              <SelectTrigger className="w-48">
                <SelectValue placeholder="Durum filtrele" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">T√ºm Kampanyalar</SelectItem>
                <SelectItem value="active">Aktif</SelectItem>
                <SelectItem value="inactive">Pasif</SelectItem>
                <SelectItem value="expired">S√ºresi Dolmu≈ü</SelectItem>
                <SelectItem value="scheduled">Zamanlanmƒ±≈ü</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </CardContent>
      </Card>

      {/* Campaigns Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {filteredCampaigns.map((campaign) => (
          <Card key={campaign.id} className="overflow-hidden">
            <div className="aspect-video relative">
              <img
                src={campaign.image || "/placeholder-campaign.jpg"}
                alt={campaign.title}
                className="w-full h-full object-cover"
                onError={(e) => {
                  (e.target as HTMLImageElement).src = "/placeholder-campaign.jpg";
                }}
              />
              <div className="absolute top-2 right-2">
                {getStatusBadge(campaign)}
              </div>
            </div>
            <CardContent className="p-6">
              <div className="space-y-3">
                <div>
                  <h3 className="font-semibold text-lg line-clamp-1">{campaign.title}</h3>
                  <p className="text-sm text-gray-600 line-clamp-2">{campaign.description}</p>
                </div>

                <div className="flex justify-between items-center text-sm">
                  <Badge variant="outline">{getCampaignTypeLabel(campaign.campaignType)}</Badge>
                  <div className="flex items-center gap-1 text-gray-500">
                    <TrendingUp className="w-3 h-3" />
                    <span>{campaign.clickCount} tƒ±klama</span>
                  </div>
                </div>

                <div className="space-y-1 text-sm text-gray-600">
                  <div className="flex items-center gap-2">
                    <Calendar className="w-3 h-3" />
                    <span>{formatDate(campaign.startDate)} - {formatDate(campaign.endDate)}</span>
                  </div>
                  <div className="flex items-center gap-2">
                    <Target className="w-3 h-3" />
                    <span>Hedef: {campaign.targetAudience === "all" ? "T√ºm M√º≈üteriler" : campaign.targetAudience}</span>
                  </div>
                </div>

                <div className="flex justify-end gap-2">
                  <Button
                    size="sm"
                    variant="outline"
                    onClick={() => setEditingCampaign(campaign)}
                  >
                    <Edit className="w-3 h-3 mr-1" />
                    D√ºzenle
                  </Button>
                  <Button
                    size="sm"
                    variant="destructive"
                    onClick={() => {
                      if (confirm("Bu kampanyayƒ± silmek istediƒüinizden emin misiniz?")) {
                        deleteCampaignMutation.mutate(campaign.id);
                      }
                    }}
                  >
                    <Trash2 className="w-3 h-3 mr-1" />
                    Sil
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {filteredCampaigns.length === 0 && (
        <Card>
          <CardContent className="p-8 text-center">
            <Target className="w-12 h-12 mx-auto mb-4 text-gray-400" />
            <h3 className="text-lg font-semibold mb-2">Kampanya bulunamadƒ±</h3>
            <p className="text-gray-600 mb-4">
              {searchTerm || statusFilter !== "all" 
                ? "Arama kriterlerinize uygun kampanya bulunamadƒ±."
                : "Hen√ºz hi√ß kampanya olu≈üturulmamƒ±≈ü."}
            </p>
            {(!searchTerm && statusFilter === "all") && (
              <Button onClick={() => setShowNewCampaignDialog(true)}>
                <Plus className="w-4 h-4 mr-2" />
                ƒ∞lk Kampanyayƒ± Olu≈ütur
              </Button>
            )}
          </CardContent>
        </Card>
      )}

      {/* Edit Campaign Dialog */}
      {editingCampaign && (
        <Dialog open={!!editingCampaign} onOpenChange={() => setEditingCampaign(null)}>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Kampanya D√ºzenle</DialogTitle>
            </DialogHeader>
            <div className="space-y-4">
              <div>
                <Label htmlFor="editTitle">Kampanya Ba≈ülƒ±ƒüƒ±</Label>
                <Input
                  id="editTitle"
                  value={editingCampaign.title}
                  onChange={(e) => setEditingCampaign(prev => prev ? { ...prev, title: e.target.value } : null)}
                />
              </div>

              <div>
                <Label htmlFor="editDescription">A√ßƒ±klama</Label>
                <Textarea
                  id="editDescription"
                  value={editingCampaign.description}
                  onChange={(e) => setEditingCampaign(prev => prev ? { ...prev, description: e.target.value } : null)}
                  rows={3}
                />
              </div>

              <div className="flex items-center space-x-2">
                <Checkbox
                  checked={editingCampaign.isActive}
                  onCheckedChange={(checked) => setEditingCampaign(prev => prev ? { ...prev, isActive: !!checked } : null)}
                />
                <Label>Aktif</Label>
              </div>

              <div className="flex justify-end gap-2">
                <Button variant="outline" onClick={() => setEditingCampaign(null)}>
                  ƒ∞ptal
                </Button>
                <Button 
                  onClick={() => {
                    updateCampaignMutation.mutate({
                      id: editingCampaign.id,
                      updates: {
                        title: editingCampaign.title,
                        description: editingCampaign.description,
                        isActive: editingCampaign.isActive
                      }
                    });
                  }}
                >
                  G√ºncelle
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      )}
    </div>
  );
}

client/src/pages/admin/commission-rates-.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Edit, Save, CreditCard, Percent, DollarSign } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";

interface CommissionRate {
  id: string;
  bankName: string;
  installments: number;
  commissionRate: string;
  bankCode?: string;
  isActive: boolean;
  virtualPosConfigId?: string;
  createdAt: string;
  updatedAt: string;
}

export default function CommissionRatesManagement() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [editingRate, setEditingRate] = useState<CommissionRate | null>(null);

  // Fetch commission rates
  const { data: commissionRates = [], isLoading } = useQuery<CommissionRate[]>({
    queryKey: ["/api/commission-rates"],
  });

  // Update commission rate
  const updateCommissionRateMutation = useMutation({
    mutationFn: async ({ id, updates }: { id: string; updates: any }) => {
      return await apiRequest(`/api/admin/commission-rates/${id}`, "PUT", updates);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/commission-rates"] });
      setEditingRate(null);
      toast({ title: "Komisyon oranƒ± g√ºncellendi" });
    },
    onError: () => {
      toast({ title: "Komisyon oranƒ± g√ºncellenemedi", variant: "destructive" });
    },
  });

  const formatCommissionRate = (rate: string) => {
    return `%${parseFloat(rate).toFixed(2)}`;
  };

  const groupedRates = commissionRates.reduce((groups, rate) => {
    const bankName = rate.bankName;
    if (!groups[bankName]) {
      groups[bankName] = [];
    }
    groups[bankName].push(rate);
    return groups;
  }, {} as Record<string, CommissionRate[]>);

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold">Komisyon Oranlarƒ±</h2>
          <p className="text-gray-600">Banka ve taksit komisyon oranlarƒ±nƒ± y√∂netin</p>
        </div>
      </div>

      {/* Commission Rates by Bank */}
      <div className="space-y-6">
        {Object.entries(groupedRates).map(([bankName, rates]) => (
          <Card key={bankName}>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <CreditCard className="w-5 h-5" />
                {bankName}
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {rates
                  .sort((a, b) => a.installments - b.installments)
                  .map((rate) => (
                    <Card key={rate.id} className="p-4">
                      <div className="flex justify-between items-start mb-3">
                        <div>
                          <div className="flex items-center gap-2 mb-1">
                            <span className="font-semibold text-lg">
                              {rate.installments} Taksit
                            </span>
                            <Badge variant={rate.isActive ? "default" : "secondary"}>
                              {rate.isActive ? "Aktif" : "Pasif"}
                            </Badge>
                          </div>
                          <div className="flex items-center gap-2 text-primary">
                            <Percent className="w-4 h-4" />
                            <span className="text-xl font-bold">
                              {formatCommissionRate(rate.commissionRate)}
                            </span>
                          </div>
                        </div>
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => setEditingRate(rate)}
                        >
                          <Edit className="w-3 h-3" />
                        </Button>
                      </div>

                      {rate.bankCode && (
                        <div className="text-sm text-gray-600">
                          <span className="font-medium">Banka Kodu:</span> {rate.bankCode}
                        </div>
                      )}
                    </Card>
                  ))}
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {commissionRates.length === 0 && (
        <Card>
          <CardContent className="p-8 text-center">
            <Percent className="w-12 h-12 mx-auto mb-4 text-gray-400" />
