import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { Eye, MessageSquare, User, Calendar, Mail, Phone, Reply } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";

interface ContactMessage {
  id: string;
  firstName: string;
  lastName: string;
  email: string;
  phone: string;
  subject: string;
  message: string;
  status: "unread" | "read" | "replied";
  createdAt: string;
  updatedAt: string;
  adminResponse?: string;
  responseDate?: string;
}

const statusOptions = [
  { value: "unread", label: "Okunmadƒ±", color: "bg-red-100 text-red-800" },
  { value: "read", label: "Okundu", color: "bg-blue-100 text-blue-800" },
  { value: "replied", label: "Yanƒ±tlandƒ±", color: "bg-green-100 text-green-800" },
];

export default function ContactMessagesManagement() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedMessage, setSelectedMessage] = useState<ContactMessage | null>(null);
  const [responseText, setResponseText] = useState("");
  const [statusFilter, setStatusFilter] = useState<string>("all");
  const [searchTerm, setSearchTerm] = useState<string>("");

  // Fetch all contact messages
  const { data: messages = [], isLoading } = useQuery<ContactMessage[]>({
    queryKey: ["/api/admin/contact-messages"],
  });

  // Update message status
  const updateMessageStatusMutation = useMutation({
    mutationFn: async ({ messageId, status }: { messageId: string; status: string }) => {
      return await apiRequest(`/api/admin/contact-messages/${messageId}/status`, "PUT", { status });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/contact-messages"] });
      toast({ title: "Mesaj durumu g√ºncellendi" });
    },
    onError: () => {
      toast({ title: "Mesaj durumu g√ºncellenemedi", variant: "destructive" });
    },
  });

  // Send response
  const sendResponseMutation = useMutation({
    mutationFn: async ({ messageId, response }: { messageId: string; response: string }) => {
      return await apiRequest(`/api/admin/contact-messages/${messageId}/response`, "POST", { 
        response 
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/contact-messages"] });
      setResponseText("");
      toast({ title: "Yanƒ±t g√∂nderildi" });
    },
    onError: () => {
      toast({ title: "Yanƒ±t g√∂nderilemedi", variant: "destructive" });
    },
  });

  // Mark as read when opening
  const markAsRead = (message: ContactMessage) => {
    if (message.status === "unread") {
      updateMessageStatusMutation.mutate({
        messageId: message.id,
        status: "read"
      });
    }
    setSelectedMessage(message);
  };

  // Filter messages
  const filteredMessages = messages.filter((message) => {
    const matchesStatus = statusFilter === "all" || message.status === statusFilter;
    const matchesSearch = 
      message.firstName.toLowerCase().includes(searchTerm.toLowerCase()) ||
      message.lastName.toLowerCase().includes(searchTerm.toLowerCase()) ||
      message.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
      message.subject.toLowerCase().includes(searchTerm.toLowerCase()) ||
      message.message.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesStatus && matchesSearch;
  });

  const getStatusBadge = (status: string) => {
    const statusOption = statusOptions.find(option => option.value === status);
    if (!statusOption) return <Badge variant="secondary">{status}</Badge>;

    return (
      <Badge className={statusOption.color}>
        {statusOption.label}
      </Badge>
    );
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('tr-TR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold">ƒ∞leti≈üim Mesajlarƒ±</h2>
          <p className="text-gray-600">M√º≈üteri ileti≈üim mesajlarƒ±nƒ± g√∂r√ºnt√ºleyin ve yanƒ±tlayƒ±n</p>
        </div>
      </div>

      {/* Filters */}
      <Card>
        <CardContent className="p-4">
          <div className="flex gap-4 items-center">
            <div className="flex-1">
              <Input
                placeholder="Ad, soyad, e-posta veya konu ile ara..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
            <Select value={statusFilter} onValueChange={setStatusFilter}>
              <SelectTrigger className="w-40">
                <SelectValue placeholder="Durum" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">T√ºm Durumlar</SelectItem>
                {statusOptions.map((option) => (
                  <SelectItem key={option.value} value={option.value}>
                    {option.label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>
        </CardContent>
      </Card>

      {/* Messages List */}
      <div className="space-y-4">
        {filteredMessages.map((message) => (
          <Card 
            key={message.id} 
            className={`overflow-hidden cursor-pointer transition-colors ${
              message.status === "unread" ? "border-l-4 border-l-red-500 bg-red-50/30" : ""
            }`}
            onClick={() => markAsRead(message)}
          >
            <CardContent className="p-6">
              <div className="flex items-start justify-between mb-4">
                <div className="flex-1">
                  <div className="flex items-center gap-3 mb-2">
                    <h3 className="font-semibold text-lg">{message.subject}</h3>
                    {getStatusBadge(message.status)}
                  </div>
                  <p className="text-gray-700 line-clamp-2 mb-3">{message.message}</p>
                  <div className="flex items-center gap-4 text-sm text-gray-600">
                    <div className="flex items-center gap-1">
                      <User className="w-4 h-4" />
                      <span>{message.firstName} {message.lastName}</span>
                    </div>
                    <div className="flex items-center gap-1">
                      <Mail className="w-4 h-4" />
                      <span>{message.email}</span>
                    </div>
                    <div className="flex items-center gap-1">
                      <Phone className="w-4 h-4" />
                      <span>{message.phone}</span>
                    </div>
                    <div className="flex items-center gap-1">
                      <Calendar className="w-4 h-4" />
                      <span>{formatDate(message.createdAt)}</span>
                    </div>
                  </div>
                </div>
                <div className="flex gap-2">
                  <Select
                    value={message.status}
                    onValueChange={(newStatus) => {
                      updateMessageStatusMutation.mutate({
                        messageId: message.id,
                        status: newStatus
                      });
                    }}
                    onClick={(e) => e.stopPropagation()}
                  >
                    <SelectTrigger className="w-32">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      {statusOptions.map((option) => (
                        <SelectItem key={option.value} value={option.value}>
                          {option.label}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <Button
                    variant="outline"
                    onClick={(e) => {
                      e.stopPropagation();
                      setSelectedMessage(message);
                    }}
                  >
                    <Eye className="w-4 h-4 mr-2" />
                    Detaylar
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {filteredMessages.length === 0 && (
        <Card>
          <CardContent className="p-8 text-center">
            <MessageSquare className="w-12 h-12 mx-auto mb-4 text-gray-400" />
            <h3 className="text-lg font-semibold mb-2">Mesaj bulunamadƒ±</h3>
            <p className="text-gray-600">
              {searchTerm || statusFilter !== "all"
                ? "Arama kriterlerinize uygun mesaj bulunamadƒ±."
                : "Hen√ºz hi√ß ileti≈üim mesajƒ± alƒ±nmamƒ±≈ü."}
            </p>
          </CardContent>
        </Card>
      )}

      {/* Message Details Modal */}
      {selectedMessage && (
        <Dialog open={!!selectedMessage} onOpenChange={() => setSelectedMessage(null)}>
          <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle>{selectedMessage.subject}</DialogTitle>
            </DialogHeader>
            <div className="space-y-6">
              {/* Contact Information */}
              <div className="grid grid-cols-2 gap-6">
                <div>
                  <h4 className="font-semibold mb-2">ƒ∞leti≈üim Bilgileri</h4>
                  <div className="space-y-1 text-sm">
                    <p><span className="font-medium">Ad Soyad:</span> {selectedMessage.firstName} {selectedMessage.lastName}</p>
                    <p><span className="font-medium">E-posta:</span> {selectedMessage.email}</p>
                    <p><span className="font-medium">Telefon:</span> {selectedMessage.phone}</p>
                  </div>
                </div>
                <div>
                  <h4 className="font-semibold mb-2">Mesaj Bilgileri</h4>
                  <div className="space-y-1 text-sm">
                    <p><span className="font-medium">Konu:</span> {selectedMessage.subject}</p>
                    <p><span className="font-medium">Durum:</span> {getStatusBadge(selectedMessage.status)}</p>
                    <p><span className="font-medium">G√∂nderilme:</span> {formatDate(selectedMessage.createdAt)}</p>
                    {selectedMessage.responseDate && (
                      <p><span className="font-medium">Yanƒ±tlanma:</span> {formatDate(selectedMessage.responseDate)}</p>
                    )}
                  </div>
                </div>
              </div>

              {/* Original Message */}
              <div>
                <h4 className="font-semibold mb-2">Mesaj ƒ∞√ßeriƒüi</h4>
                <div className="bg-gray-50 p-4 rounded-lg">
                  <p className="whitespace-pre-wrap">{selectedMessage.message}</p>
                </div>
              </div>

              {/* Previous Response */}
              {selectedMessage.adminResponse && (
                <div>
                  <h4 className="font-semibold mb-2">√ñnceki Yanƒ±t</h4>
                  <div className="bg-blue-50 p-4 rounded-lg border-l-4 border-blue-500">
                    <p className="whitespace-pre-wrap">{selectedMessage.adminResponse}</p>
                    <p className="text-xs text-gray-600 mt-2">
                      Yanƒ±tlandƒ±: {selectedMessage.responseDate && formatDate(selectedMessage.responseDate)}
                    </p>
                  </div>
                </div>
              )}

              {/* Send Response */}
              <div>
                <h4 className="font-semibold mb-2">
                  {selectedMessage.adminResponse ? "Yeni Yanƒ±t G√∂nder" : "Yanƒ±t G√∂nder"}
                </h4>
                <div className="space-y-3">
                  <Textarea
                    placeholder="M√º≈üteriye yanƒ±t yazƒ±n..."
                    value={responseText}
                    onChange={(e) => setResponseText(e.target.value)}
                    rows={6}
                  />
                  <div className="flex justify-end gap-2">
                    <Button
                      variant="outline"
                      onClick={() => setResponseText("")}
                    >
                      Temizle
                    </Button>
                    <Button
                      onClick={() => {
                        if (responseText.trim()) {
                          sendResponseMutation.mutate({
                            messageId: selectedMessage.id,
                            response: responseText.trim()
                          });
                        }
                      }}
                      disabled={!responseText.trim() || sendResponseMutation.isPending}
                    >
                      <Reply className="w-4 h-4 mr-2" />
                      {sendResponseMutation.isPending ? "G√∂nderiliyor..." : "Yanƒ±t G√∂nder"}
                    </Button>
                  </div>
                </div>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      )}
    </div>
  );
}

