export default function CorporateContentManagement() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [editingContent, setEditingContent] = useState<CorporateContent | null>(null);
  const [showNewContentDialog, setShowNewContentDialog] = useState(false);
