  const [contentTypeFilter, setContentTypeFilter] = useState<string>("all");

  const [newContentData, setNewContentData] = useState({
    contentType: "about",
    title: "",
    content: "",
    excerpt: "",
    image: "",
    isActive: true,
    sortOrder: 0,
    metadata: {
      author: "",
      department: "",
      effectiveDate: "",
      version: "1.0"
    }
  });

  // Fetch corporate content
  const { data: corporateContent = [], isLoading } = useQuery<CorporateContent[]>({
    queryKey: ["/api/admin/corporate-content"],
  });

  // Create corporate content
  const createContentMutation = useMutation({
    mutationFn: async (contentData: any) => {
      return await apiRequest("/api/admin/corporate-content", "POST", contentData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/corporate-content"] });
      setShowNewContentDialog(false);
      resetForm();
      toast({ title: "Kurumsal i√ßerik ba≈üarƒ±yla olu≈üturuldu" });
    },
    onError: () => {
      toast({ title: "Kurumsal i√ßerik olu≈üturulurken hata olu≈ütu", variant: "destructive" });
    },
  });

  // Update corporate content
  const updateContentMutation = useMutation({
    mutationFn: async ({ id, updates }: { id: string; updates: any }) => {
      return await apiRequest(`/api/admin/corporate-content/${id}`, "PUT", updates);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/corporate-content"] });
      setEditingContent(null);
      toast({ title: "Kurumsal i√ßerik g√ºncellendi" });
    },
    onError: () => {
      toast({ title: "Kurumsal i√ßerik g√ºncellenemedi", variant: "destructive" });
    },
  });

  // Filter corporate content
  const filteredContent = corporateContent.filter((content) => {
    const matchesContentType = contentTypeFilter === "all" || content.contentType === contentTypeFilter;
    const matchesSearch = 
      content.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      content.content.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesContentType && matchesSearch;
  });

  const resetForm = () => {
