                  rows={3}
                />
              </div>

              <div>
                <Label htmlFor="content">ƒ∞√ßerik *</Label>
                <Textarea
                  id="content"
                  value={newContentData.content}
                  onChange={(e) => handleInputChange("content", e.target.value)}
                  placeholder="Tam i√ßerik metni..."
                  rows={12}
                />
              </div>

              <div>
                <Label>G√∂rsel</Label>
                <ObjectUploader
                  currentImageUrl={newContentData.image}
                  onImageUploaded={(url: string) => handleInputChange("image", url)}
                  uploadPath="corporate"
                  imageType="corporate-content"
                />
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="author">Yazar</Label>
                  <Input
                    id="author"
                    value={newContentData.metadata.author}
                    onChange={(e) => handleInputChange("metadata.author", e.target.value)}
                    placeholder="ƒ∞√ßerik yazarƒ±"
                  />
                </div>
                <div>
                  <Label htmlFor="department">Departman</Label>
                  <Input
                    id="department"
                    value={newContentData.metadata.department}
                    onChange={(e) => handleInputChange("metadata.department", e.target.value)}
                    placeholder="Sorumlu departman"
                  />
                </div>
              </div>

              <div className="grid grid-cols-3 gap-4">
                <div>
                  <Label htmlFor="effectiveDate">Y√ºr√ºrl√ºk Tarihi</Label>
                  <Input
                    id="effectiveDate"
                    type="date"
                    value={newContentData.metadata.effectiveDate}
                    onChange={(e) => handleInputChange("metadata.effectiveDate", e.target.value)}
                  />
                </div>
                <div>
                  <Label htmlFor="version">Versiyon</Label>
                  <Input
                    id="version"
                    value={newContentData.metadata.version}
                    onChange={(e) => handleInputChange("metadata.version", e.target.value)}
                    placeholder="1.0"
                  />
                </div>
                <div>
                  <Label htmlFor="sortOrder">Sƒ±ra</Label>
                  <Input
                    id="sortOrder"
                    type="number"
                    value={newContentData.sortOrder}
                    onChange={(e) => handleInputChange("sortOrder", parseInt(e.target.value))}
                    placeholder="0"
                  />
                </div>
              </div>

              <div className="flex items-center space-x-2">
                <Checkbox
                  id="isActive"
                  checked={newContentData.isActive}
                  onCheckedChange={(checked) => handleInputChange("isActive", checked)}
                />
                <Label htmlFor="isActive">Aktif</Label>
              </div>

              <div className="flex justify-end gap-2">
                <Button variant="outline" onClick={() => setShowNewContentDialog(false)}>
                  ƒ∞ptal
                </Button>
                <Button 
                  onClick={() => createContentMutation.mutate(newContentData)}
                  disabled={!newContentData.title || !newContentData.content}
                >
                  Olu≈ütur
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      </div>

      {/* Filters */}
      <Card>
        <CardContent className="p-4">
          <div className="flex gap-4 items-center">
            <div className="flex-1">
              <Input
                placeholder="Ba≈ülƒ±k veya i√ßerik ile ara..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
            <Select value={contentTypeFilter} onValueChange={setContentTypeFilter}>
              <SelectTrigger className="w-48">
                <SelectValue placeholder="ƒ∞√ßerik t√ºr√º filtrele" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">T√ºm ƒ∞√ßerik T√ºrleri</SelectItem>
                {contentTypes.map((type) => (
                  <SelectItem key={type.value} value={type.value}>
                    {type.label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>
        </CardContent>
      </Card>

      {/* Content List */}
      <div className="space-y-4">
        {filteredContent
          .sort((a, b) => a.sortOrder - b.sortOrder)
          .map((content) => {
            const typeInfo = getContentTypeInfo(content.contentType);
            const IconComponent = typeInfo.icon;

            return (
              <Card key={content.id} className="overflow-hidden">
                <CardContent className="p-6">
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <div className="flex items-center gap-3 mb-2">
                        <IconComponent className="w-5 h-5 text-primary" />
                        <Badge variant="outline">{typeInfo.label}</Badge>
                        <Badge variant={content.isActive ? "default" : "secondary"}>
                          {content.isActive ? "Aktif" : "Pasif"}
                        </Badge>
                      </div>

                      <h3 className="font-semibold text-lg mb-2">{content.title}</h3>

                      {content.excerpt && (
                        <p className="text-gray-700 text-sm mb-3 line-clamp-2">{content.excerpt}</p>
                      )}

                      <div className="grid grid-cols-2 gap-4 text-xs text-gray-600">
                        {content.metadata?.author && (
                          <div>
                            <span className="font-medium">Yazar:</span> {content.metadata.author}
                          </div>
                        )}
                        {content.metadata?.department && (
                          <div>
                            <span className="font-medium">Departman:</span> {content.metadata.department}
                          </div>
                        )}
                        {content.metadata?.version && (
                          <div>
                            <span className="font-medium">Versiyon:</span> {content.metadata.version}
                          </div>
                        )}
                        <div>
                          <span className="font-medium">G√ºncellendi:</span> {formatDate(content.updatedAt)}
                        </div>
                      </div>
                    </div>

                    <div className="flex gap-2">
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => setEditingContent(content)}
                      >
                        <Edit className="w-3 h-3 mr-1" />
                        D√ºzenle
                      </Button>
                    </div>
                  </div>
                </CardContent>
              </Card>
            );
          })}
      </div>

      {filteredContent.length === 0 && (
        <Card>
          <CardContent className="p-8 text-center">
            <FileText className="w-12 h-12 mx-auto mb-4 text-gray-400" />
            <h3 className="text-lg font-semibold mb-2">Kurumsal i√ßerik bulunamadƒ±</h3>
            <p className="text-gray-600 mb-4">
              {searchTerm || contentTypeFilter !== "all" 
                ? "Arama kriterlerinize uygun i√ßerik bulunamadƒ±."
                : "Hen√ºz hi√ß kurumsal i√ßerik olu≈üturulmamƒ±≈ü."}
            </p>
            {(!searchTerm && contentTypeFilter === "all") && (
              <Button onClick={() => setShowNewContentDialog(true)}>
                <Plus className="w-4 h-4 mr-2" />
                ƒ∞lk ƒ∞√ßeriƒüi Olu≈ütur
              </Button>
            )}
          </CardContent>
        </Card>
      )}

      {/* Edit Content Dialog */}
      {editingContent && (
        <Dialog open={!!editingContent} onOpenChange={() => setEditingContent(null)}>
          <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle>Kurumsal ƒ∞√ßerik D√ºzenle</DialogTitle>
            </DialogHeader>
            <div className="space-y-4">
              <div>
                <Label htmlFor="editTitle">Ba≈ülƒ±k</Label>
                <Input
                  id="editTitle"
                  value={editingContent.title}
                  onChange={(e) => setEditingContent(prev => prev ? { ...prev, title: e.target.value } : null)}
                />
              </div>

              <div>
                <Label htmlFor="editExcerpt">√ñzet</Label>
                <Textarea
                  id="editExcerpt"
                  value={editingContent.excerpt || ""}
                  onChange={(e) => setEditingContent(prev => prev ? { ...prev, excerpt: e.target.value } : null)}
                  rows={3}
                />
              </div>

              <div>
                <Label htmlFor="editContent">ƒ∞√ßerik</Label>
                <Textarea
                  id="editContent"
                  value={editingContent.content}
                  onChange={(e) => setEditingContent(prev => prev ? { ...prev, content: e.target.value } : null)}
                  rows={12}
                />
              </div>

              <div className="flex items-center space-x-2">
                <Checkbox
                  checked={editingContent.isActive}
                  onCheckedChange={(checked) => setEditingContent(prev => prev ? { ...prev, isActive: !!checked } : null)}
                />
                <Label>Aktif</Label>
              </div>

              <div className="flex justify-end gap-2">
                <Button variant="outline" onClick={() => setEditingContent(null)}>
                  ƒ∞ptal
                </Button>
                <Button 
                  onClick={() => {
                    updateContentMutation.mutate({
                      id: editingContent.id,
                      updates: {
                        title: editingContent.title,
                        excerpt: editingContent.excerpt,
                        content: editingContent.content,
                        isActive: editingContent.isActive
                      }
                    });
                  }}
                >
                  <Save className="w-4 h-4 mr-2" />
                  G√ºncelle
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      )}
    </div>
  );
}

