import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Checkbox } from "@/components/ui/checkbox";
import { useToast } from "@/hooks/use-toast";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Edit, Plus, Trash2, Eye, Save, Percent, Calendar, DollarSign } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";

interface Coupon {
  id: string;
  code: string;
  name: string;
  description?: string;
  type: "percentage" | "fixed";
  value: string;
  maxDiscount: string;
  minOrderAmount: string;
  usageLimit: number;
  usedCount: number;
  isActive: boolean;
  validFrom: string;
  validUntil: string;
  createdAt: string;
  updatedAt: string;
}

export default function CouponManagement() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [editingCoupon, setEditingCoupon] = useState<Coupon | null>(null);
  const [showNewCouponDialog, setShowNewCouponDialog] = useState(false);
  const [searchTerm, setSearchTerm] = useState<string>("");
  const [statusFilter, setStatusFilter] = useState<string>("all");

  const [newCouponData, setNewCouponData] = useState({
    code: "",
    name: "",
    description: "",
    type: "percentage" as "percentage" | "fixed",
    value: "",
    maxDiscount: "0",
    minOrderAmount: "0",
    usageLimit: 100,
    isActive: true,
    validFrom: "",
    validUntil: ""
  });

  // Fetch coupons
  const { data: coupons = [], isLoading } = useQuery<Coupon[]>({
    queryKey: ["/api/admin/coupons"],
  });

  // Create coupon
  const createCouponMutation = useMutation({
    mutationFn: async (couponData: any) => {
      return await apiRequest("/api/admin/coupons", "POST", couponData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/coupons"] });
      setShowNewCouponDialog(false);
      resetForm();
      toast({ title: "ƒ∞ndirim kuponu ba≈üarƒ±yla olu≈üturuldu" });
    },
    onError: () => {
      toast({ title: "ƒ∞ndirim kuponu olu≈üturulurken hata olu≈ütu", variant: "destructive" });
    },
  });

  // Update coupon
  const updateCouponMutation = useMutation({
    mutationFn: async ({ id, updates }: { id: string; updates: any }) => {
      return await apiRequest(`/api/admin/coupons/${id}`, "PUT", updates);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/coupons"] });
      setEditingCoupon(null);
      toast({ title: "ƒ∞ndirim kuponu g√ºncellendi" });
    },
    onError: () => {
      toast({ title: "ƒ∞ndirim kuponu g√ºncellenemedi", variant: "destructive" });
    },
  });

  // Delete coupon
  const deleteCouponMutation = useMutation({
    mutationFn: async (couponId: string) => {
      return await apiRequest(`/api/admin/coupons/${couponId}`, "DELETE");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/coupons"] });
      toast({ title: "ƒ∞ndirim kuponu silindi" });
    },
    onError: () => {
      toast({ title: "ƒ∞ndirim kuponu silinirken hata olu≈ütu", variant: "destructive" });
    },
  });

  // Filter coupons
  const filteredCoupons = coupons.filter((coupon) => {
    const matchesStatus = statusFilter === "all" || 
      (statusFilter === "active" && coupon.isActive) ||
      (statusFilter === "inactive" && !coupon.isActive) ||
      (statusFilter === "expired" && new Date(coupon.validUntil) < new Date());

    const matchesSearch = 
      coupon.code.toLowerCase().includes(searchTerm.toLowerCase()) ||
      coupon.name.toLowerCase().includes(searchTerm.toLowerCase());

    return matchesStatus && matchesSearch;
  });

  const resetForm = () => {
    setNewCouponData({
      code: "",
      name: "",
      description: "",
      type: "percentage",
      value: "",
      maxDiscount: "0",
      minOrderAmount: "0",
      usageLimit: 100,
      isActive: true,
      validFrom: "",
      validUntil: ""
    });
  };

  const handleInputChange = (field: string, value: any) => {
    setNewCouponData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const getStatusBadge = (coupon: Coupon) => {
    const now = new Date();
    const validUntil = new Date(coupon.validUntil);
    const validFrom = new Date(coupon.validFrom);

    if (!coupon.isActive) {
      return <Badge variant="secondary">Pasif</Badge>;
    }
    if (now > validUntil) {
      return <Badge variant="destructive">S√ºresi Dolmu≈ü</Badge>;
    }
    if (now < validFrom) {
      return <Badge variant="outline">Beklemede</Badge>;
    }
    if (coupon.usageLimit > 0 && coupon.usedCount >= coupon.usageLimit) {
      return <Badge variant="destructive">Limit Dolmu≈ü</Badge>;
    }
    return <Badge variant="default">Aktif</Badge>;
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('tr-TR');
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold">ƒ∞ndirim Kuponlarƒ±</h2>
          <p className="text-gray-600">ƒ∞ndirim kuponlarƒ±nƒ± olu≈üturun ve y√∂netin</p>
        </div>
        <Dialog open={showNewCouponDialog} onOpenChange={setShowNewCouponDialog}>
          <DialogTrigger asChild>
            <Button>
              <Plus className="w-4 h-4 mr-2" />
              Yeni Kupon
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Yeni ƒ∞ndirim Kuponu</DialogTitle>
            </DialogHeader>
            <div className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="code">Kupon Kodu *</Label>
                  <Input
                    id="code"
                    value={newCouponData.code}
                    onChange={(e) => handleInputChange("code", e.target.value.toUpperCase())}
                    placeholder="YAZI2024"
                  />
                </div>
                <div>
                  <Label htmlFor="name">Kupon Adƒ± *</Label>
                  <Input
                    id="name"
                    value={newCouponData.name}
                    onChange={(e) => handleInputChange("name", e.target.value)}
                    placeholder="Yazlƒ±k ƒ∞ndirim"
                  />
                </div>
              </div>

              <div>
                <Label htmlFor="description">A√ßƒ±klama</Label>
                <Textarea
                  id="description"
                  value={newCouponData.description}
                  onChange={(e) => handleInputChange("description", e.target.value)}
                  placeholder="ƒ∞ndirim kuponu hakkƒ±nda a√ßƒ±klama..."
                />
              </div>

              <div className="grid grid-cols-3 gap-4">
                <div>
                  <Label htmlFor="type">ƒ∞ndirim T√ºr√º *</Label>
                  <Select value={newCouponData.type} onValueChange={(value) => handleInputChange("type", value)}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="percentage">Y√ºzde (%)</SelectItem>
                      <SelectItem value="fixed">Sabit Tutar (‚Ç∫)</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <div>
                  <Label htmlFor="value">ƒ∞ndirim Deƒüeri *</Label>
                  <Input
                    id="value"
                    type="number"
                    step="0.01"
                    value={newCouponData.value}
                    onChange={(e) => handleInputChange("value", e.target.value)}
                    placeholder={newCouponData.type === "percentage" ? "10" : "50.00"}
                  />
                </div>
                <div>
                  <Label htmlFor="maxDiscount">Maks. ƒ∞ndirim (‚Ç∫)</Label>
                  <Input
                    id="maxDiscount"
                    type="number"
                    step="0.01"
                    value={newCouponData.maxDiscount}
                    onChange={(e) => handleInputChange("maxDiscount", e.target.value)}
                    placeholder="0 = Sƒ±nƒ±rsƒ±z"
                  />
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="minOrderAmount">Min. Sipari≈ü Tutarƒ± (‚Ç∫)</Label>
                  <Input
                    id="minOrderAmount"
                    type="number"
                    step="0.01"
                    value={newCouponData.minOrderAmount}
                    onChange={(e) => handleInputChange("minOrderAmount", e.target.value)}
                    placeholder="0.00"
                  />
                </div>
                <div>
                  <Label htmlFor="usageLimit">Kullanƒ±m Limiti</Label>
                  <Input
                    id="usageLimit"
                    type="number"
                    value={newCouponData.usageLimit}
                    onChange={(e) => handleInputChange("usageLimit", parseInt(e.target.value))}
                    placeholder="100"
                  />
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="validFrom">Ba≈ülangƒ±√ß Tarihi *</Label>
                  <Input
                    id="validFrom"
                    type="datetime-local"
                    value={newCouponData.validFrom}
                    onChange={(e) => handleInputChange("validFrom", e.target.value)}
                  />
                </div>
                <div>
                  <Label htmlFor="validUntil">Biti≈ü Tarihi *</Label>
                  <Input
                    id="validUntil"
                    type="datetime-local"
                    value={newCouponData.validUntil}
                    onChange={(e) => handleInputChange("validUntil", e.target.value)}
                  />
                </div>
              </div>

              <div className="flex items-center space-x-2">
                <Checkbox
                  id="isActive"
                  checked={newCouponData.isActive}
                  onCheckedChange={(checked) => handleInputChange("isActive", checked)}
                />
                <Label htmlFor="isActive">Aktif</Label>
              </div>

              <div className="flex justify-end gap-2">
                <Button variant="outline" onClick={() => setShowNewCouponDialog(false)}>
                  ƒ∞ptal
                </Button>
                <Button 
                  onClick={() => createCouponMutation.mutate(newCouponData)}
                  disabled={!newCouponData.code || !newCouponData.name || !newCouponData.value || !newCouponData.validFrom || !newCouponData.validUntil}
                >
                  Olu≈ütur
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      </div>

      {/* Filters */}
      <Card>
        <CardContent className="p-4">
          <div className="flex gap-4 items-center">
            <div className="flex-1">
              <Input
                placeholder="Kupon kodu veya adƒ± ile ara..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
            <Select value={statusFilter} onValueChange={setStatusFilter}>
              <SelectTrigger className="w-48">
                <SelectValue placeholder="Durum filtrele" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">T√ºm Kuponlar</SelectItem>
                <SelectItem value="active">Aktif</SelectItem>
                <SelectItem value="inactive">Pasif</SelectItem>
                <SelectItem value="expired">S√ºresi Dolmu≈ü</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </CardContent>
      </Card>

      {/* Coupons List */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {filteredCoupons.map((coupon) => (
          <Card key={coupon.id} className="overflow-hidden">
            <CardContent className="p-6">
              <div className="flex justify-between items-start mb-4">
                <div>
                  <div className="flex items-center gap-2 mb-2">
                    <h3 className="font-bold text-lg">{coupon.code}</h3>
                    {getStatusBadge(coupon)}
                  </div>
                  <p className="font-medium text-gray-900">{coupon.name}</p>
                  {coupon.description && (
                    <p className="text-sm text-gray-600 mt-1">{coupon.description}</p>
                  )}
                </div>
                <div className="text-right">
                  <div className="flex items-center gap-1 text-primary">
                    {coupon.type === "percentage" ? (
                      <>
                        <Percent className="w-4 h-4" />
                        <span className="text-xl font-bold">{coupon.value}%</span>
                      </>
                    ) : (
                      <>
                        <DollarSign className="w-4 h-4" />
                        <span className="text-xl font-bold">{parseFloat(coupon.value).toLocaleString('tr-TR')} ‚Ç∫</span>
                      </>
                    )}
                  </div>
                </div>
              </div>

              <div className="space-y-2 text-sm text-gray-600 mb-4">
                {parseFloat(coupon.maxDiscount) > 0 && (
                  <p>Max. ƒ∞ndirim: {parseFloat(coupon.maxDiscount).toLocaleString('tr-TR')} ‚Ç∫</p>
                )}
                {parseFloat(coupon.minOrderAmount) > 0 && (
                  <p>Min. Sipari≈ü: {parseFloat(coupon.minOrderAmount).toLocaleString('tr-TR')} ‚Ç∫</p>
                )}
                <p>Kullanƒ±m: {coupon.usedCount} / {coupon.usageLimit > 0 ? coupon.usageLimit : "Sƒ±nƒ±rsƒ±z"}</p>
                <p>Ge√ßerli: {formatDate(coupon.validFrom)} - {formatDate(coupon.validUntil)}</p>
              </div>

              <div className="flex justify-end gap-2">
                <Button
                  size="sm"
                  variant="outline"
                  onClick={() => setEditingCoupon(coupon)}
                >
                  <Edit className="w-3 h-3 mr-1" />
                  D√ºzenle
                </Button>
                <Button
                  size="sm"
                  variant="destructive"
                  onClick={() => {
                    if (confirm("Bu kuponu silmek istediƒüinizden emin misiniz?")) {
                      deleteCouponMutation.mutate(coupon.id);
                    }
                  }}
                >
                  <Trash2 className="w-3 h-3 mr-1" />
                  Sil
                </Button>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {filteredCoupons.length === 0 && (
        <Card>
          <CardContent className="p-8 text-center">
            <Percent className="w-12 h-12 mx-auto mb-4 text-gray-400" />
            <h3 className="text-lg font-semibold mb-2">ƒ∞ndirim kuponu bulunamadƒ±</h3>
            <p className="text-gray-600 mb-4">
              {searchTerm || statusFilter !== "all" 
                ? "Arama kriterlerinize uygun kupon bulunamadƒ±."
                : "Hen√ºz hi√ß indirim kuponu olu≈üturulmamƒ±≈ü."}
            </p>
            {(!searchTerm && statusFilter === "all") && (
              <Button onClick={() => setShowNewCouponDialog(true)}>
                <Plus className="w-4 h-4 mr-2" />
                ƒ∞lk Kuponu Olu≈ütur
              </Button>
            )}
          </CardContent>
        </Card>
      )}

      {/* Edit Coupon Dialog */}
      {editingCoupon && (
        <Dialog open={!!editingCoupon} onOpenChange={() => setEditingCoupon(null)}>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Kupon D√ºzenle - {editingCoupon.code}</DialogTitle>
            </DialogHeader>
            <div className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label>Kupon Kodu</Label>
                  <Input value={editingCoupon.code} disabled />
                </div>
                <div>
                  <Label htmlFor="editName">Kupon Adƒ±</Label>
                  <Input
                    id="editName"
                    value={editingCoupon.name}
                    onChange={(e) => setEditingCoupon(prev => prev ? { ...prev, name: e.target.value } : null)}
                  />
                </div>
              </div>

              <div className="flex items-center space-x-2">
                <Checkbox
                  checked={editingCoupon.isActive}
                  onCheckedChange={(checked) => setEditingCoupon(prev => prev ? { ...prev, isActive: !!checked } : null)}
                />
                <Label>Aktif</Label>
              </div>

              <div className="flex justify-end gap-2">
                <Button variant="outline" onClick={() => setEditingCoupon(null)}>
                  ƒ∞ptal
                </Button>
                <Button 
                  onClick={() => {
                    updateCouponMutation.mutate({
                      id: editingCoupon.id,
                      updates: {
                        name: editingCoupon.name,
                        isActive: editingCoupon.isActive
                      }
                    });
                  }}
                >
                  G√ºncelle
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      )}
    </div>
  );
}

