import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Switch } from "@/components/ui/switch";
import { useToast } from "@/hooks/use-toast";
import { Save, Settings, Globe, Mail, Phone, MapPin, Shield } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { ObjectUploader } from "@/components/ObjectUploader";

interface GeneralSettings {
  id: string;
  siteName: string;
  siteDescription: string;
  siteUrl: string;
  logoUrl: string;
  faviconUrl: string;
  contactEmail: string;
  contactPhone: string;
  contactAddress: string;
  socialMedia: {
    facebook?: string;
    twitter?: string;
    instagram?: string;
    linkedin?: string;
    youtube?: string;
  };
  seoSettings: {
    metaTitle: string;
    metaDescription: string;
    metaKeywords: string;
  };
  emailSettings: {
    smtpHost: string;
    smtpPort: number;
    smtpUser: string;
    smtpPassword: string;
    fromEmail: string;
    fromName: string;
  };
  features: {
    enableRegistration: boolean;
    enableComments: boolean;
    enableNewsletter: boolean;
    enableCookieConsent: boolean;
    enableAnalytics: boolean;
  };
  maintenanceMode: boolean;
  maintenanceMessage: string;
  createdAt: string;
  updatedAt: string;
}

export default function GeneralSettingsManagement() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isEditing, setIsEditing] = useState(false);

  // Fetch general settings
  const { data: settings, isLoading } = useQuery<GeneralSettings>({
    queryKey: ["/api/admin/settings"],
  });

  // Update settings
  const updateSettingsMutation = useMutation({
    mutationFn: async (updates: Partial<GeneralSettings>) => {
      return await apiRequest("/api/admin/settings", "PUT", updates);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/settings"] });
      setIsEditing(false);
      toast({ title: "Ayarlar ba≈üarƒ±yla g√ºncellendi" });
    },
    onError: () => {
      toast({ title: "Ayarlar g√ºncellenemedi", variant: "destructive" });
    },
  });

  const [editedSettings, setEditedSettings] = useState<GeneralSettings | null>(null);

  const handleEdit = () => {
    if (settings) {
      setEditedSettings({ ...settings });
      setIsEditing(true);
    }
  };

  const handleSave = () => {
    if (editedSettings) {
      updateSettingsMutation.mutate(editedSettings);
    }
  };

  const handleCancel = () => {
    setEditedSettings(null);
    setIsEditing(false);
  };

  const updateField = (path: string, value: any) => {
    if (!editedSettings) return;

    const keys = path.split('.');
    const newSettings = { ...editedSettings };

    let current: any = newSettings;
    for (let i = 0; i < keys.length - 1; i++) {
      current = current[keys[i]];
    }
    current[keys[keys.length - 1]] = value;

    setEditedSettings(newSettings);
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  if (!settings) {
    return (
      <div className="flex items-center justify-center h-64">
        <p className="text-gray-500">Ayarlar y√ºklenemedi</p>
      </div>
    );
  }

  const currentSettings = editedSettings || settings;

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold">Genel Ayarlar</h2>
