              )}
            </div>
          </div>

          <div>
            <Label htmlFor="siteDescription">Site A√ßƒ±klamasƒ±</Label>
            {isEditing ? (
              <Textarea
                id="siteDescription"
                value={currentSettings.siteDescription}
                onChange={(e) => updateField('siteDescription', e.target.value)}
                rows={3}
              />
            ) : (
              <p className="py-2 text-gray-900">{currentSettings.siteDescription}</p>
            )}
          </div>

          {isEditing && (
            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label>Logo</Label>
                <ObjectUploader
                  currentImageUrl={currentSettings.logoUrl}
                  onImageUploaded={(url) => updateField('logoUrl', url)}
                  uploadPath="settings"
                  imageType="logo"
                />
              </div>
              <div>
                <Label>Favicon</Label>
                <ObjectUploader
                  currentImageUrl={currentSettings.faviconUrl}
