import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Checkbox } from "@/components/ui/checkbox";
import { useToast } from "@/hooks/use-toast";
import { Edit, Plus, Trash2, Navigation, ArrowUp, ArrowDown, ExternalLink } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";

interface NavigationItem {
  id: string;
  title: string;
  url: string;
  parentId?: string;
  sortOrder: number;
  isActive: boolean;
  isExternal: boolean;
  icon?: string;
  description?: string;
  createdAt: string;
  updatedAt: string;
  children?: NavigationItem[];
}

export default function NavigationManagement() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [editingItem, setEditingItem] = useState<NavigationItem | null>(null);
  const [showNewItemDialog, setShowNewItemDialog] = useState(false);
  const [selectedParentId, setSelectedParentId] = useState<string>("");

  const [newItemData, setNewItemData] = useState({
    title: "",
    url: "",
    parentId: null as string | null,
    isActive: true,
    isExternal: false,
    icon: "",
    description: "",
    sortOrder: 0
  });

  // Fetch navigation items
  const { data: navigationItems = [], isLoading } = useQuery<NavigationItem[]>({
    queryKey: ["/api/admin/navigation"],
  });

  // Create navigation item
  const createNavigationItemMutation = useMutation({
    mutationFn: async (itemData: any) => {
      return await apiRequest("/api/admin/navigation", "POST", itemData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/navigation"] });
      setShowNewItemDialog(false);
      resetForm();
      toast({ title: "Navigasyon √∂ƒüesi ba≈üarƒ±yla olu≈üturuldu" });
    },
    onError: () => {
      toast({ title: "Navigasyon √∂ƒüesi olu≈üturulurken hata olu≈ütu", variant: "destructive" });
    },
  });

  // Update navigation item
  const updateNavigationItemMutation = useMutation({
    mutationFn: async ({ id, updates }: { id: string; updates: any }) => {
      return await apiRequest(`/api/admin/navigation/${id}`, "PUT", updates);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/navigation"] });
      setEditingItem(null);
      toast({ title: "Navigasyon √∂ƒüesi g√ºncellendi" });
    },
    onError: () => {
      toast({ title: "Navigasyon √∂ƒüesi g√ºncellenemedi", variant: "destructive" });
    },
  });

  // Delete navigation item
  const deleteNavigationItemMutation = useMutation({
    mutationFn: async (itemId: string) => {
      return await apiRequest(`/api/admin/navigation/${itemId}`, "DELETE");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/navigation"] });
      toast({ title: "Navigasyon √∂ƒüesi silindi" });
    },
    onError: () => {
      toast({ title: "Navigasyon √∂ƒüesi silinirken hata olu≈ütu", variant: "destructive" });
    },
  });

  // Update sort order
  const updateSortOrderMutation = useMutation({
    mutationFn: async ({ itemId, direction }: { itemId: string; direction: "up" | "down" }) => {
      return await apiRequest(`/api/admin/navigation/${itemId}/sort`, "PUT", { direction });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/navigation"] });
      toast({ title: "Sƒ±ralama g√ºncellendi" });
    },
    onError: () => {
      toast({ title: "Sƒ±ralama g√ºncellenemedi", variant: "destructive" });
    },
  });

  const resetForm = () => {
    setNewItemData({
      title: "",
      url: "",
      parentId: null,
      isActive: true,
      isExternal: false,
      icon: "",
      description: "",
      sortOrder: 0
    });
  };

  const handleInputChange = (field: string, value: any) => {
    setNewItemData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  // Get top level items (no parent)
  const topLevelItems = navigationItems.filter(item => !item.parentId);

  // Get children for each item
  const getChildren = (parentId: string) => {
    return navigationItems.filter(item => item.parentId === parentId);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('tr-TR');
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
