                    onChange={(e) => handleInputChange("url", e.target.value)}
                    placeholder="/"
                  />
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="icon">ƒ∞kon (Emoji)</Label>
                  <Input
                    id="icon"
