  // Add banner mutation
  const addBannerMutation = useMutation({
    mutationFn: async (data: Omit<CategoryBanner, "id" | "createdAt" | "updatedAt">) => {
      const response = await fetch("/api/category-banners", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });
      if (!response.ok) throw new Error("Failed to add banner");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/category-banners"] });
      setIsAddDialogOpen(false);
      resetForm();
      toast({
        title: "Ba≈üarƒ±lƒ±",
        description: "Kategori banner'ƒ± ba≈üarƒ±yla eklendi.",
        className: "bg-green-50 border-green-200 text-green-800",
      });
    },
    onError: (error) => {
      toast({
        title: "Hata",
        description: "Banner eklenirken bir hata olu≈ütu.",
        variant: "destructive",
      });
    },
  });

  // Update banner mutation
  const updateBannerMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: Partial<CategoryBanner> }) => {
      const response = await fetch(`/api/category-banners/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });
      if (!response.ok) throw new Error("Failed to update banner");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/category-banners"] });
      setIsEditDialogOpen(false);
      setEditingBanner(null);
      resetForm();
      toast({
        title: "Ba≈üarƒ±lƒ±",
        description: "Kategori banner'ƒ± ba≈üarƒ±yla g√ºncellendi.",
        className: "bg-green-50 border-green-200 text-green-800",
      });
    },
    onError: (error) => {
      toast({
        title: "Hata",
        description: "Banner g√ºncellenirken bir hata olu≈ütu.",
        variant: "destructive",
      });
    },
  });

  // Delete banner mutation
  const deleteBannerMutation = useMutation({
    mutationFn: async (id: string) => {
      const response = await fetch(`/api/category-banners/${id}`, {
        method: "DELETE",
      });
      if (!response.ok) throw new Error("Failed to delete banner");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/category-banners"] });
      toast({
        title: "Ba≈üarƒ±lƒ±",
        description: "Kategori banner'ƒ± ba≈üarƒ±yla silindi.",
        className: "bg-green-50 border-green-200 text-green-800",
      });
    },
    onError: (error) => {
      toast({
        title: "Hata",
        description: "Banner silinirken bir hata olu≈ütu.",
        variant: "destructive",
      });
    },
  });

  // Handle image upload
  const handleImageUpload = async () => {
    if (!imageFile) return;

    setIsUploading(true);
    const formDataForUpload = new FormData();
    formDataForUpload.append("file", imageFile);

    try {
      const response = await fetch("/api/upload", {
        method: "POST",
        body: formDataForUpload,
      });

      if (!response.ok) {
        throw new Error("Upload failed");
      }

      const result = await response.json();
      setFormData(prev => ({ ...prev, imageUrl: result.url }));
      setImageFile(null);
      toast({
        title: "Ba≈üarƒ±lƒ±",
        description: "Resim ba≈üarƒ±yla y√ºklendi.",
        className: "bg-green-50 border-green-200 text-green-800",
      });
    } catch (error) {
      toast({
        title: "Hata",
        description: "Resim y√ºklenirken bir hata olu≈ütu.",
        variant: "destructive",
      });
    } finally {
      setIsUploading(false);
    }
  };

  const resetForm = () => {
    setFormData({
      categoryName: "",
      title: "",
      description: "",
      imageUrl: "",
      isActive: true,
    });
    setImageFile(null);
  };

  const handleEdit = (banner: CategoryBanner) => {
    setEditingBanner(banner);
    setFormData({
      categoryName: banner.categoryName,
      title: banner.title,
      description: banner.description || "",
      imageUrl: banner.imageUrl,
      isActive: banner.isActive ?? true,
    });
    setIsEditDialogOpen(true);
  };

  const handleSubmit = () => {
    if (!formData.categoryName || !formData.title || !formData.imageUrl) {
      toast({
        title: "Eksik Bilgi",
        description: "L√ºtfen kategori adƒ±, ba≈ülƒ±k ve resim alanlarƒ±nƒ± doldurun.",
        variant: "destructive",
      });
      return;
    }

    if (editingBanner) {
      updateBannerMutation.mutate({
        id: editingBanner.id,
        data: formData,
      });
    } else {
      addBannerMutation.mutate(formData);
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Kategori Banner Y√∂netimi</h1>
          <p className="text-gray-600 mt-2">
            Kategori sayfalarƒ±nda g√∂sterilecek hero banner'larƒ± y√∂netin
          </p>
        </div>
        <Dialog open={isAddDialogOpen} onOpenChange={setIsAddDialogOpen}>
          <DialogTrigger asChild>
            <Button data-testid="button-add-banner">
              <Plus className="w-4 h-4 mr-2" />
              Yeni Banner Ekle
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Yeni Kategori Banner Ekle</DialogTitle>
            </DialogHeader>
            <BannerForm
              formData={formData}
              setFormData={setFormData}
              imageFile={imageFile}
              setImageFile={setImageFile}
              isUploading={isUploading}
              onImageUpload={handleImageUpload}
              onSubmit={handleSubmit}
              isLoading={addBannerMutation.isPending}
            />
          </DialogContent>
        </Dialog>
      </div>

      {banners.length === 0 ? (
        <div className="text-center py-12 bg-white rounded-lg border border-dashed border-gray-300">
          <ImageIcon className="mx-auto h-12 w-12 text-gray-400" />
          <h3 className="text-lg font-semibold text-gray-900 mb-2">
            Hen√ºz kategori banner'ƒ± yok
          </h3>
          <p className="text-gray-600 mb-6">
            ƒ∞lk kategori banner'ƒ±nƒ±zƒ± ekleyerek ba≈ülayƒ±n.
          </p>
        </div>
      ) : (
        <div className="bg-white rounded-lg border">
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>√ñnizleme</TableHead>
                <TableHead>Kategori</TableHead>
                <TableHead>Ba≈ülƒ±k</TableHead>
                <TableHead>A√ßƒ±klama</TableHead>
                <TableHead>Durum</TableHead>
                <TableHead>ƒ∞≈ülemler</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {banners.map((banner) => (
                <TableRow key={banner.id} data-testid={`row-banner-${banner.id}`}>
                  <TableCell>
                    <img
                      src={banner.imageUrl}
                      alt={banner.title}
                      className="w-16 h-10 object-cover rounded"
                    />
                  </TableCell>
                  <TableCell className="font-medium">
                    {banner.categoryName}
                  </TableCell>
                  <TableCell>{banner.title}</TableCell>
                  <TableCell className="max-w-xs truncate">
                    {banner.description || "-"}
                  </TableCell>
                  <TableCell>
                    <Badge
                      variant={banner.isActive ? "default" : "secondary"}
                      data-testid={`status-${banner.id}`}
                    >
                      {banner.isActive ? "Aktif" : "Pasif"}
                    </Badge>
                  </TableCell>
                  <TableCell>
                    <div className="flex items-center gap-2">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => handleEdit(banner)}
                        data-testid={`button-edit-${banner.id}`}
                      >
                        <Edit className="w-4 h-4" />
                      </Button>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => deleteBannerMutation.mutate(banner.id)}
                        disabled={deleteBannerMutation.isPending}
                        data-testid={`button-delete-${banner.id}`}
                      >
                        <Trash2 className="w-4 h-4" />
                      </Button>
                    </div>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </div>
      )}

      {/* Edit Dialog */}
      <Dialog open={isEditDialogOpen} onOpenChange={setIsEditDialogOpen}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Kategori Banner D√ºzenle</DialogTitle>
          </DialogHeader>
          <BannerForm
            formData={formData}
            setFormData={setFormData}
            imageFile={imageFile}
            setImageFile={setImageFile}
            isUploading={isUploading}
            onImageUpload={handleImageUpload}
            onSubmit={handleSubmit}
            isLoading={updateBannerMutation.isPending}
          />
        </DialogContent>
      </Dialog>
    </div>
  );
}

interface BannerFormProps {
  formData: CategoryBannerFormData;
  setFormData: React.Dispatch<React.SetStateAction<CategoryBannerFormData>>;
  imageFile: File | null;
  setImageFile: React.Dispatch<React.SetStateAction<File | null>>;
  isUploading: boolean;
  onImageUpload: () => void;
  onSubmit: () => void;
  isLoading: boolean;
}

function BannerForm({
  formData,
  setFormData,
  imageFile,
  setImageFile,
  isUploading,
  onImageUpload,
  onSubmit,
  isLoading,
}: BannerFormProps) {
  return (
    <div className="space-y-4">
      <div>
        <Label htmlFor="categoryName">Kategori Se√ßin *</Label>
        <Select
          value={formData.categoryName}
          onValueChange={(value) => setFormData(prev => ({ ...prev, categoryName: value }))}
        >
          <SelectTrigger data-testid="select-category-name">
            <SelectValue placeholder="Kategori se√ßin..." />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="Beyaz E≈üya">Beyaz E≈üya</SelectItem>
            <SelectItem value="Ankastre">Ankastre</SelectItem>
            <SelectItem value="Televizyon">Televizyon</SelectItem>
            <SelectItem value="Elektronik">Elektronik</SelectItem>
            <SelectItem value="Isƒ±tma Soƒüutma">Isƒ±tma Soƒüutma</SelectItem>
            <SelectItem value="K√º√ß√ºk Ev Aletleri">K√º√ß√ºk Ev Aletleri</SelectItem>
            <SelectItem value="Buzdolabƒ±">Buzdolabƒ±</SelectItem>
            <SelectItem value="√áama≈üƒ±r Makinesi">√áama≈üƒ±r Makinesi</SelectItem>
            <SelectItem value="Bula≈üƒ±k Makinesi">Bula≈üƒ±k Makinesi</SelectItem>
            <SelectItem value="Klima">Klima</SelectItem>
            <SelectItem value="Mikrodalga">Mikrodalga</SelectItem>
          </SelectContent>
        </Select>
      </div>

      <div>
        <Label htmlFor="title">Banner Ba≈ülƒ±ƒüƒ± *</Label>
        <Input
          id="title"
          value={formData.title}
          onChange={(e) => setFormData(prev => ({ ...prev, title: e.target.value }))}
          placeholder="Banner √ºzerinde g√∂sterilecek ba≈ülƒ±k"
          data-testid="input-title"
        />
      </div>

      <div>
        <Label htmlFor="description">A√ßƒ±klama</Label>
        <Textarea
          id="description"
          value={formData.description}
          onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
          placeholder="Banner a√ßƒ±klamasƒ± (opsiyonel)"
          rows={3}
          data-testid="input-description"
        />
      </div>

      <div>
        <Label>Banner Resmi *</Label>
        <div className="space-y-3">
          <div className="flex items-center gap-3">
            <Input
              type="file"
              accept="image/*"
              onChange={(e) => setImageFile(e.target.files?.[0] || null)}
              className="flex-1"
              data-testid="input-image-file"
            />
            <Button
              onClick={onImageUpload}
              disabled={!imageFile || isUploading}
              data-testid="button-upload-image"
            >
              <Upload className="w-4 h-4 mr-2" />
              {isUploading ? "Y√ºkleniyor..." : "Y√ºkle"}
            </Button>
          </div>
          {formData.imageUrl && (
            <div className="border rounded p-3">
              <p className="text-sm text-gray-600 mb-2">√ñnizleme:</p>
              <img
                src={formData.imageUrl}
                alt="Banner √∂nizleme"
                className="w-full h-32 object-cover rounded"
              />
            </div>
          )}
        </div>
      </div>

      <div className="flex items-center space-x-2">
        <Switch
          id="isActive"
          checked={formData.isActive}
          onCheckedChange={(checked) => setFormData(prev => ({ ...prev, isActive: checked }))}
          data-testid="switch-active"
        />
        <Label htmlFor="isActive">Aktif</Label>
      </div>

      <div className="flex justify-end space-x-2 pt-4">
        <Button
          onClick={onSubmit}
          disabled={isLoading}
          data-testid="button-save"
        >
          {isLoading ? "Kaydediliyor..." : "Kaydet"}
        </Button>
      </div>
    </div>
  );
}

